import React, { useEffect, useState } from 'react';
import routineService from '../services/routineTemplateService';
import './styles/routineTemplates.css';
import { useNavigate } from 'react-router-dom';

export default function TherapistTemplates() {
  const [templates, setTemplates] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    routineService.getTemplates()
      .then(res => setTemplates(res.data))
      .catch(console.error);
  }, []);

  return (
    <div className="tpl-container">
      <header className="tpl-header">
        <h1>Plantillas de Rutinas</h1>
        <button onClick={()=>navigate('/therapist/templates/new')} className="btn-new">+ Nueva Plantilla</button>
      </header>
      <div className="tpl-grid">
        {templates.map(t => (
          <div key={t._id} className="tpl-card">
            <h2>{t.title}</h2>
            <p className="tpl-desc">{t.description}</p>
            <span className="tpl-meta">{t.type} Â· {t.duration} min</span>
            <button onClick={()=>navigate(`/therapist/templates/${t._id}/assign`)} className="btn-assign">
              Asignar
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
