// src/pages/RoutineTemplatesPage.js

import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaTrash, FaEdit } from 'react-icons/fa';
import axios from 'axios';
import '../styles/routineTemplates.css';
import '../styles/patientsList.css';

export default function RoutineTemplatesPage() {
  const [templates, setTemplates]       = useState([]);
  const [patients, setPatients]         = useState([]);
  const [loading, setLoading]           = useState(true);
  const [error, setError]               = useState(null);
  const [modalTpl, setModalTpl]         = useState(null);
  const [modalPatient, setModalPatient] = useState(null);
  const [modalInstance, setModalInstance] = useState(null);
  const [modalLoading, setModalLoading] = useState(false);
  const [modalError, setModalError]     = useState(null);

  const navigate = useNavigate();
  const token    = localStorage.getItem('token');
  const headers  = {
    'Content-Type': 'application/json',
    'x-auth-token': token
  };

  // Carga inicial de plantillas y pacientes
  useEffect(() => {
    const fetchAll = async () => {
      if (!token) {
        navigate('/login');
        setLoading(false);
        return;
      }
      try {
        const [tplRes, patRes] = await Promise.all([
          axios.get('/api/routines/templates', { headers }),
          axios.get('/api/therapist/patients',   { headers })
        ]);
        setTemplates(tplRes.data);
        setPatients(patRes.data);
      } catch (err) {
        console.error(err);
        setError(err.response?.data?.msg || err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchAll();
  }, [navigate, token]);

  // Drag start para plantillas
  const onDragStart = (e, item) => {
    e.dataTransfer.setData('application/json',
      JSON.stringify({ type: 'template', item }));
    e.dataTransfer.effectAllowed = 'copyMove';
  };

  // Zona de drop para eliminar, duplicar o asignar
  const handleDrop = useCallback((e, zone) => {
    e.preventDefault();
    const { type, item } = JSON.parse(
      e.dataTransfer.getData('application/json')
    );

    // Eliminar plantilla
    if (zone === 'trash' && type === 'template') {
      setTemplates(ts => ts.filter(t => t._id !== item._id));
      axios.delete(`/api/routines/templates/${item._id}`, { headers })
           .catch(console.error);
    }
    // Duplicar plantilla
    else if (zone === 'duplicate' && type === 'template') {
      const copy = {
        ...item,
        name: item.name + ' (copia)'
      };
      delete copy._id;
      axios.post('/api/routines/templates', copy, { headers })
        .then(r => setTemplates(ts => [r.data, ...ts]))
        .catch(err => {
          console.error('Error duplicando:', err);
          alert('No se pudo duplicar: ' + err.message);
        });
    }
    // Asignar plantilla a paciente
    else if (zone.startsWith('assign-') && type === 'template') {
      const patientId = zone.split('-')[1];
      axios.post(
        '/api/routines/instances',
        { templateId: item._id, patientId },
        { headers }
      )
      .then(() => {
        alert(`Asignada ¬´${item.name}¬ª a paciente ${patientId}`);
      })
      .catch(err => {
        console.error('Error asignando:', err);
        alert('No se pudo asignar: ' + err.message);
      });
    }
  }, [headers]);

  const allowDrop = e => e.preventDefault();

  // Abrir modal de detalle de instancia para tpl y paciente
  const openPatientDetail = async (tpl, patient) => {
    setModalTpl(tpl);
    setModalPatient(patient);
    setModalLoading(true);
    setModalError(null);
    setModalInstance(null);

    try {
      // Obtener todas las instancias de ese paciente
      const res = await axios.get(
        `/api/routines/instances/${patient._id}`,
        { headers }
      );
      const list = Array.isArray(res.data) ? res.data : res.data.instances || [];
      // Buscar la instancia de la plantilla seleccionada
      const inst = list.find(i => i.template._id === tpl._id);
      if (!inst) {
        throw new Error('Esta plantilla no est√° asignada a ese paciente');
      }
      setModalInstance(inst);
    } catch (err) {
      console.error(err);
      setModalError(err.response?.data?.msg || err.message);
    } finally {
      setModalLoading(false);
    }
  };

  const closeModal = () => {
    setModalTpl(null);
    setModalPatient(null);
    setModalInstance(null);
    setModalError(null);
  };

  if (loading) return <div className="tpl-container">Cargando plantillas‚Ä¶</div>;
  if (error)   return <div className="tpl-container error-msg">Error: {error}</div>;

  return (
    <div className="routine-templates-page">
      <div className="tpl-container">
        <div className="tpl-header">
          <h1>Mis Plantillas</h1>
          <button
            className="btn-new"
            onClick={() => navigate('/therapist/templates/new')}
          >
            üìÑ Nueva plantilla
          </button>
        </div>

        {templates.length === 0 ? (
          <div className="empty-state">
            ü§î A√∫n no se han creado plantillas
          </div>
        ) : (
          <div className="tpl-grid">
            {templates.map(tpl => (
              <div
                key={tpl._id}
                className="tpl-card"
                draggable
                onDragStart={e => onDragStart(e, tpl)}
              >
                <FaEdit
                  className="icon-edit"
                  onClick={() => navigate(`/therapist/templates/${tpl._id}/edit`)}
                />
                <h2>{tpl.name}</h2>
                <p className="tpl-desc">{tpl.description}</p>
                <div className="tpl-meta">
                  {tpl.activities.length} actividad(es)
                </div>

                <h3 style={{ marginTop: '1.5rem' }}>Pacientes</h3>
                <div className="tpl-grid">
                  {patients.map(p => (
                    <div
                      key={p._id}
                      className="patient-card"
                      onClick={() => openPatientDetail(tpl, p)}
                      onDragOver={allowDrop}
                      onDrop={e => handleDrop(e, `assign-${p._id}`)}
                      style={{ cursor: 'pointer' }}
                    >
                      <img
                        src={p.avatar}
                        alt={p.name}
                        style={{ width: 40, borderRadius: '50%' }}
                      />
                      <div style={{ marginLeft: '0.5rem', fontWeight: 500 }}>
                        {p.name}
                      </div>
                      <div style={{ fontSize: '0.8rem', color: '#666' }}>
                        {p.email}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        )}

        <div
          className="dropzones"
          onDragOver={allowDrop}
          style={{ display: 'flex', gap: '1rem', marginTop: '2rem' }}
        >
          <div className="dropzone trash" onDrop={e => handleDrop(e, 'trash')}>
            üóëÔ∏è Arrastra aqu√≠ para eliminar
          </div>
          <div
            className="dropzone duplicate"
            onDrop={e => handleDrop(e, 'duplicate')}
          >
            üìã Arrastra aqu√≠ para duplicar
          </div>
        </div>
      </div>

      {/* Modal de detalle de instancia */}
      {modalTpl && (
        <div className="modal-overlay" onClick={closeModal}>
          <div
            className="modal-content"
            onClick={e => e.stopPropagation()}
          >
            <button className="close-modal-btn" onClick={closeModal}>
              &times;
            </button>
            <h2>
              {modalPatient.name} ‚Äì {modalTpl.name}
            </h2>

            {modalLoading && <p>Cargando detalles‚Ä¶</p>}
            {modalError   && <p className="error-msg">{modalError}</p>}

            {modalInstance && !modalLoading && !modalError && (
              <>
                <p>
                  Asignado: {new Date(modalInstance.createdAt).toLocaleDateString()}
                </p>
                <ul className="tpl-activities-list">
                  {modalInstance.template.activities.map((act, idx) => {
                    const done = modalInstance.completedActivities.includes(idx);
                    return (
                      <li key={idx} className="activity-item">
                        <label style={{ display: 'flex', alignItems: 'center' }}>
                          <input type="checkbox" checked={done} readOnly />
                          <span
                            style={{
                              marginLeft: '0.5rem',
                              textDecoration: done ? 'line-through' : 'none'
                            }}
                          >
                            {act.name} ({act.minutes} min)
                          </span>
                        </label>
                      </li>
                    );
                  })}
                </ul>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
}
