// src/pages/RoutineTemplatesPage.js
import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/routineTemplates.css';

export default function RoutineTemplatesPage() {
  const [templates, setTemplates] = useState([]);
  const [patients, setPatients]   = useState([]);
  const [loading, setLoading]     = useState(true);
  const [error, setError]         = useState(null);
  const navigate = useNavigate();

  // Leer token desde LocalStorage
  const token = localStorage.getItem('token');
  const headers = {
    'Content-Type': 'application/json',
    'x-auth-token': token
  };

  // Fetch inicial de plantillas y pacientes
  useEffect(() => {
    if (!token) {
      navigate('/login');
      return;
    }

    Promise.all([
      fetch('/api/routines/templates', { headers }),        // CORRECCI√ìN: ruta coincide con server.js
      fetch('/api/therapist/patients', { headers })          // CORRECCI√ìN: ruta de pacientes protegida
    ])
      .then(async ([rTpl, rPat]) => {
        if (rTpl.status === 401 || rPat.status === 401) {
          alert('Sesi√≥n expirada. Por favor, inicia sesi√≥n de nuevo.');
          navigate('/login');
          return;
        }
        const dataTpl = await rTpl.json();
        const dataPat = await rPat.json();
        if (!Array.isArray(dataTpl) || !Array.isArray(dataPat)) {
          throw new Error('Respuesta inesperada del servidor');
        }
        setTemplates(dataTpl);
        setPatients(dataPat);
      })
      .catch(err => {
        console.error('Error cargando datos:', err);
        setError(err.message);
      })
      .finally(() => setLoading(false));
  }, [navigate, token]);

  // Drag start: guardamos tipo y payload
  const onDragStart = (e, item, type) => {
    e.dataTransfer.setData('application/json', JSON.stringify({ type, item }));
    e.dataTransfer.effectAllowed = 'copyMove';
  };

  // Manejo de drops (eliminar, duplicar, asignar)
  const handleDrop = useCallback((e, zone) => {
    e.preventDefault();
    const { type, item } = JSON.parse(e.dataTransfer.getData('application/json'));

    // Eliminar plantilla
    if (zone === 'trash' && type === 'template') {
      setTemplates(ts => ts.filter(t => t._id !== item._id));
      fetch(`/api/routines/templates/${item._id}`, { method: 'DELETE', headers })
        .catch(console.error);
    }
    // Duplicar plantilla
    else if (zone === 'duplicate' && type === 'template') {
      fetch('/api/routines/templates', {
        method: 'POST',
        headers,
        body: JSON.stringify({ ...item, name: item.name + ' (copia)' })
      })
        .then(res => res.json())
        .then(newTpl => setTemplates(ts => [newTpl, ...ts]))
        .catch(console.error);
    }
    // Asignar a paciente
    else if (zone.startsWith('assign-') && type === 'template') {
      const patientId = zone.split('-')[1];
      fetch(`/api/patients/${patientId}/assignTemplate`, {
        method: 'POST',
        headers,
        body: JSON.stringify({ templateId: item._id })
      })
        .then(() => alert(`Plantilla ¬´${item.name}¬ª asignada a ${patients.find(p=>p._id===patientId)?.nombre}`))
        .catch(console.error);
    }
  }, [patients, token]);

  const allowDrop = e => e.preventDefault();

  if (loading) {
    return <div className="tpl-container">Cargando‚Ä¶</div>;
  }
  if (error) {
    return <div className="tpl-container">Error: {error}</div>;
  }

  return (
    <div className="routine-templates-page">
      <div className="tpl-container">
        <div className="tpl-header">
          <h1>Mis Plantillas</h1>
          <button className="btn-new" onClick={() => navigate('/therapist/templates/new')}>
            üìÑ Nueva plantilla
          </button>
        </div>

        {templates.length === 0 ? (
          <div className="empty-state">
            ü§î A√∫n no se han creado plantillas
          </div>
        ) : (
          <div className="tpl-grid">
            {templates.map(t => (
              <div
                key={t._id}
                className="tpl-card"
                draggable
                onDragStart={e => onDragStart(e, t, 'template')}
              >
                <h2>{t.name}</h2>
                <p className="tpl-desc">{t.description}</p>
                <div className="tpl-meta">{t.activities.length} actividades</div>
              </div>
            ))}
          </div>
        )}

        <div className="dropzones">
          <div
            className="dropzone trash"
            onDragOver={allowDrop}
            onDrop={e => handleDrop(e, 'trash')}
          >
            üóëÔ∏è Eliminar plantilla
          </div>
          <div
            className="dropzone duplicate"
            onDragOver={allowDrop}
            onDrop={e => handleDrop(e, 'duplicate')}
          >
            üìã Duplicar plantilla
          </div>
        </div>

        <h3>Pacientes</h3>
        <div className="tpl-grid">
          {patients.map(p => (
            <div
              key={p._id}
              className="patient-card"
              onDragOver={allowDrop}
              onDrop={e => handleDrop(e, `assign-${p._id}`)}
            >
              <img src={p.avatar} alt={p.nombre} className="avatar-small" />
              <div>
                <strong>{p.nombre} {p.apellidos}</strong>
                <div className="patient-email">{p.email}</div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
