import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import axios from 'axios';
import '../styles/timeMachineGame.css';

const generateTimeSlots = () => {
  const slots = [];
  for (let hour = 0; hour < 24; hour++) {
    // Cada hora se divide en dos franjas: HH:00 y HH:30
    const label1 = `${String(hour).padStart(2, '0')}:00`;
    const label2 = `${String(hour).padStart(2, '0')}:30`;
    slots.push({ id: label1, activities: [] });
    slots.push({ id: label2, activities: [] });
  }
  return slots;
};

const initialActivities = [
  { id: 'futbol', title: 'Jugar al fútbol', icon: '⚽' },
  { id: 'dibujos', title: 'Ver dibujos animados', icon: '📺' },
  { id: 'comics', title: 'Leer cómics', icon: '📚' },
  { id: 'tarea', title: 'Hacer tarea', icon: '📝' },
  { id: 'videojuegos', title: 'Jugar videojuegos', icon: '🎮' },
  { id: 'helado', title: 'Comer helado', icon: '🍦' },
  { id: 'parque', title: 'Ir al parque', icon: '🏞️' },
  { id: 'banio', title: 'Bañarse', icon: '🛁' },
  { id: 'dormir', title: 'Dormir', icon: '😴' },
  { id: 'musica', title: 'Escuchar música', icon: '🎵' },
  { id: 'bailar', title: 'Bailar', icon: '💃' },
  { id: 'amigos', title: 'Jugar con amigos', icon: '👫' },
  { id: 'bicicleta', title: 'Montar en bicicleta', icon: '🚲' },
  { id: 'dibujar', title: 'Dibujar', icon: '🎨' },
  { id: 'mascotas', title: 'Jugar con mascotas', icon: '🐶' },
  { id: 'experimentos', title: 'Hacer experimentos', icon: '🔬' },
  { id: 'cantar', title: 'Cantar', icon: '🎤' },
  { id: 'lego', title: 'Construir con LEGO', icon: '🧱' },
  { id: 'nadar', title: 'Nadar', icon: '🏊' },
  { id: 'computadora', title: 'Jugar en la computadora', icon: '💻' },
  { id: 'manualidades', title: 'Hacer manualidades', icon: '✂️' },
  { id: 'pintar', title: 'Pintar', icon: '🖌️' },
  { id: 'cine', title: 'Ir al cine', icon: '🎬' },
  { id: 'jugarPatio', title: 'Jugar en el patio', icon: '🏃' },
  // Puedes agregar aún más actividades divertidas propias de un niño occidental de 10 años
];

function TimeMachineGame() {
  const [dayType, setDayType] = useState('Miércoles'); // Puede ser 'Miércoles' o 'Sábado'
  const [timeSlots, setTimeSlots] = useState([]);
  const [pool, setPool] = useState([]);
  const [instructionsVisible, setInstructionsVisible] = useState(true);
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    setTimeSlots(generateTimeSlots());
    setPool(initialActivities);
  }, []);

  const onDragEnd = (result) => {
    const { source, destination } = result;
    if (!destination) return;

    // Caso 1: Arrastrar desde el pool hacia una franja horaria
    if (source.droppableId === 'pool' && destination.droppableId !== 'pool') {
      const activity = pool[source.index];
      // Crear una instancia única (usamos timestamp para generar un ID único)
      const instance = {
        instanceId: `${activity.id}-${Date.now()}`,
        ...activity,
        duration: 30 // Duración inicial de 30 minutos
      };
      const newSlots = Array.from(timeSlots);
      const slotIndex = newSlots.findIndex(s => s.id === destination.droppableId);
      const destActivities = Array.from(newSlots[slotIndex].activities);
      destActivities.splice(destination.index, 0, instance);
      newSlots[slotIndex].activities = destActivities;
      setTimeSlots(newSlots);
      return;
    }

    // Caso 2: Mover actividad dentro de la misma franja o entre franjas
    if (source.droppableId !== 'pool' && destination.droppableId !== 'pool') {
      const newSlots = Array.from(timeSlots);
      const sourceSlotIndex = newSlots.findIndex(s => s.id === source.droppableId);
      const sourceActivities = Array.from(newSlots[sourceSlotIndex].activities);
      const [movedItem] = sourceActivities.splice(source.index, 1);
      newSlots[sourceSlotIndex].activities = sourceActivities;

      const destSlotIndex = newSlots.findIndex(s => s.id === destination.droppableId);
      const destActivities = Array.from(newSlots[destSlotIndex].activities);
      destActivities.splice(destination.index, 0, movedItem);
      newSlots[destSlotIndex].activities = destActivities;

      setTimeSlots(newSlots);
    }
  };

  const handleDurationChange = (slotId, index, newDuration) => {
    const newSlots = Array.from(timeSlots);
    const slotIndex = newSlots.findIndex(s => s.id === slotId);
    newSlots[slotIndex].activities[index].duration = newDuration;
    setTimeSlots(newSlots);
  };

  const handleSave = async () => {
    setSaving(true);
    const dataToSave = {
      day: dayType,
      timeSlots: timeSlots
        .filter(slot => slot.activities.length > 0)
        .map(slot => ({
          slot: slot.id,
          activities: slot.activities.map(act => ({
            activityId: act.id,
            title: act.title,
            icon: act.icon,
            duration: act.duration
          }))
        }))
    };
    try {
      await axios.post('/api/time-machine', dataToSave, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      alert('¡Tu día ha sido guardado!');
    } catch (err) {
      console.error(err);
      alert('Error al guardar, inténtalo de nuevo.');
    }
    setSaving(false);
  };

  return (
    <div className="time-machine-game-container">
      <header className="tmg-header">
        <h1>La Máquina del Tiempo</h1>
        <div className="day-selector">
          <label>Elige tu día: </label>
          <select value={dayType} onChange={(e) => setDayType(e.target.value)}>
            <option value="Miércoles">Miércoles</option>
            <option value="Sábado">Sábado</option>
          </select>
        </div>
      </header>

      {instructionsVisible && (
        <div className="tmg-instructions">
          <p>
            ¡Esta es tu máquina del tiempo! Arrastra las actividades que normalmente haces un {dayType}.
            Ajusta el tiempo deslizando la barra para cada actividad.
          </p>
          <button onClick={() => setInstructionsVisible(false)}>Cerrar instrucciones</button>
        </div>
      )}

      <DragDropContext onDragEnd={onDragEnd}>
        <div className="tmg-content">
          <div className="tmg-pool">
            <h2>Actividades divertidas</h2>
            <Droppable droppableId="pool" isDropDisabled={true}>
              {(provided) => (
                <div ref={provided.innerRef} {...provided.droppableProps}>
                  {pool.map((activity, index) => (
                    <Draggable key={activity.id} draggableId={activity.id} index={index}>
                      {(providedDraggable) => (
                        <div
                          className="tmg-activity-card"
                          ref={providedDraggable.innerRef}
                          {...providedDraggable.draggableProps}
                          {...providedDraggable.dragHandleProps}
                        >
                          <span className="tmg-activity-icon">{activity.icon}</span>
                          <span className="tmg-activity-title">{activity.title}</span>
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          </div>

          <div className="tmg-board">
            <h2>Organiza tu día</h2>
            <div className="tmg-timeline">
              {timeSlots.map(slot => (
                <Droppable key={slot.id} droppableId={slot.id}>
                  {(provided) => (
                    <div className="tmg-slot" ref={provided.innerRef} {...provided.droppableProps}>
                      <div className="tmg-slot-label">{slot.id}</div>
                      <div className="tmg-slot-activities">
                        {slot.activities.map((act, index) => (
                          <Draggable key={act.instanceId} draggableId={act.instanceId} index={index}>
                            {(providedDraggable) => (
                              <div
                                className="tmg-slot-activity"
                                ref={providedDraggable.innerRef}
                                {...providedDraggable.draggableProps}
                                {...providedDraggable.dragHandleProps}
                              >
                                <div className="tmg-activity-info">
                                  <span className="tmg-activity-icon">{act.icon}</span>
                                  <span className="tmg-activity-title">{act.title}</span>
                                </div>
                                <div className="tmg-duration-control">
                                  <input
                                    type="range"
                                    min="15"
                                    max="120"
                                    step="15"
                                    value={act.duration}
                                    onChange={(e) => handleDurationChange(slot.id, index, parseInt(e.target.value, 10))}
                                  />
                                  <span>{act.duration} min</span>
                                </div>
                              </div>
                            )}
                          </Draggable>
                        ))}
                        {provided.placeholder}
                      </div>
                    </div>
                  )}
                </Droppable>
              ))}
            </div>
          </div>
        </div>
      </DragDropContext>

      <div className="tmg-save-container">
        <button onClick={handleSave} disabled={saving}>
          {saving ? 'Guardando...' : 'Guardar mi día'}
        </button>
      </div>
    </div>
  );
}

export default TimeMachineGame;
