import React, { useState, useEffect, useCallback } from 'react';
// Usar require para evitar problemas de default export
const jwt_decode = require('jwt-decode');
import { useNavigate } from 'react-router-dom';
import '../styles/routineTemplates.css';

export default function PatientTemplatesPage() {
  const [instances, setInstances] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortKey, setSortKey] = useState('name');
  const [expanded, setExpanded] = useState({});
  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!token) {
      navigate('/login');
      setLoading(false);
      return;
    }
    const headers = {
      'Content-Type': 'application/json',
      'x-auth-token': token
    };

    fetch(`/api/routines/instances/${userId}`, { headers })
      .then(res => {
        if (res.status === 401) {
          navigate('/login');
          throw new Error('Sesión expirada');
        }
        return res.json();
      })
      .then(data => {
        setInstances(Array.isArray(data) ? data : []);
      })
      .catch(err => setError(err.message))
      .finally(() => setLoading(false));
  }, [token, navigate]);

  const handleMarkActivity = useCallback((instanceId, idx) => {
    const headers = { 'Content-Type': 'application/json', 'x-auth-token': token };
    fetch(`/api/routines/instances/${instanceId}/activities/${idx}`, { method: 'POST', headers })
      .then(res => {
        if (!res.ok) throw new Error('Error al actualizar actividad');
        setInstances(insts =>
          insts.map(inst => {
            if (inst._id !== instanceId) return inst;
            const updatedActivities = inst.activities.map((act, i) =>
              i === idx ? { ...act, completed: !act.completed } : act
            );
            return { ...inst, activities: updatedActivities };
          })
        );
      })
      .catch(console.error);
  }, [token]);

  const handleMarkAll = useCallback(instanceId => {
    const inst = instances.find(i => i._id === instanceId);
    if (!inst) return;
    inst.activities.forEach((act, idx) => {
      if (!act.completed) handleMarkActivity(instanceId, idx);
    });
  }, [instances, handleMarkActivity]);

  const toggleExpand = id => {
    setExpanded(e => ({ ...e, [id]: !e[id] }));
  };

  const filtered = instances.filter(inst => inst.name.toLowerCase().includes(searchTerm.toLowerCase()));

  const sorted = [...filtered].sort((a, b) => {
    if (sortKey === 'name') return a.name.localeCompare(b.name);
    if (sortKey === 'progress') {
      const pa = a.activities.filter(act => act.completed).length / a.activities.length;
      const pb = b.activities.filter(act => act.completed).length / b.activities.length;
      return pb - pa;
    }
    return 0;
  });

  if (loading) return <div className="tpl-container">Cargando rutinas…</div>;
  if (error) return <div className="tpl-container">Error: {error}</div>;

  return (
    <div className="routine-templates-page">
      <div className="tpl-container">
        <div className="tpl-header">
          <h1>Mis Rutinas</h1>
          <div className="tpl-actions">
            <input
              type="text"
              placeholder="Buscar rutina…"
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
              className="search-input"
            />
            <select value={sortKey} onChange={e => setSortKey(e.target.value)} className="sort-select">
              <option value="name">Ordenar por nombre</option>
              <option value="progress">Ordenar por progreso</option>
            </select>
          </div>
        </div>
        {sorted.length === 0 ? (
          <div className="empty-state">No tienes rutinas asignadas</div>
        ) : (
          <div className="tpl-grid">
            {sorted.map(inst => {
              const completedCount = inst.activities.filter(act => act.completed).length;
              const total = inst.activities.length;
              const progress = Math.round((completedCount / total) * 100);
              return (
                <div key={inst._id} className="tpl-card">
                  <h2 onClick={() => toggleExpand(inst._id)} style={{ cursor: 'pointer' }}>
                    {inst.name} ({progress}%)
                  </h2>
                  <div className="tpl-meta">{completedCount} de {total} actividades completadas</div>
                  <progress value={completedCount} max={total} />
                  <button onClick={() => handleMarkAll(inst._id)} className="btn-mark-all">
                    Completar todo
                  </button>
                  {expanded[inst._id] && (
                    <ul className="activity-list">
                      {inst.activities.map((act, idx) => (
                        <li key={idx} className={`activity ${act.completed ? 'completed' : ''}`}>
                          <div className="activity-info">
                            <strong>{act.name}</strong> - {act.desc}
                            <div className="activity-challenge">{act.challenge}</div>
                            <div className="activity-time">{act.minutes} min</div>
                          </div>
                          <button onClick={() => handleMarkActivity(inst._id, idx)} className="btn-activity">
                            {act.completed ? 'Desmarcar' : 'Completar'}
                          </button>
                        </li>
                      ))}
                    </ul>
                  )}
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
}
