// src/components/TherapistTemplates.js
import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/routineTemplates.css';

export default function TherapistTemplates() {
  const [templates, setTemplates] = useState([]);
  const [patients, setPatients]   = useState([]);
  const [loading, setLoading]     = useState(true);
  const [error, setError]         = useState(null);
  const navigate = useNavigate();

  // Leemos el JWT que guardaste tras el login
  const token = localStorage.getItem('token');
  const headers = {
    'Content-Type': 'application/json',
    'x-auth-token': token
  };

  useEffect(() => {
    if (!token) {
      navigate('/login');
      return;
    }

    (async () => {
      try {
        // 1) Plantillas: ahora bajo /api/routines/templates
        const resTpl = await fetch('/api/routines/templates', { headers });
        if (resTpl.status === 401) {
          alert('Sesi√≥n expirada. Inicia sesi√≥n de nuevo.');
          return navigate('/login');
        }
        if (!resTpl.ok) {
          // Si llega HTML aqu√≠, es que la ruta no coincide
          const text = await resTpl.text();
          throw new Error(`Plantillas: ${resTpl.status} ‚Üí ${text.substring(0, 100)}`);
        }
        const dataTpl = await resTpl.json();
        if (!Array.isArray(dataTpl)) {
          throw new Error('Formato inv√°lido de plantillas recibido');
        }

        // 2) Pacientes: bajo /api/therapist/patients :contentReference[oaicite:0]{index=0}:contentReference[oaicite:1]{index=1}
        const resPat = await fetch('/api/therapist/patients', { headers });
        if (resPat.status === 401) {
          alert('Sesi√≥n expirada. Inicia sesi√≥n de nuevo.');
          return navigate('/login');
        }
        if (!resPat.ok) {
          const text = await resPat.text();
          throw new Error(`Pacientes: ${resPat.status} ‚Üí ${text.substring(0, 100)}`);
        }
        const dataPat = await resPat.json();
        if (!Array.isArray(dataPat)) {
          throw new Error('Formato inv√°lido de pacientes recibido');
        }

        setTemplates(dataTpl);
        setPatients(dataPat);
      } catch (err) {
        console.error(err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    })();
  }, [navigate, token]);

  const allowDrop = e => e.preventDefault();

  const onDragStart = (e, item, type) => {
    e.dataTransfer.setData('application/json', JSON.stringify({ type, item }));
    e.dataTransfer.effectAllowed = 'copyMove';
  };

  const handleDrop = useCallback((e, zone) => {
    e.preventDefault();
    const { type, item } = JSON.parse(e.dataTransfer.getData('application/json'));

    // CONFIGURAMOS LOS HEADERS DE NUEVO
    const fetchOpts = { headers };

    // Eliminar plantilla
    if (zone === 'trash' && type === 'template') {
      setTemplates(ts => ts.filter(t => t._id !== item._id));
      fetch(`/api/routines/templates/${item._id}`, { ...fetchOpts, method: 'DELETE' })
        .catch(console.error);
      return;
    }

    // Duplicar plantilla
    if (zone === 'duplicate' && type === 'template') {
      fetch('/api/routines/templates', {
        ...fetchOpts,
        method: 'POST',
        body: JSON.stringify({ ...item, name: item.name + ' (copia)' })
      })
        .then(r => {
          if (!r.ok) throw new Error(`Duplicar: ${r.status}`);
          return r.json();
        })
        .then(newTpl => setTemplates(ts => [newTpl, ...ts]))
        .catch(console.error);
      return;
    }

    // Asignar plantilla a un paciente
    if (zone.startsWith('assign-') && type === 'template') {
      const patientId = zone.split('-')[1];
      fetch(`/api/patients/${patientId}/assignTemplate`, {
        ...fetchOpts,
        method: 'POST',
        body: JSON.stringify({ templateId: item._id })
      })
        .then(r => {
          if (!r.ok) throw new Error(`Asignar: ${r.status}`);
          alert(`Plantilla ‚Äú${item.name}‚Äù asignada a paciente.`);
        })
        .catch(console.error);
    }
  }, [patients, token]);

  if (loading) return <div className="tpl-container">Cargando plantillas‚Ä¶</div>;
  if (error)   return <div className="tpl-container">Error: {error}</div>;

  return (
    <div className="routine-templates-page">
      <div className="tpl-container">
        <div className="tpl-header">
          <h1>Mis Plantillas</h1>
          <button className="btn-new" onClick={() => navigate('/therapist/templates/new')}>
            üìÑ Nueva plantilla
          </button>
        </div>

        {templates.length === 0 ? (
          <div className="empty-state">
            ü§î A√∫n no se han creado plantillas
          </div>
        ) : (
          <div className="tpl-grid">
            {templates.map(t => (
              <div
                key={t._id}
                className="tpl-card"
                draggable
                onDragStart={e => onDragStart(e, t, 'template')}
              >
                <h2>{t.name}</h2>
                <p className="tpl-desc">{t.description}</p>
                <div className="tpl-meta">{t.activities.length} actividades</div>
              </div>
            ))}
          </div>
        )}

        <div className="dropzones">
          <div
            className="dropzone trash"
            onDragOver={allowDrop}
            onDrop={e => handleDrop(e, 'trash')}
          >
            üóëÔ∏è Eliminar plantilla
          </div>
          <div
            className="dropzone duplicate"
            onDragOver={allowDrop}
            onDrop={e => handleDrop(e, 'duplicate')}
          >
            üìã Duplicar plantilla
          </div>
        </div>

        <h3>Pacientes</h3>
        <div className="tpl-grid">
          {patients.map(p => (
            <div
              key={p._id}
              className="patient-card"
              onDragOver={allowDrop}
              onDrop={e => handleDrop(e, `assign-${p._id}`)}
            >
              <img src={p.avatar} alt={p.nombre} className="avatar-small" />
              <div>
                <strong>{p.nombre} {p.apellidos}</strong>
                <div className="patient-email">{p.email}</div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
