// src/components/PatientRoutines.js
import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import {
  PieChart, Pie, Cell, Tooltip, Legend,
  BarChart, Bar, XAxis, YAxis, CartesianGrid,
  RadarChart, PolarGrid, PolarAngleAxis, PolarRadiusAxis, Radar,
  RadialBarChart, RadialBar,
  ResponsiveContainer
} from 'recharts';
import '../styles/therapistRoutines.css';

const categoryMap = {
  futbol: 'Ocio', dibujos: 'Ocio', comics: 'Ocio', tarea: 'Obligaciones',
  videojuegos: 'Ocio', helado: 'Ocio', parque: 'Ocio', banio: 'Autocuidado',
  dormir: 'Autocuidado', musica: 'Ocio', bailar: 'Ocio', amigos: 'Ocio',
  bicicleta: 'Ocio', dibujar: 'Ocio', mascotas: 'Ocio', experimentos: 'Obligaciones',
  cantar: 'Ocio', lego: 'Ocio', nadar: 'Autocuidado', computadora: 'Ocio'
};
const groupMap = {
  Autocuidado: 'Autocuidado',
  Obligaciones: 'Obligaciones',
  Ocio: 'Ocio'
};
const COLORS = ['#8884d8', '#82ca9d', '#ffc658', '#ff8042', '#0088FE'];

function PatientRoutines() {
  const { patientId } = useParams();
  const [results, setResults] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [selected, setSelected] = useState(null);
  const [metrics, setMetrics] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    axios.get(`/api/game?patientId=${patientId}`, {
      headers: { 'x-auth-token': token }
    }).then(({ data }) => {
      setResults(data);
      const now = new Date();
      const weekAgo = new Date(now);
      weekAgo.setDate(now.getDate() - 6);
      setFrom(weekAgo.toISOString().slice(0,10));
      setTo(now.toISOString().slice(0,10));
    }).catch(console.error);
  }, [patientId]);

  useEffect(() => {
    if (!from || !to) return;
    const f = new Date(from), t = new Date(to);
    t.setHours(23,59,59,999);
    setFiltered(results.filter(r => {
      const d = new Date(r.createdAt);
      return d >= f && d <= t;
    }));
  }, [results, from, to]);

  useEffect(() => {
    setSelected(filtered.length ? filtered[0].createdAt : null);
  }, [filtered]);

  useEffect(() => {
    if (!selected) return;
    const res = filtered.find(r => r.createdAt === selected);
    setMetrics(res ? computeMetrics(res) : null);
  }, [selected, filtered]);

  function computeMetrics(r) {
    const slots = r.timeSlots || [];
    const acts = slots.flatMap(s => (s.activities || []).map(a => ({ ...a, slot: s.slot })));
    const totalMin = acts.reduce((sum, a) => sum + a.duration, 0) || 1;

    // 1. Distribución porcentual por categoría
    const catSum = {};
    acts.forEach(a => {
      const cat = categoryMap[a.activityId] || 'Otros';
      catSum[cat] = (catSum[cat] || 0) + a.duration;
    });
    const distribution = Object.entries(catSum).map(([name, val]) => ({
      name,
      value: +(val / totalMin * 100).toFixed(1)
    }));

    // 2. Horas por categoría
    const hours = Object.entries(catSum).map(([name, val]) => ({
      name,
      hours: +(val / 60).toFixed(2)
    }));

    // 3. Diversidad de actividades
    const diversity = new Set(acts.map(a => a.activityId)).size;

    // 4. Consistencia diaria
    const occupiedSlots = slots.filter(s => (s.activities || []).length > 0).length;
    const consistency = +((occupiedSlots / slots.length * 100).toFixed(1));

    // 5. Balance de grupos
    const grpSum = { Autocuidado: 0, Obligaciones: 0, Ocio: 0 };
    acts.forEach(a => {
      const c = categoryMap[a.activityId] || 'Ocio';
      grpSum[groupMap[c]] += a.duration;
    });
    const groupBalance = Object.entries(grpSum).map(([subject, val]) => ({
      subject,
      value: +(val / totalMin * 100).toFixed(1)
    }));

    // 6. Índice de equilibrio (entropía inversa)
    const N = distribution.length;
    const uniform = 100 / N;
    const diff = distribution.reduce((acc, d) => acc + Math.abs(d.value - uniform), 0) / 2;
    const balanceIndex = +((100 - diff) / 100 * 100).toFixed(1);

    // 7. Top 3 actividades por tiempo total
    const actSum = {};
    acts.forEach(a => {
      actSum[a.activityId] = (actSum[a.activityId] || 0) + a.duration;
    });
    const top3 = Object.entries(actSum)
      .sort(([, a], [, b]) => b - a)
      .slice(0, 3)
      .map(([id, val]) => ({ name: id, hours: +(val / 60).toFixed(2) }));

    // 8. Estadísticas adicionales
    const avgActivityDuration = +(totalMin / acts.length).toFixed(1);
    const slotDurMap = {};
    slots.forEach(s => {
      slotDurMap[s.slot] = (s.activities || []).reduce((sum, a) => sum + a.duration, 0);
    });
    const sortedSlotDur = Object.entries(slotDurMap).sort(([, a], [, b]) => b - a);
    const busiestSlot = sortedSlotDur[0][0];
    const leastSlot = sortedSlotDur[sortedSlotDur.length - 1][0];
    const emptySlots = slots.filter(s => !(s.activities || []).length).length;

    return {
      distribution,
      hours,
      diversity,
      consistency,
      groupBalance,
      balanceIndex,
      top3,
      avgActivityDuration,
      busiestSlot,
      leastSlot,
      emptySlots
    };
  }

  return (
    <div className="routines-container">
      <header className="routines-header">
        <Link to="/therapist/routines">← Volver</Link>
        <h2>Estadísticas de Rutinas</h2>
      </header>

      {metrics && (
        <>
          <div className="metrics-grid">
            <div className="metric-card">
              Diversidad: <strong>{metrics.diversity}</strong>
            </div>
            <div className="metric-card">
              Consistencia: <strong>{metrics.consistency}%</strong>
            </div>
            <div className="metric-card">
              Equilibrio: <strong>{metrics.balanceIndex}%</strong>
            </div>
            <div className="metric-card">
              Duración media actividad: <strong>{metrics.avgActivityDuration} min</strong>
            </div>
            <div className="metric-card">
              Slot más ocupado: <strong>{metrics.busiestSlot}</strong>
            </div>
            <div className="metric-card">
              Slot menos ocupado: <strong>{metrics.leastSlot}</strong>
            </div>
            <div className="metric-card">
              Slots vacíos: <strong>{metrics.emptySlots}</strong>
            </div>
          </div>

          <div className="charts-grid">
            <div className="chart-block">
              <h3>Distribución % por Categoría</h3>
              <ResponsiveContainer width="100%" height={250}>
                <PieChart>
                  <Pie
                    data={metrics.distribution}
                    dataKey="value"
                    nameKey="name"
                    cx="50%" cy="50%"
                    outerRadius={80}
                    label
                  >
                    {metrics.distribution.map((_, i) => (
                      <Cell key={i} fill={COLORS[i % COLORS.length]} />
                    ))}
                  </Pie>
                  <Tooltip />
                  <Legend />
                </PieChart>
              </ResponsiveContainer>
            </div>

            <div className="chart-block">
              <h3>Horas por Categoría</h3>
              <ResponsiveContainer width="100%" height={250}>
                <BarChart data={metrics.hours} margin={{ top: 20, right: 30, bottom: 5, left: 0 }}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Bar dataKey="hours" fill="#8884d8" />
                </BarChart>
              </ResponsiveContainer>
            </div>

            <div className="chart-block">
              <h3>Balance Grupo</h3>
              <ResponsiveContainer width="100%" height={250}>
                <RadarChart data={metrics.groupBalance} cx="50%" cy="50%" outerRadius={80}>
                  <PolarGrid />
                  <PolarAngleAxis dataKey="subject" />
                  <PolarRadiusAxis domain={[0,100]} />
                  <Radar dataKey="value" stroke="#82ca9d" fill="#82ca9d" fillOpacity={0.6} />
                  <Tooltip />
                </RadarChart>
              </ResponsiveContainer>
            </div>

            <div className="chart-block full-width">
              <h3>Top 3 Actividades por Tiempo</h3>
              <ResponsiveContainer width="100%" height={250}>
                <BarChart data={metrics.top3} layout="vertical" margin={{ top: 10, right: 30, left: 50, bottom: 5 }}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis type="number" />
                  <YAxis dataKey="name" type="category" />
                  <Tooltip />
                  <Bar dataKey="hours" fill="#ffc658" />
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>
        </>
      )}
    </div>
  );
}

export default PatientRoutines;
