// src/components/PatientRoutines.js
import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import {
  PieChart, Pie, Cell, Tooltip as PieTooltip, Legend as PieLegend,
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip as BarTooltip,
  RadarChart, PolarGrid, PolarAngleAxis, PolarRadiusAxis, Radar,
  ResponsiveContainer
} from 'recharts';
import '../styles/therapistRoutines.css';

// Mapeo de actividad a categoría
const categoryMap = {
  futbol:       'Ocio', dibujos:     'Ocio', comics:      'Ocio',
  tarea:        'Obligaciones', videojuegos:'Ocio', helado:      'Ocio',
  parque:       'Ocio', banio:       'Autocuidado', dormir:      'Autocuidado',
  musica:       'Ocio', bailar:      'Ocio', amigos:      'Ocio',
  bicicleta:    'Ocio', dibujar:     'Ocio', mascotas:    'Ocio',
  experimentos: 'Obligaciones', cantar:      'Ocio', lego:        'Ocio',
  nadar:        'Autocuidado', computadora: 'Ocio'
};
// Mapeo de categoría a grupo para radar
const groupMap = {
  Autocuidado:   'Autocuidado',
  Obligaciones:  'Obligaciones',
  Ocio:          'Ocio'
};
// Colores para gráficos
const COLORS = ['#8884d8', '#82ca9d', '#ffc658', '#ff8042', '#0088FE'];

function PatientRoutines() {
  const { patientId } = useParams();
  const [results, setResults] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [from, setFrom]       = useState('');
  const [to, setTo]           = useState('');
  const [selected, setSelected] = useState('');
  const [metrics, setMetrics] = useState(null);

  // 1. Fetch de resultados
  useEffect(() => {
    const token = localStorage.getItem('token');
    axios.get(`http://localhost:8080/api/game?patientId=${patientId}`, {
      headers: { 'x-auth-token': token }
    })
    .then(resp => {
      setResults(resp.data);
      // filtro inicial: últimos 7 días
      const now = new Date(), weekAgo = new Date(now);
      weekAgo.setDate(now.getDate() - 6);
      setFrom( weekAgo.toISOString().slice(0,10) );
      setTo(   now.toISOString().slice(0,10) );
    })
    .catch(console.error);
  }, [patientId]);

  // 2. Aplicar filtro de fechas
  useEffect(() => {
    if (!from || !to) return;
    const fDate = new Date(from), tDate = new Date(to);
    tDate.setHours(23,59,59,999);
    const arr = results.filter(r => {
      const d = new Date(r.createdAt);
      return d >= fDate && d <= tDate;
    });
    setFiltered(arr);
  }, [results, from, to]);

  // 3. Seleccionar primer resultado
  useEffect(() => {
    setSelected(filtered.length ? filtered[0].createdAt : '');
  }, [filtered]);

  // 4. Calcular métricas cuando cambia el seleccionado
  useEffect(() => {
    if (!selected) return;
    const res = filtered.find(r => r.createdAt === selected);
    if (!res) { setMetrics(null); return; }
    setMetrics( computeMetrics(res) );
  }, [selected, filtered]);

  // Cálculo de métricas para un solo resultado
  function computeMetrics(result) {
    // a) Aplanar actividades
    const slots = result.timeSlots || [];
    const activities = slots.flatMap(slot =>
      (slot.activities||[]).map(act => ({
        ...act,
        slot: slot.slot
      }))
    );
    const totalMin = activities.reduce((sum,a)=>sum + a.duration, 0);
    // b) Distribución % por categoría
    const catSum = {};
    activities.forEach(a => {
      const c = categoryMap[a.activityId] || 'Otros';
      catSum[c] = (catSum[c]||0) + a.duration;
    });
    const distribution = Object.entries(catSum).map(([name,val])=>({
      name,
      value: +((val/totalMin)*100).toFixed(1)
    }));
    // c) Horas absolutas por categoria
    const hours = Object.entries(catSum).map(([name,val])=>({
      name,
      hours: +(val/60).toFixed(2)
    }));
    // d) Diversidad: actividades distintas
    const diversity = new Set(activities.map(a=>a.activityId)).size;
    // e) Consistencia: % de franjas de hora ocupadas
    const usedSlots = slots.filter(s=>s.activities?.length>0).length;
    const consistency = +((usedSlots/24)*100).toFixed(1);
    // f) Transiciones: cambios de actividad en orden cronológico
    const sorted = [...activities].sort((a,b)=>{
      return parseInt(a.slot,10) - parseInt(b.slot,10);
    });
    let transitions = 0;
    for (let i=1; i<sorted.length; i++){
      if (sorted[i].activityId !== sorted[i-1].activityId) transitions++;
    }
    // g) Tiempo por periodo: mañana/tarde/noche
    const periods = { Mañana:[6,11], Tarde:[12,17], Noche:[18,23] };
    const pdSum = { Mañana:0, Tarde:0, Noche:0 };
    activities.forEach(a=>{
      const h = parseInt(a.slot.split(':')[0],10);
      for (let p in periods){
        const [s,e] = periods[p];
        if (h>=s && h<=e) pdSum[p] += a.duration;
      }
    });
    const timeOfDay = Object.entries(pdSum).map(([name,val])=>({
      name,
      hours: +(val/60).toFixed(2)
    }));
    // h) Balance por grupo (Autocuidado/Obligaciones/Ocio)
    const grpSum = { Autocuidado:0, Obligaciones:0, Ocio:0 };
    activities.forEach(a=>{
      const cat = categoryMap[a.activityId] || 'Ocio';
      const grp = groupMap[cat] || 'Ocio';
      grpSum[grp] += a.duration;
    });
    const groupBalance = Object.entries(grpSum).map(([subject,val])=>({
      subject,
      value: +((val/totalMin)*100).toFixed(1)
    }));
    // i) Índice de equilibrio global
    const N = groupBalance.length;
    const uniform = 100/N;
    const idx = 1 - (
      groupBalance.reduce((acc,g)=>acc + Math.abs(g.value - uniform),0)
      / (2*100)
    );
    const balanceIndex = +idx.toFixed(2);

    return {
      distribution, hours, diversity, consistency,
      transitions, timeOfDay, groupBalance, balanceIndex
    };
  }

  return (
    <div className="routines-container">
      <header className="routines-header">
        <Link to="/therapist/routines">← Volver a pacientes</Link>
        <h2>Rutinas del paciente</h2>
      </header>

      <section className="filter-section">
        <label>
          Desde:
          <input type="date" value={from}
            onChange={e=>setFrom(e.target.value)} />
        </label>
        <label>
          Hasta:
          <input type="date" value={to}
            onChange={e=>setTo(e.target.value)} />
        </label>
        <label>
          Seleccionar día:
          <select value={selected}
            onChange={e=>setSelected(e.target.value)}>
            {filtered.map(r => {
              const d = new Date(r.createdAt)
                .toLocaleDateString();
              return (
                <option key={r.createdAt}
                  value={r.createdAt}>
                  {d}
                </option>
              );
            })}
          </select>
        </label>
      </section>

      {metrics && (
        <>
          <div className="metrics-grid">
            <div className="metric-card">
              Diversidad<br/>
              <strong>{metrics.diversity}</strong>
            </div>
            <div className="metric-card">
              Consistencia<br/>
              <strong>{metrics.consistency}%</strong>
            </div>
            <div className="metric-card">
              Transiciones<br/>
              <strong>{metrics.transitions}</strong>
            </div>
            <div className="metric-card">
              Balance<br/>
              <strong>{metrics.balanceIndex}</strong>
            </div>
          </div>

          <div className="charts-grid">
            <div className="chart-block">
              <h3>Distribución por Categoría (%)</h3>
              <ResponsiveContainer width="100%" height={250}>
                <PieChart>
                  <Pie data={metrics.distribution}
                       dataKey="value"
                       nameKey="name"
                       cx="50%" cy="50%"
                       outerRadius={80}
                       label>
                    {metrics.distribution.map((_, i)=>(
                      <Cell key={i}
                            fill={COLORS[i % COLORS.length]} />
                    ))}
                  </Pie>
                  <PieTooltip />
                  <PieLegend />
                </PieChart>
              </ResponsiveContainer>
            </div>

            <div className="chart-block">
              <h3>Horas por Categoría</h3>
              <ResponsiveContainer width="100%" height={250}>
                <BarChart data={metrics.hours}
                          margin={{ top:20, right:30, left:0, bottom:0 }}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <BarTooltip />
                  <Bar dataKey="hours" fill="#8884d8" />
                </BarChart>
              </ResponsiveContainer>
            </div>

            <div className="chart-block">
              <h3>Tiempo por Periodo</h3>
              <ResponsiveContainer width="100%" height={250}>
                <BarChart data={metrics.timeOfDay}
                          margin={{ top:20, right:30, left:0, bottom:0 }}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <BarTooltip />
                  <Bar dataKey="hours" fill="#82ca9d" />
                </BarChart>
              </ResponsiveContainer>
            </div>

            <div className="chart-block">
              <h3>Balance de Grupos (%)</h3>
              <ResponsiveContainer width="100%" height={250}>
                <RadarChart data={metrics.groupBalance}
                            cx="50%" cy="50%" outerRadius={80}>
                  <PolarGrid />
                  <PolarAngleAxis dataKey="subject" />
                  <PolarRadiusAxis angle={30} domain={[0,100]} />
                  <Radar dataKey="value"
                         stroke="#ffc658"
                         fill="#ffc658"
                         fillOpacity={0.6} />
                  <PieTooltip />
                </RadarChart>
              </ResponsiveContainer>
            </div>
          </div>
        </>
      )}
    </div>
  );
}

export default PatientRoutines;
