// src/components/TherapistPatientsList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/patientsList.css';

function PatientsList() {
  const [patients, setPatients] = useState([]);
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);

  useEffect(() => {
    const fetchPatients = async () => {
      try {
        const token = localStorage.getItem('token');
        // Se asume que este endpoint devuelve un arreglo de pacientes con sus datos
        const response = await axios.get('http://localhost:5000/api/users/patients', {
          headers: { 'x-auth-token': token }
        });
        setPatients(response.data);
      } catch (error) {
        console.error("Error al obtener pacientes:", error);
      }
    };

    fetchPatients();
  }, []);

  const openModal = (patient) => {
    setSelectedPatient(patient);
    setModalOpen(true);
  };

  const closeModal = () => {
    setSelectedPatient(null);
    setModalOpen(false);
  };

  return (
    <div className="patients-list-container">
      <h1>Listado de Pacientes</h1>
      <table className="patients-table">
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Apellidos</th>
            <th>Fecha de Nacimiento</th>
            <th>Acción</th>
          </tr>
        </thead>
        <tbody>
          {patients.map((patient) => (
            <tr key={patient._id}>
              <td>{patient.nombre}</td>
              <td>{patient.apellidos}</td>
              <td>{new Date(patient.fechaNacimiento).toLocaleDateString()}</td>
              <td>
                <button onClick={() => openModal(patient)} className="view-btn">
                  <i className="fa fa-search"></i>
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modal para perfil detallado */}
      {modalOpen && selectedPatient && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Detalle del Paciente</h2>
            <p>
              <strong>Nombre:</strong> {selectedPatient.nombre} {selectedPatient.apellidos}
            </p>
            <p>
              <strong>Fecha de Nacimiento:</strong>{" "}
              {new Date(selectedPatient.fechaNacimiento).toLocaleDateString()}
            </p>
            <p>
              <strong>Juegos Completados:</strong>{" "}
              {selectedPatient.gamesCompleted ? "Sí" : "No"}
            </p>
            <button onClick={closeModal} className="close-modal-btn">
              Cerrar
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default PatientsList;
