// src/pages/PatientTemplatesPage.js
import React, { useState, useEffect } from 'react';
import assignedRoutineService from '../services/assignedRoutineService';
import '../styles/routineTemplates.css';
import '../styles';  // estilos específicos

export default function PatientTemplatesPage() {
  const [templates, setTemplates]   = useState([]);
  const [loading, setLoading]       = useState(true);
  const [expandedIds, setExpanded]  = useState([]);
  const [search, setSearch]         = useState('');
  const [filterStatus, setFilter]   = useState('all');
  const [sortKey, setSort]          = useState('dueDate');

  // Obtener patientId desde localStorage (ajusta según tu autenticación)
  const userData    = JSON.parse(localStorage.getItem('user') || '{}');
  const patientId   = userData.id || '';

  useEffect(() => {
    if (!patientId) return;
    assignedRoutineService
      .getAssignedTemplates(patientId)
      .then(({ data }) => {
        // Cada item: { _id, template: { title, description, activities, dueDate }, completedActivities }
        // Transformamos un poco para nuestro estado:
        const arr = data.map(inst => ({
          id: inst._id,
          title: inst.template.name || inst.template.title,
          description: inst.template.description,
          dueDate: inst.template.dueDate || inst.assignedAt,
          activities: inst.template.activities.map((act, idx) => ({
            id: idx,
            name: act.name,
            details: act.desc || act.details || '',
            estimatedTime: act.minutes || act.estimatedTime || '',
            completed: inst.completedActivities.includes(idx),
            note: ''  // opcional: puedes cargar notas si las guardas
          })),
          progress:
            inst.template.activities.length === 0
              ? 0
              : Math.round(
                  inst.completedActivities.length /
                    inst.template.activities.length *
                  100
                ),
          status: inst.completedActivities.length === inst.template.activities.length
            ? 'completed'
            : inst.completedActivities.length > 0
            ? 'in-progress'
            : 'pending'
        }));
        setTemplates(arr);
      })
      .catch(console.error)
      .finally(() => setLoading(false));
  }, [patientId]);

  const toggleExpand = id =>
    setExpanded(ids =>
      ids.includes(id)
        ? ids.filter(x => x !== id)
        : [...ids, id]
    );

  const handleActivityToggle = (tplId, actId) => {
    // Encuentra instancia original
    const inst = templates.find(t => t.id === tplId);
    const currently = inst.activities.find(a => a.id === actId).completed;
    assignedRoutineService
      .toggleActivity(tplId, actId, !currently)
      .then(() => {
        // Actualiza UI
        setTemplates(ts =>
          ts.map(t => {
            if (t.id !== tplId) return t;
            return {
              ...t,
              activities: t.activities.map(a =>
                a.id === actId
                  ? { ...a, completed: !currently }
                  : a
              ),
              progress: Math.round(
                (t.progress * t.activities.length +
                  (!currently ? 1 : -1)) /
                  t.activities.length *
                100
              )
            };
          })
        );
      })
      .catch(console.error);
  };

  if (loading) return <div className="pt-loading">Cargando plantillas…</div>;

  // Filtrado, búsqueda y ordenación
  const visible = templates
    .filter(t => filterStatus === 'all' || t.status === filterStatus)
    .filter(t => t.title.toLowerCase().includes(search.toLowerCase()))
    .sort((a, b) => {
      if (sortKey === 'dueDate')
        return new Date(a.dueDate) - new Date(b.dueDate);
      if (sortKey === 'progress')
        return a.progress - b.progress;
      return 0;
    });

  return (
    <div className="pt-container">
      <header className="pt-header">
        <h1>Mis Plantillas Asignadas</h1>
        <div className="pt-controls">
          <input
            type="text"
            placeholder="Buscar plantilla…"
            value={search}
            onChange={e => setSearch(e.target.value)}
          />
          <select
            value={filterStatus}
            onChange={e => setFilter(e.target.value)}
          >
            <option value="all">Todas</option>
            <option value="pending">Pendientes</option>
            <option value="in-progress">En progreso</option>
            <option value="completed">Completadas</option>
          </select>
          <select
            value={sortKey}
            onChange={e => setSort(e.target.value)}
          >
            <option value="dueDate">Por fecha</option>
            <option value="progress">Por progreso</option>
          </select>
        </div>
      </header>

      <div className="pt-grid">
        {visible.map(t => (
          <div key={t.id} className="pt-card">
            <div
              className="pt-card-header"
              onClick={() => toggleExpand(t.id)}
            >
              <div>
                <h2>{t.title}</h2>
                <small>
                  Vence: {new Date(t.dueDate).toLocaleDateString()}
                </small>
              </div>
              <div className="pt-progress-wrapper">
                <div className="pt-progress-bar">
                  <div
                    className="pt-progress-filled"
                    style={{ width: `${t.progress}%` }}
                  />
                </div>
                <span className="pt-progress-label">
                  {t.progress}%
                </span>
              </div>
            </div>

            {expandedIds.includes(t.id) && (
              <div className="pt-card-body">
                <p className="pt-description">
                  {t.description}
                </p>
                <div className="pt-activities">
                  {t.activities.map(act => (
                    <div
                      key={act.id}
                      className={
                        `pt-activity-item ${
                          act.completed ? 'done' : ''
                        }`
                      }
                    >
                      <div className="pt-act-main">
                        <input
                          type="checkbox"
                          checked={act.completed}
                          onChange={() =>
                            handleActivityToggle(t.id, act.id)
                          }
                        />
                        <div>
                          <h3>{act.name}</h3>
                          <p>{act.details}</p>
                          <small>
                            Tiempo estimado: {act.estimatedTime} min
                          </small>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
