// src/components/PatientTemplates.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/routineTemplates.css';

export default function PatientTemplates() {
  const [templates, setTemplates] = useState([]);
  const [filterTag, setFilterTag] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAssigned = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('No estás autenticado. Por favor, inicia sesión.');
        setLoading(false);
        return;
      }

      try {
        const res = await axios.get('/api/routineInstances', {
          headers: { 'x-auth-token': token }
        });
        setTemplates(res.data);
      } catch (err) {
        console.error(err);
        setError(err.response?.data?.message || 'Error al cargar plantillas');
      } finally {
        setLoading(false);
      }
    };

    fetchAssigned();
  }, []);

  const toggleCompleted = async (instId, actId) => {
    const token = localStorage.getItem('token');
    try {
      await axios.patch(
        `/api/routineInstances/${instId}/activities/${actId}/toggle`,
        null,
        { headers: { 'x-auth-token': token } }
      );
      setTemplates(ts =>
        ts.map(t =>
          t.id !== instId
            ? t
            : {
                ...t,
                activities: t.activities.map(a =>
                  a.id !== actId ? a : { ...a, completed: !a.completed }
                )
              }
        )
      );
    } catch (err) {
      console.error(err);
      alert('No se pudo cambiar el estado.');
    }
  };

  const handleDurationChange = (instId, actId, val) => {
    setTemplates(ts =>
      ts.map(t =>
        t.id !== instId
          ? t
          : {
              ...t,
              activities: t.activities.map(a =>
                a.id !== actId ? a : { ...a, duration: val }
              )
            }
      )
    );
  };

  const saveChanges = async instId => {
    const token = localStorage.getItem('token');
    const tpl = templates.find(t => t.id === instId);
    try {
      await axios.put(
        `/api/routineInstances/${instId}`,
        { activities: tpl.activities },
        { headers: { 'x-auth-token': token } }
      );
      alert('Cambios guardados 👍');
    } catch (err) {
      console.error(err);
      alert('Error guardando cambios');
    }
  };

  const filtered = templates.filter(t =>
    filterTag ? t.tags?.includes(filterTag) : true
  );

  return (
    <div className="routine-templates-page patient-templates-page">
      <div className="tpl-container">
        <div className="tpl-header">
          <h1>Mis Plantillas Asignadas</h1>
          <input
            type="text"
            placeholder="Filtrar por etiqueta"
            value={filterTag}
            onChange={e => setFilterTag(e.target.value)}
            className="small-input tag-filter"
          />
        </div>

        {loading && <p>Cargando plantillas…</p>}
        {error && <p className="error-msg">{error}</p>}

        {!loading && !error && (
          <div className="tpl-grid">
            {filtered.length === 0 && (
              <p>No se encontraron plantillas con esa etiqueta.</p>
            )}
            {filtered.map(tpl => (
              <div key={tpl.id} className="tpl-card">
                <h2>{tpl.title}</h2>
                <p className="tpl-desc">{tpl.description}</p>
                <p className="tpl-meta">
                  Asignado el:{' '}
                  {new Date(tpl.assignedAt).toLocaleDateString()}
                </p>
                <div
                  className="challenge-indicator"
                  data-level={tpl.challengeLevel}
                >
                  Nivel {tpl.challengeLevel}
                </div>

                <ul className="tpl-activities-list">
                  {tpl.activities.map(act => (
                    <li key={act.id}>
                      <div
                        className={`activity-item ${
                          act.completed ? 'status-completed' : ''
                        }`}
                      >
                        <span className="drag-handle">≡</span>
                        <div className="activity-content">
                          <h3>{act.title}</h3>
                          <p>{act.description}</p>
                          <div className="form-grid">
                            <input
                              type="text"
                              value={act.duration}
                              onChange={e =>
                                handleDurationChange(
                                  tpl.id,
                                  act.id,
                                  e.target.value
                                )
                              }
                              className="small-input"
                            />
                            <select
                              value={act.category}
                              onChange={() => {}}
                              className="small-select"
                            >
                              <option>Ejercicio</option>
                              <option>Meditación</option>
                              <option>Lectura</option>
                              <option>Otro</option>
                            </select>
                          </div>
                        </div>
                        <button
                          className="activity-toggle"
                          onClick={() =>
                            toggleCompleted(tpl.id, act.id)
                          }
                        >
                          {act.completed ? '✔️' : '◻️'}
                        </button>
                      </div>
                    </li>
                  ))}
                </ul>

                <button
                  className="btn-save"
                  onClick={() => saveChanges(tpl.id)}
                >
                  Guardar Cambios
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
