// src/pages/RoutineTemplatesPage.js

import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FaTrash, FaEdit } from 'react-icons/fa';
import '../styles/routineTemplates.css';
import '../styles/patientsList.css'; // para estilos de modal

export default function RoutineTemplatesPage() {
  const [templates, setTemplates] = useState([]);
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [assignments, setAssignments] = useState({});

  // ‚Äî Estados para el modal de detalle de un paciente ‚Äî
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [patientInstances, setPatientInstances] = useState([]);
  const [notes, setNotes] = useState({});
  const [modalLoading, setModalLoading] = useState(false);
  const [modalError, setModalError] = useState(null);

  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  const headers = {
    'Content-Type': 'application/json',
    'x-auth-token': token
  };

  // Fetch inicial de plantillas, pacientes y assignments
  useEffect(() => {
    if (!token) {
      navigate('/login');
      setLoading(false);
      return;
    }

    Promise.all([
      fetch('/api/routines/templates', { headers }),
      fetch('/api/therapist/patients', { headers }),
    ])
      .then(async ([resTpl, resPat]) => {
        if (resTpl.status === 401 || resPat.status === 401) {
          alert('Sesi√≥n expirada. Vuelve a iniciar sesi√≥n.');
          navigate('/login');
          return;
        }
        const dataTpl = await resTpl.json();
        const dataPat = await resPat.json();
        if (!Array.isArray(dataTpl) || !Array.isArray(dataPat)) {
          throw new Error('Respuesta inesperada del servidor');
        }
        setTemplates(dataTpl);
        setPatients(dataPat);

        // Cargar las instancias de cada plantilla (assignments por plantilla)
        const resp = await Promise.all(
          dataTpl.map(async tpl => {
            const { data: insts } = await axios.get(
              `/api/routines/instances/template/${tpl._id}`,
              { headers }
            );
            return [tpl._id, insts];
          })
        );
        setAssignments(Object.fromEntries(resp));
      })
      .catch(err => {
        console.error(err);
        setError(err.message);
      })
      .finally(() => setLoading(false));
  }, [navigate, token]);

  // Drag & drop handlers (mantener tu l√≥gica)
  const onDragStart = (e, item, type) => {
    e.dataTransfer.setData('application/json', JSON.stringify({ type, item }));
    e.dataTransfer.effectAllowed = 'copyMove';
  };
  const handleDrop = useCallback((e, zone) => {
    e.preventDefault();
    const { type, item } = JSON.parse(e.dataTransfer.getData('application/json'));
    // ... l√≥gica de eliminar/duplicar/asignar ...
  }, [token]);
  const allowDrop = e => e.preventDefault();

  // Abrir modal y cargar detalle de un paciente
  const handleClickPatient = async p => {
    setSelectedPatient(p);
    setModalError(null);
    setModalLoading(true);
    try {
      const { data: instsRaw } = await axios.get(
        `/api/routines/instances/${p._id}`,
        { headers }
      );
      const insts = Array.isArray(instsRaw)
        ? instsRaw
        : Array.isArray(instsRaw.instances)
          ? instsRaw.instances
          : [];
      setPatientInstances(insts);

      // Inicializar notas vac√≠as para cada [instancia-actividad]
      const initNotes = {};
      insts.forEach(inst => {
        inst.template.activities.forEach((_, idx) => {
          initNotes[`${inst._id}-${idx}`] = '';
        });
      });
      setNotes(initNotes);

      setModalOpen(true);
    } catch (err) {
      console.error(err);
      setModalError('No se pudo cargar el detalle del paciente');
    } finally {
      setModalLoading(false);
    }
  };

  // Toggle de actividad dentro del modal
  const toggleActivity = async (inst, idx) => {
    const key = `${inst._id}-${idx}`;
    const was = inst.completedActivities.includes(idx);
    try {
      await axios.post(
        `/api/routines/instances/${inst._id}/activities/${idx}`,
        { completed: !was },
        { headers }
      );
      setPatientInstances(prev =>
        prev.map(i =>
          i._id !== inst._id
            ? i
            : {
                ...i,
                completedActivities: was
                  ? i.completedActivities.filter(n => n !== idx)
                  : [...i.completedActivities, idx]
              }
        )
      );
    } catch {
      alert('Error actualizando actividad');
    }
  };

  // Gesti√≥n de notas en modal
  const handleNoteChange = (instId, idx, text) => {
    setNotes(n => ({ ...n, [`${instId}-${idx}`]: text }));
  };
  const saveNote = (instId, idx) => {
    console.log(`Nota para ${instId} act ${idx}:`, notes[`${instId}-${idx}`]);
  };

  if (loading) return <div className="tpl-container">Cargando plantillas‚Ä¶</div>;
  if (error) return <div className="tpl-container">Error: {error}</div>;

  return (
    <div className="routine-templates-page">
      <div className="tpl-container">
        <div className="tpl-header">
          <h1>Mis Plantillas</h1>
          <button className="btn-new" onClick={() => navigate('/therapist/templates/new')}>
            üìÑ Nueva plantilla
          </button>
        </div>

        {/* ‚Äî Grid de templates ‚Äî */}
        {templates.length === 0 ? (
          <div className="empty-state">ü§î A√∫n no se han creado plantillas</div>
        ) : (
          <div className="tpl-grid">
            {templates.map(t => (
              <div
                key={t._id}
                className="tpl-card"
                draggable
                onDragStart={e => onDragStart(e, t, 'template')}
              >
                <FaEdit
                  className="icon-edit"
                  onClick={() => navigate(`/therapist/templates/${t._id}/edit`)}
                />
                <h2>{t.name}</h2>
                <p className="tpl-desc">{t.description}</p>
                <div className="tpl-meta">{t.activities.length} actividades</div>
              </div>
            ))}
          </div>
        )}

        {/* ‚Äî Dropzones ‚Äî */}
        <div className="dropzones">
          <div className="dropzone trash" onDragOver={allowDrop} onDrop={e => handleDrop(e, 'trash')}>
            üóëÔ∏è Arrastra aqu√≠ para eliminar
          </div>
          <div className="dropzone duplicate" onDragOver={allowDrop} onDrop={e => handleDrop(e, 'duplicate')}>
            üìã Arrastra aqu√≠ Ctrl+drag para duplicar
          </div>
        </div>

        {/* ‚Äî Grid de pacientes ‚Äî */}
        <h3>Pacientes</h3>
        <div className="tpl-grid">
          {patients.map(p => (
            <div
              key={p._id}
              className="patient-card"
              onDragOver={allowDrop}
              onDrop={e => handleDrop(e, `assign-${p._id}`)}
              onClick={() => handleClickPatient(p)}
              style={{ cursor: 'pointer' }}
            >
              <img src={p.avatar} alt={p.name} />
              <div className="patient-info">
                <strong>{p.name}</strong>
                <small>{p.email}</small>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* ‚Äî Modal de detalle de paciente ‚Äî */}
      {modalOpen && (
        <div className="modal-overlay" onClick={() => setModalOpen(false)}>
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            <button className="modal-close" onClick={() => setModalOpen(false)}>
              &times;
            </button>

            <h2>Estado de {selectedPatient.name}</h2>
            {modalLoading && <p>Cargando...</p>}
            {modalError && <p className="error-msg">{modalError}</p>}

            {!modalLoading && !modalError && patientInstances.map(inst => {
              const tpl = inst.template;
              const total = tpl.activities.length;
              const done = inst.completedActivities.length;
              const pct = total ? Math.round((done / total) * 100) : 0;

              return (
                <div key={inst._id} className="tpl-card modal-tpl-card">
                  <h3>{tpl.name}</h3>
                  <div className="progress-bar">
                    <div className="progress-filled" style={{ width: `${pct}%` }} />
                  </div>
                  <p className="progress-text">
                    {done} / {total} actividades ({pct}%)
                  </p>

                  <ul className="activities-detailed-list">
                    {tpl.activities.map((act, i) => {
                      const completed = inst.completedActivities.includes(i);
                      return (
                        <li key={i} className={`modal-activity-item ${completed ? 'completed' : ''}`}>
                          <div className="activity-main" style={{ display:'flex', alignItems:'center', gap:'0.5rem' }}>
                            <span className="drag-handle">‚â°</span>
                            <label className="checkbox-label" style={{ display:'flex', alignItems:'center', gap:'0.3rem' }}>
                              <input
                                type="checkbox"
                                checked={completed}
                                readOnly
                              />
                              <span className="activity-name">{act.name}</span>
                            </label>
                            <div className="activity-icons" style={{ marginLeft:'auto', display:'flex', gap:'1rem', fontSize:'0.9rem' }}>
                              <span><i className="fa fa-clock-o"></i> {act.minutes} min</span>
                              <span><i className="fa fa-star"></i> {act.challenge}</span>
                            </div>
                          </div>
                          {act.desc && (
                            <p className="activity-desc" style={{ margin: '0.5rem 0 0 2rem' }}>
                              {act.desc}
                            </p>
                          )}

                          <textarea
                            className="note-box"
                            placeholder="Tus notas‚Ä¶"
                            value={notes[`${inst._id}-${i}`] || ''}
                            onChange={e => handleNoteChange(inst._id, i, e.target.value)}
                          />
                          <button className="btn-note" onClick={() => saveNote(inst._id, i)}>
                            <i className="fa fa-save"></i> Guardar nota
                          </button>
                        </li>
                      );
                    })}
                  </ul>
                </div>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );
}
