import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/patientRoutines.css';
import { FaCheck, FaTimes } from 'react-icons/fa';

export default function PatientRoutinesPage() {
  const [instances, setInstances] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  const userId = localStorage.getItem('userId'); // Debe guardarse al login

  useEffect(() => {
    if (!token) {
      navigate('/login');
      return;
    }

    fetch(`/api/routines/instances/${userId}`, {
      headers: { 'Content-Type':'application/json', 'x-auth-token': token }
    })
      .then(res => {
        if (res.status === 401) {
          alert('Sesión expirada');
          navigate('/login');
          return null;
        }
        if (!res.ok) throw new Error(`Error ${res.status}`);
        return res.json();
      })
      .then(data => {
        if (data) setInstances(data);
      })
      .catch(err => {
        console.error(err);
        setError(err.message);
      })
      .finally(() => setLoading(false));
  }, [navigate, token, userId]);

  const toggleActivity = (instanceId, idx, completed) => {
    fetch(`/api/routines/instances/${instanceId}/activities/${idx}`, {
      method: 'POST',
      headers: { 'Content-Type':'application/json', 'x-auth-token': token },
      body: JSON.stringify({ completed: !completed })
    })
      .then(res => {
        if (!res.ok) throw new Error(`Error ${res.status}`);
        return res.json();
      })
      .then(({ completedActivities }) => {
        setInstances(instances.map(inst =>
          inst._id === instanceId
            ? { ...inst, completedActivities }
            : inst
        ));
      })
      .catch(err => {
        console.error('Error marcando actividad:', err);
        alert('No se pudo actualizar el estado de la actividad.');
      });
  };

  if (loading) return <div className="pr-container">Cargando tus rutinas…</div>;
  if (error)   return <div className="pr-container">Error: {error}</div>;

  return (
    <div className="patient-routines-page">
      <div className="pr-container">
        <h1>Tus Rutinas Asignadas</h1>
        {instances.length === 0 ? (
          <div className="empty-state">No tienes rutinas asignadas.</div>
        ) : (
          instances.map(inst => (
            <div key={inst._id} className="routine-card">
              <h2>{inst.template.name}</h2>
              <p className="rt-desc">{inst.template.description}</p>
              <div className="activities-list">
                {inst.template.activities.map((act, idx) => {
                  const done = inst.completedActivities.includes(idx);
                  return (
                    <label key={idx} className={done ? 'completed' : ''}>
                      <input
                        type="checkbox"
                        checked={done}
                        onChange={() => toggleActivity(inst._id, idx, done)}
                      />
                      <span>{act.name}</span>
                      {done ? <FaCheck /> : <FaTimes />}
                    </label>
                  );
                })}
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
