import React, { useState, useEffect } from 'react';
import Draggable from 'react-draggable';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/miPlaneta.css';

const initialPaletteItems = [
  { id: 'estadio', title: 'Estadio', iconClass: 'fa-solid fa-futbol' },
  { id: 'parque', title: 'Parque', iconClass: 'fa-solid fa-tree' },
  { id: 'colegio', title: 'Colegio', iconClass: 'fa-solid fa-school' },
  { id: 'cine', title: 'Cine', iconClass: 'fa-solid fa-film' },
  { id: 'biblioteca', title: 'Biblioteca', iconClass: 'fa-solid fa-book' },
  { id: 'museo', title: 'Museo', iconClass: 'fa-solid fa-landmark' },
  { id: 'centro-deportivo', title: 'Centro Deportivo', iconClass: 'fa-solid fa-dumbbell' }
];

const sizeOptions = ['small', 'medium', 'large'];
const sizeStyles = {
  small: { width: 50, height: 50 },
  medium: { width: 80, height: 80 },
  large: { width: 110, height: 110 }
};

function MiPlaneta() {
  const navigate = useNavigate();
  // Si no hay token, redirige al login
  useEffect(() => {
    if (!localStorage.getItem('token')) {
      navigate('/');
    }
  }, [navigate]);

  // Estado para el zoom del planeta
  const [scale, setScale] = useState(1);
  // Estado para los elementos (hábitats) colocados en el planeta
  const [planetItems, setPlanetItems] = useState([]);
  // Paleta fija (no se mueve, al hacer click se añade una copia)
  const [palette] = useState(initialPaletteItems);

  // Agregar un elemento de la paleta al planeta en posición inicial (centro del planeta)
  const handleAddItem = (item) => {
    // Cada nuevo elemento recibe un id único y tamaño por defecto mediano
    const newItem = {
      ...item,
      instanceId: `${item.id}-${Date.now()}`,
      x: 250, // posición inicial (puedes ajustar según el tamaño del contenedor)
      y: 250,
      size: 'medium'
    };
    setPlanetItems(prev => [...prev, newItem]);
  };

  // Permitir cambiar tamaño: al hacer click en el elemento se cicla el tamaño
  const handleToggleSize = (instanceId) => {
    setPlanetItems(prev =>
      prev.map(item => {
        if (item.instanceId === instanceId) {
          const currentIndex = sizeOptions.indexOf(item.size);
          const newSize = sizeOptions[(currentIndex + 1) % sizeOptions.length];
          return { ...item, size: newSize };
        }
        return item;
      })
    );
  };

  // Cambiar la posición del elemento al soltarlo
  const handleStopDrag = (e, data, instanceId) => {
    setPlanetItems(prev =>
      prev.map(item => item.instanceId === instanceId ? { ...item, x: data.x, y: data.y } : item)
    );
  };

  // Control de zoom
  const zoomIn = () => setScale(prev => Math.min(prev + 0.2, 2));
  const zoomOut = () => setScale(prev => Math.max(prev - 0.2, 0.6));

  // Guardar el resultado en el backend
  const handleSavePlanet = async () => {
    const token = localStorage.getItem('token');
    // Preparamos un payload con cada elemento y sus propiedades
    const payload = {
      items: planetItems.map(item => ({
        interestId: item.id,
        title: item.title,
        iconClass: item.iconClass,
        x: item.x,
        y: item.y,
        size: item.size
      }))
    };
    try {
      await axios.post('http://localhost:5000/api/mi-planeta', payload, {
        headers: { 'x-auth-token': token }
      });
      alert('¡Tu planeta ha sido guardado!');
      navigate('/dashboard');
    } catch (error) {
      console.error('Error al guardar Mi Planeta:', error);
      alert('Error al guardar tu planeta.');
    }
  };

  return (
    <div className="mi-planeta-container">
      <header className="mp-header">
        <div className="mp-header-left">
          <button className="back-btn" onClick={() => navigate('/dashboard')}>←</button>
          <h1>Mi Planeta</h1>
        </div>
      </header>
      <div className="mp-instructions">
        <p>Este es <strong>TU PLANETA</strong>, llénalo con las cosas que más te gustan hacer.</p>
        <p>
          Cada opción tiene tres tamaños diferentes. Elige el tamaño que mejor represente cuánto te gusta: 
          más grande si te encanta, mediano si te gusta y pequeño si te gusta menos.
        </p>
      </div>
      <div className="mp-main">
        <aside className="mp-palette">
          <h2>Elementos</h2>
          <div className="palette-list">
            {palette.map(item => (
              <div 
                key={item.id} 
                className="palette-item" 
                onClick={() => handleAddItem(item)}
              >
                <i className={item.iconClass}></i>
                <span>{item.title}</span>
              </div>
            ))}
          </div>
          <div className="mp-zoom-controls">
            <button onClick={zoomOut}>− Zoom</button>
            <button onClick={zoomIn}>+ Zoom</button>
          </div>
        </aside>
        <section className="mp-planet-section">
          <div 
            className="planet-container"
            style={{ transform: `scale(${scale})` }}
          >
            {/* Aquí se renderizan los elementos colocados en el planeta */}
            {planetItems.map(item => (
              <Draggable
                key={item.instanceId}
                defaultPosition={{ x: item.x, y: item.y }}
                onStop={(e, data) => handleStopDrag(e, data, item.instanceId)}
              >
                <div 
                  className={`planet-item ${item.size}`}
                  onDoubleClick={() => handleToggleSize(item.instanceId)}
                >
                  <i className={item.iconClass}></i>
                  <span>{item.title}</span>
                </div>
              </Draggable>
            ))}
          </div>
        </section>
      </div>
      <div className="mp-actions">
        <button className="save-btn" onClick={handleSavePlanet}>Guardar mi Planeta</button>
      </div>
      <footer className="mp-footer">
        <p>2025 © Iván Vela Campos</p>
      </footer>
    </div>
  );
}

export default MiPlaneta;
