// src/components/TemplateForm.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/routineTemplates.css';

export default function TemplateForm() {
  const [name, setName] = useState('');
  const [description, setDesc] = useState('');
  const [activities, setActivities] = useState([]);
  const navigate = useNavigate();

  const addActivity = () => {
    setActivities([...activities, { id: Date.now(), name:'', challenge:'Bajo' }]);
  };
  const updateAct = (id, field, val) => {
    setActivities(acts => acts.map(a => a.id===id ? { ...a, [field]: val } : a));
  };
  const removeAct = (id) => {
    setActivities(acts => acts.filter(a => a.id!==id));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    await axios.post('/api/routines/templates',
      { name, description, activities },
      { headers: { 'x-auth-token': token } }
    );
    navigate('/therapist/templates');
  };

  return (
    <div className="routine-templates-page">
    <div className="tpl-form">
      <h2>Nueva Plantilla</h2>
      <form onSubmit={handleSubmit}>
        <label>Nombre</label>
        <input value={name} onChange={e => setName(e.target.value)} required />

        <label>Descripción</label>
        <textarea value={description} onChange={e => setDesc(e.target.value)} />

        <label>Actividades</label>
        {activities.map(act => (
          <div key={act.id} className="activity-row">
            <input
              placeholder="Actividad..."
              value={act.name}
              onChange={e => updateAct(act.id, 'name', e.target.value)}
              required
            />
            <select
              value={act.challenge}
              onChange={e => updateAct(act.id, 'challenge', e.target.value)}
            >
              <option>Bajo</option>
              <option>Medio</option>
              <option>Alto</option>
            </select>
            <button type="button" onClick={() => removeAct(act.id)}>×</button>
          </div>
        ))}
        <button type="button" onClick={addActivity} className="btn-new">
          <i className="fas fa-plus"></i> Añadir Actividad
        </button>

        <button type="submit" className="btn-save">Guardar Plantilla</button>
      </form>
    </div>
    </div>
  );
}
