// src/pages/RoutineTemplatesPage.js

import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaTrash, FaEdit, FaEye } from 'react-icons/fa';
import '../styles/routineTemplates.css';

export default function RoutineTemplatesPage() {
  const [templates, setTemplates] = useState([]);
  const [patients, setPatients]   = useState([]);
  const [loading, setLoading]     = useState(true);
  const [error, setError]         = useState(null);
  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  // Fetch inicial de plantillas y pacientes
  useEffect(() => {
    if (!token) {
      navigate('/login');
      setLoading(false);
      return;
    }

    const headers = {
      'Content-Type': 'application/json',
      'x-auth-token': token
    };

    Promise.all([
      fetch('/api/routines/templates', { headers }),
      fetch('/api/therapist/patients',   { headers })
    ])
      .then(async ([resTpl, resPat]) => {
        if (resTpl.status === 401 || resPat.status === 401) {
          setLoading(false);
          alert('Sesi√≥n expirada. Vuelve a iniciar sesi√≥n.');
          navigate('/login');
          return;
        }
        const dataTpl = await resTpl.json();
        const dataPat = await resPat.json();
        if (!Array.isArray(dataTpl) || !Array.isArray(dataPat)) {
          throw new Error('Respuesta inesperada del servidor');
        }
        setTemplates(dataTpl);
        setPatients(dataPat);
      })
      .catch(err => {
        console.error(err);
        setError(err.message);
      })
      .finally(() => setLoading(false));
  }, [navigate, token]);

  // Drag start: guardamos tipo y payload
  const onDragStart = (e, item, type) => {
    e.dataTransfer.setData(
      'application/json',
      JSON.stringify({ type, item })
    );
    e.dataTransfer.effectAllowed = 'copyMove';
  };

  // Drop gen√©rico (basado en zona)
  const handleDrop = useCallback((e, zone) => {
    e.preventDefault();
    const { type, item } = JSON.parse(
      e.dataTransfer.getData('application/json')
    );

    const headers = {
      'Content-Type': 'application/json',
      'x-auth-token': token
    };

    // Eliminar plantilla
    if (zone === 'trash' && type === 'template') {
      setTemplates(ts => ts.filter(t => t._id !== item._id));
      fetch(`/api/routines/templates/${item._1}`, {
        method: 'DELETE',
        headers
      }).catch(console.error);
    }
    // Duplicar plantilla
    else if (zone === 'duplicate' && type === 'template') {
      const payload = {
        name: item.name + ' (copia)',
        description: item.description,
        category: item.category,
        tags: item.tags,
        duration: item.duration,
        reminder: item.reminder,
        activities: item.activities.map(a => ({
          name: a.name,
          desc: a.desc,
          challenge: a.challenge,
          minutes: a.minutes
        }))
      };
      fetch('/api/routines/templates', {
        method: 'POST',
        headers,
        body: JSON.stringify(payload)
      })
        .then(res => {
          if (!res.ok) {
            return res.text().then(txt => { throw new Error(txt); });
          }
          return res.json();
        })
        .then(newTpl => {
          setTemplates(ts => [newTpl, ...ts]);
        })
        .catch(err => {
          console.error('Error duplicando plantilla:', err);
          alert('No se pudo duplicar: ' + err.message);
        });
    }
    // Asignar a paciente
    else if (zone.startsWith('assign-') && type === 'template') {
      const patientId = zone.split('-')[1];
      fetch('/api/routines/instances', {
        method: 'POST',
        headers,
        body: JSON.stringify({ templateId: item._id, patientId })
      })
        .then(res => {
          if (!res.ok) throw new Error(`HTTP ${res.status}`);
          alert(`¬´${item.name}¬ª asignada a ${patientId}`);
        })
        .catch(err => {
          console.error('Error asignando plantilla:', err);
          alert('No se pudo asignar: ' + err.message);
        });
    }
  }, [token]);

  const allowDrop = e => e.preventDefault();

  if (loading) {
    return <div className="tpl-container">Cargando plantillas‚Ä¶</div>;
  }
  if (error) {
    return <div className="tpl-container">Error: {error}</div>;
  }

  return (
    <div className="routine-templates-page">
      <div className="tpl-container">
        <div className="tpl-header">
          <h1>Mis Plantillas</h1>
          <button
            className="btn-new"
            onClick={() => navigate('/therapist/templates/new')}
          >
            üìÑ Nueva plantilla
          </button>
        </div>

        {templates.length === 0 ? (
          <div
            className="empty-state"
            style={{
              border: '2px dashed var(--primary-color)',
              padding: '2rem',
              textAlign: 'center',
              borderRadius: '8px',
              color: 'var(--primary-color)',
              fontSize: '1.2rem',
              marginTop: '2rem'
            }}
          >
            ü§î A√∫n no se han creado plantillas
          </div>
        ) : (
          <div className="tpl-grid">
            {templates.map(t => (
              <div
                key={t._id}
                className="tpl-card"
                draggable
                onDragStart={e => onDragStart(e, t, 'template')}
              >
                <FaEdit
                  className="icon-edit"
                  onClick={() =>
                    navigate(`/therapist/templates/${t._id}/edit`)
                  }
                />
                <h2>{t.name}</h2>
                <p className="tpl-desc">{t.description}</p>
                <div className="tpl-meta">
                  {t.activities.length} actividades
                  <span
                    className="detail-link"
                    onClick={() => navigate(`/therapist/templates/${t._id}`)}
                    title="Ver detalle de plantilla"
                  >
                    <FaEye />
                  </span>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Papelera y duplicado */}
        <div
          className="dropzones"
          style={{ display: 'flex', gap: '1rem', marginTop: '2rem' }}
        >
          <div
            className="dropzone trash"
            onDragOver={allowDrop}
            onDrop={e => handleDrop(e, 'trash')}
          >
            üóëÔ∏è Eliminar plantilla
          </div>
          <div
            className="dropzone duplicate"
            onDragOver={allowDrop}
            onDrop={e => handleDrop(e, 'duplicate')}
          >
            üìã Duplicar plantilla
          </div>
        </div>

        {/* Lista de pacientes para asignar o ver detalle */}
        <h3 style={{ marginTop: '2rem' }}>Pacientes</h3>
        <div className="tpl-grid">
          {patients.map(p => (
            <div
              key={p._id}
              className="patient-card"
              style={{ cursor: 'pointer' }}
              onClick={() => navigate(`/therapist/templates/${p._id}`)}
              onDragOver={allowDrop}
              onDrop={e => handleDrop(e, `assign-${p._id}`)}
            >
              <img
                src={p.avatar}
                alt={`${p.nombre} avatar`}
                className="patient-avatar"
              />
              <div className="patient-info">
                <strong>
                  {p.nombre} {p.apellidos}
                </strong>
                <small>{p.email}</small>
              </div>
              <FaEye className="icon-eye" title="Ver detalle de paciente" />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
