import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import '../styles/home.css';

/**
 * Convierte un objeto Date en una cadena YYYY-MM-DD.
 */
function formatDate(date) {
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, '0');
  const day = String(date.getDate()).padStart(2, '0');
  return `${year}-${month}-${day}`;
}

function Home() {
  // Estado local para la fecha seleccionada en el calendario
  const [selectedDate, setSelectedDate] = useState(new Date());

  /**
   * Tareas de ejemplo, organizadas por fecha en formato YYYY-MM-DD.
   * En un proyecto real, podrías obtener estos datos de una API.
   */
  const tasksByDate = {
    '2023-09-07': ['Hacer Documentación del TFG', 'Desarrollar la gestión de credenciales'],
    '2023-09-08': ['Llamar al dentista', 'Enviar informe de criptografía'],
    '2023-09-10': ['Tarea 1', 'Tarea 2', 'Tarea 3'],
  };

  /**
   * Se llama cada vez que el usuario cambia la fecha en el calendario.
   */
  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  /**
   * Destaca los días que tienen tareas, añadiendo un pequeño indicador (un punto) debajo de la fecha.
   * Esto se maneja con la prop `tileContent` de react-calendar.
   */
  const renderTileContent = ({ date, view }) => {
    if (view === 'month') {
      const dateStr = formatDate(date);
      if (tasksByDate[dateStr]) {
        return <div className="task-dot"></div>;
      }
    }
    return null;
  };

  // Determina las tareas del día seleccionado
  const selectedDateStr = formatDate(selectedDate);
  const tasksForSelectedDate = tasksByDate[selectedDateStr] || [];

  return (
    <div className="home-container">
      {/* Barra superior */}
      <header className="top-bar">
        <div className="left-section">
          <i className="fa fa-user-circle user-icon" aria-hidden="true"></i>
        </div>

        <div className="center-section">
          <h1 className="title">JANUS</h1>
        </div>

        <div className="right-section">
          <i className="fa fa-bell bell-icon" aria-hidden="true"></i>
          <button className="logout-btn">
            <i className="fa fa-sign-out" aria-hidden="true"></i>
            <span>Cerrar Sesión</span>
          </button>
        </div>
      </header>

      {/* Contenido principal */}
      <main className="main-content">
        {/* Columna izquierda con botones */}
        <div className="left-column">
          <button className="option-btn btn-tareas">TAREAS PLANIFICADAS</button>
          <button className="option-btn btn-recordatorios">RECORDATORIOS</button>
          <button className="option-btn btn-feedback">FEEDBACK</button>
        </div>

        {/* Columna derecha con calendario real + lista de tareas */}
        <div className="right-column">
          <div className="calendar-box">
            <Calendar
              onChange={handleDateChange}
              value={selectedDate}
              tileContent={renderTileContent}
            />
          </div>

          <div className="tasks-list">
            <h3>Tareas para {selectedDateStr}</h3>
            {tasksForSelectedDate.length === 0 ? (
              <p>No hay tareas para este día</p>
            ) : (
              <ul>
                {tasksForSelectedDate.map((task, index) => (
                  <li key={index}>{task}</li>
                ))}
              </ul>
            )}
          </div>
        </div>
      </main>

      {/* Sección de bienvenida */}
      <section className="welcome-section">
        <h2>¡BIENVENIDO A JANUS!</h2>
        <p>
          Una aplicación destinada a ayudarte de la mejor manera posible con tus 
          problemas de administración y balance de tareas.
        </p>
      </section>

      {/* Pie de página */}
      <footer className="footer-bar">
        <p>2025 © Iván Vela Campos</p>
      </footer>
    </div>
  );
}

export default Home;
