import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import axios from 'axios';
import '../styles/timeMachine.css';

const initialActivities = [
  { id: '1', title: 'Jugar al f√∫tbol', icon: '‚öΩ' },
  { id: '2', title: 'Ver dibujos animados', icon: 'üì∫' },
  { id: '3', title: 'Leer c√≥mics', icon: 'üìö' },
  { id: '4', title: 'Hacer tarea', icon: 'üìù' },
  { id: '5', title: 'Jugar videojuegos', icon: 'üéÆ' },
  { id: '6', title: 'Comer helado', icon: 'üç¶' },
  { id: '7', title: 'Ir al parque', icon: 'üèûÔ∏è' },
  { id: '8', title: 'Ba√±arse', icon: 'üõÅ' },
  { id: '9', title: 'Dormir', icon: 'üò¥' },
  { id: '10', title: 'Escuchar m√∫sica', icon: 'üéµ' },
  { id: '11', title: 'Bailar', icon: 'üíÉ' },
  { id: '12', title: 'Jugar con amigos', icon: 'üë´' },
  { id: '13', title: 'Montar en bicicleta', icon: 'üö≤' },
  { id: '14', title: 'Dibujar', icon: 'üé®' },
  { id: '15', title: 'Jugar con mascotas', icon: 'üê∂' },
  { id: '16', title: 'Hacer experimentos', icon: 'üî¨' },
  { id: '17', title: 'Cantar', icon: 'üé§' },
  { id: '18', title: 'Construir con LEGO', icon: 'üß±' },
  { id: '19', title: 'Nadar', icon: 'üèä' },
  { id: '20', title: 'Jugar en la computadora', icon: 'üíª' }
];

const generateTimeSlots = () => {
  const slots = [];
  for (let i = 0; i < 24; i++) {
    const label = `${String(i).padStart(2, '0')}:00`;
    slots.push({ id: label, activities: [] });
  }
  return slots;
};

function TimeMachine() {
  const [dayType, setDayType] = useState('Mi√©rcoles');
  const [pool, setPool] = useState(initialActivities);
  const [timeSlots, setTimeSlots] = useState(generateTimeSlots());
  const [instructionsVisible, setInstructionsVisible] = useState(true);
  const [saving, setSaving] = useState(false);

  const onDragEnd = (result) => {
    const { source, destination, draggableId } = result;
    if (!destination) return;
    if (source.droppableId === destination.droppableId && source.index === destination.index) return;

    // Remover el elemento de la lista origen
    let sourceList, updateSource;
    if (source.droppableId === 'pool') {
      sourceList = Array.from(pool);
      updateSource = setPool;
    } else {
      const slotIndex = timeSlots.findIndex(slot => slot.id === source.droppableId);
      sourceList = Array.from(timeSlots[slotIndex].activities);
      updateSource = (newList) => {
        const newSlots = Array.from(timeSlots);
        newSlots[slotIndex].activities = newList;
        setTimeSlots(newSlots);
      };
    }
    const [movedItem] = sourceList.splice(source.index, 1);
    updateSource(sourceList);

    // Agregar el elemento a la lista destino
    if (destination.droppableId === 'pool') {
      const newPool = Array.from(pool);
      newPool.splice(destination.index, 0, movedItem);
      setPool(newPool);
    } else {
      const slotIndex = timeSlots.findIndex(slot => slot.id === destination.droppableId);
      const newActivities = Array.from(timeSlots[slotIndex].activities);
      // Se asigna una duraci√≥n por defecto (en minutos)
      newActivities.splice(destination.index, 0, { ...movedItem, duration: 60 });
      const newSlots = Array.from(timeSlots);
      newSlots[slotIndex].activities = newActivities;
      setTimeSlots(newSlots);
    }
  };

  const handleDurationChange = (slotId, activityIndex, newDuration) => {
    const slotIndex = timeSlots.findIndex(slot => slot.id === slotId);
    const newSlots = Array.from(timeSlots);
    newSlots[slotIndex].activities[activityIndex].duration = newDuration;
    setTimeSlots(newSlots);
  };

  const handleSave = async () => {
    setSaving(true);
    // Preparar los datos: se filtran los slots con actividades
    const resultData = {
      day: dayType,
      timeSlots: timeSlots.filter(slot => slot.activities.length > 0).map(slot => ({
        slot: slot.id,
        activities: slot.activities
      }))
    };
    try {
      await axios.post('/api/time-machine', resultData, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      alert('Resultado guardado exitosamente');
    } catch (err) {
      console.error('Error guardando resultado:', err);
      alert('Error al guardar. Int√©ntalo de nuevo.');
    }
    setSaving(false);
  };

  return (
    <div className="time-machine-container">
      <header className="tm-header">
        <h1>La M√°quina del Tiempo</h1>
        <select value={dayType} onChange={(e) => setDayType(e.target.value)}>
          <option value="Mi√©rcoles">Mi√©rcoles</option>
          <option value="S√°bado">S√°bado</option>
        </select>
      </header>

      {instructionsVisible && (
        <div className="tm-instructions">
          <p>
            ¬°Esta es tu m√°quina del tiempo! Arrastra las actividades que normalmente haces un {dayType}. 
            Ajusta el tiempo deslizando la barra para cada actividad.
          </p>
          <button onClick={() => setInstructionsVisible(false)}>Cerrar instrucciones</button>
        </div>
      )}

      <DragDropContext onDragEnd={onDragEnd}>
        <div className="tm-content">
          <div className="tm-pool">
            <h2>Actividades disponibles</h2>
            <Droppable droppableId="pool">
              {(provided) => (
                <div ref={provided.innerRef} {...provided.droppableProps}>
                  {pool.map((activity, index) => (
                    <Draggable key={activity.id} draggableId={activity.id} index={index}>
                      {(providedDraggable) => (
                        <div 
                          className="tm-activity-card" 
                          ref={providedDraggable.innerRef} 
                          {...providedDraggable.draggableProps} 
                          {...providedDraggable.dragHandleProps}
                        >
                          <span className="tm-activity-icon">{activity.icon}</span>
                          <span className="tm-activity-title">{activity.title}</span>
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          </div>

          <div className="tm-board">
            <h2>Organiza tu d√≠a</h2>
            <div className="tm-timeline">
              {timeSlots.map(slot => (
                <Droppable droppableId={slot.id} key={slot.id}>
                  {(provided) => (
                    <div className="tm-slot" ref={provided.innerRef} {...provided.droppableProps}>
                      <div className="tm-slot-label">{slot.id}</div>
                      <div className="tm-slot-activities">
                        {slot.activities.map((activity, index) => (
                          <Draggable key={activity.id + '-' + slot.id + '-' + index} draggableId={activity.id + '-' + slot.id + '-' + index} index={index}>
                            {(providedDraggable) => (
                              <div 
                                className="tm-slot-activity" 
                                ref={providedDraggable.innerRef} 
                                {...providedDraggable.draggableProps} 
                                {...providedDraggable.dragHandleProps}
                              >
                                <div className="tm-slot-activity-header">
                                  <span className="tm-activity-icon">{activity.icon}</span>
                                  <span className="tm-activity-title">{activity.title}</span>
                                </div>
                                <div className="tm-duration-control">
                                  <input 
                                    type="range" 
                                    min="30" 
                                    max="120" 
                                    step="15" 
                                    value={activity.duration} 
                                    onChange={(e) => handleDurationChange(slot.id, index, parseInt(e.target.value, 10))}
                                  />
                                  <span>{activity.duration} min</span>
                                </div>
                              </div>
                            )}
                          </Draggable>
                        ))}
                        {provided.placeholder}
                      </div>
                    </div>
                  )}
                </Droppable>
              ))}
            </div>
          </div>
        </div>
      </DragDropContext>
      <div className="tm-save-container">
        <button onClick={handleSave} disabled={saving}>
          {saving ? 'Guardando...' : 'Guardar mi d√≠a'}
        </button>
      </div>
    </div>
  );
}

export default TimeMachine;
