// src/components/Dashboard.js

import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import Calendar from 'react-calendar';
import axios from 'axios';
import 'react-calendar/dist/Calendar.css';
import '../styles/home.css';
import '../styles/taskPlanner.css';

// Decodifica JWT para extraer userId
function decodeJwt(token) {
  try {
    const b64 = token.split('.')[1].replace(/-/g, '+').replace(/_/g, '/');
    return JSON.parse(atob(b64));
  } catch {
    return null;
  }
}
function getUserId() {
  const raw = localStorage.getItem('token');
  if (!raw) return null;
  const token = raw.startsWith('Bearer ') ? raw.slice(7) : raw;
  const payload = decodeJwt(token);
  return payload?.id || payload?.sub || payload?.user?.id || null;
}

export default function Home() {
  const navigate    = useNavigate();
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [showPanel,    setShowPanel]    = useState(false);
  const [showModal,    setShowModal]    = useState(false);
  const [upcoming,     setUpcoming]     = useState([]);
  const [countdowns,   setCountdowns]   = useState({});
  const [newTitle,     setNewTitle]     = useState('');
  const [newDateTime,  setNewDateTime]  = useState('');
  const timerRef = useRef();

  // 1) Carga tareas/reminders desde el backend
  useEffect(() => {
    const fetchTasks = async () => {
      const token  = localStorage.getItem('token');
      const userId = getUserId();
      if (!token || !userId) return;
      try {
        const res = await axios.get('/api/tasks', {
          headers: { 'x-auth-token': token }
        });
        const tasks = Array.isArray(res.data) ? res.data : [];
        const now = Date.now();
        // solo futuras y ordenadas por startTime
        const up = tasks
          .filter(t => new Date(t.startTime).getTime() > now)
          .sort((a, b) => new Date(a.startTime) - new Date(b.startTime));
        setUpcoming(up);
      } catch (err) {
        console.error('Error cargando tareas:', err);
      }
    };
    fetchTasks();
    return () => clearInterval(timerRef.current);
  }, []);

  // 2) Countdown para cada reminder
  useEffect(() => {
    if (!upcoming.length) return;
    const updateCounts = () => {
      const now = Date.now();
      const counts = {};
      upcoming.forEach(t => {
        const diff = new Date(t.startTime).getTime() - now;
        if (diff > 0) {
          const days = String(Math.floor(diff / 86400000)).padStart(2,'0');
          const hrs  = String(Math.floor((diff % 86400000)/3600000)).padStart(2,'0');
          const mins = String(Math.floor((diff % 3600000)/60000)).padStart(2,'0');
          const secs = String(Math.floor((diff % 60000)/1000)).padStart(2,'0');
          counts[t._id] = `${days}:${hrs}:${mins}:${secs}`;
        } else {
          counts[t._id] = '00:00:00:00';
        }
      });
      setCountdowns(counts);
    };

    updateCounts();
    timerRef.current = setInterval(updateCounts, 1000);
    return () => clearInterval(timerRef.current);
  }, [upcoming]);

  // 3) Crear un nuevo recordatorio
  const handleCreate = async () => {
    const token = localStorage.getItem('token');
    if (!newTitle || !newDateTime || !token) return;
    try {
      await axios.post(
        '/api/tasks',
        {
          title: newTitle,
          startTime: newDateTime,
          endTime: newDateTime
        },
        { headers: { 'x-auth-token': token } }
      );
      setNewTitle('');
      setNewDateTime('');
      setShowModal(false);

      // refrescar lista de recordatorios
      const res = await axios.get('/api/tasks', {
        headers: { 'x-auth-token': token }
      });
      const tasks = Array.isArray(res.data) ? res.data : [];
      const now   = Date.now();
      setUpcoming(tasks.filter(t => new Date(t.startTime).getTime() > now));
    } catch (err) {
      console.error('Error creando recordatorio:', err);
    }
  };

  // 4) Decorar días con algún reminder en el calendario
  const tileContent = ({ date, view }) => {
    if (view === 'month') {
      const day = date.toISOString().slice(0,10);
      const has = upcoming.some(t => t.startTime.slice(0,10) === day);
      return has ? <div className="task-dot" /> : null;
    }
    return null;
  };

  const togglePanel      = () => setShowPanel(v => !v);
  const handleDateChange = date => setSelectedDate(date);
  const handleLogout     = ()   => navigate('/');

  return (
    <div className="home-container">
      <header className="top-bar">
        <div className="left-section">
          <i className="fa fa-user-circle user-icon" aria-hidden="true" />
        </div>
        <div className="center-section">
          <h1 className="title">JANUS</h1>
        </div>
        <div className="right-section">
          <div className="notification-wrapper" onClick={togglePanel}>
            <i className="fa fa-bell bell-icon" aria-hidden="true" />
            {upcoming.length > 0 && <span className="notification-dot" />}
          </div>
          <button className="logout-btn" onClick={handleLogout}>
            <i className="fa fa-sign-out" aria-hidden="true" /><span>Salir</span>
          </button>
        </div>
      </header>

      {showPanel && (
        <div className="notification-panel">
          <h3>Próximas actividades</h3>
          {upcoming.length === 0 ? (
            <p>No hay actividades programadas.</p>
          ) : (
            upcoming.map(t => (
              <div key={t._id} className="notification-item">
                <div className="notif-title">{t.title}</div>
                <div className="notif-countdown">{countdowns[t._id]}</div>
              </div>
            ))
          )}
        </div>
      )}

      <main className="main-content">
        <section className="intro-section">
          <h2>¡Bienvenido/a a JANUS!</h2>
          <p>
            Explora las distintas opciones que tenemos para ti o crea un recordatorio:
          </p>
          <button
            className="create-task-btn"
            onClick={() => setShowModal(true)}
          >
            <i className="fa fa-plus" aria-hidden="true" /> Añadir recordatorio
          </button>
        </section>

        <div className="buttons-and-calendar">
          <div className="buttons-block">
            <button className="option-btn" onClick={() => navigate('/messaging')}>
              <i className="fa fa-comments icon" aria-hidden="true"></i> Mensajería
            </button>
            <button className="option-btn" onClick={() => navigate('/time-machine-game')}>
              <i className="fa fa-clock-o icon" aria-hidden="true"></i> Máquina del Tiempo
            </button>
            <button className="option-btn" onClick={() => navigate('/mi-planeta')}>
              <i className="fa fa-globe icon" aria-hidden="true"></i> Mi Planeta
            </button>
            <button className="option-btn" onClick={() => navigate('/patient/templates')}>
              <i className="fa fa-folder-open icon" aria-hidden="true"></i> Mis Plantillas
            </button>
            <button className="option-btn" onClick={() => navigate('/diario-de-emociones')}>
              <i className="fa fa-book icon" aria-hidden="true"></i> Mi diario de emociones
            </button>
          </div>
          <div className="calendar-block">
            <Calendar
              onChange={handleDateChange}
              value={selectedDate}
              tileContent={tileContent}
            />
          </div>
        </div>
      </main>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Nuevo Recordatorio</h2>
            <div className="create-task-form">
              <label>
                Título
                <input
                  type="text"
                  value={newTitle}
                  onChange={e => setNewTitle(e.target.value)}
                />
              </label>
              <label>
                Fecha y hora
                <input
                  type="datetime-local"
                  value={newDateTime}
                  onChange={e => setNewDateTime(e.target.value)}
                />
              </label>
            </div>
            <div className="modal-buttons">
              <button className="save-btn" onClick={handleCreate}>Guardar</button>
              <button className="cancel-btn" onClick={() => setShowModal(false)}>Cancelar</button>
            </div>
          </div>
        </div>
      )}

      <footer className="footer-bar">
        <p>2025 © Iván Vela Campos</p>
      </footer>
    </div>
  );
}
