import React, { useState, useEffect } from 'react';
import { FaCheckCircle, FaRegCheckCircle, FaFilter, FaSearch, FaInfoCircle } from 'react-icons/fa';
import authService from '../services/authService';
import routineTemplateService from '../services/routineTemplateService';
import './routineTemplates.css';

const PatientTemplates = () => {
  const [templates, setTemplates] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [expanded, setExpanded] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Load assigned templates on mount
  useEffect(() => {
    async function fetchTemplates() {
      try {
        const user = authService.getCurrentUser();
        const res = await routineTemplateService.getAssignedByPatient(user.id);
        setTemplates(res.data);
        setFiltered(res.data);
      } catch (err) {
        setError('Error al cargar plantillas.');
      } finally {
        setLoading(false);
      }
    }
    fetchTemplates();
  }, []);

  // Apply search & status filters
  useEffect(() => {
    let list = [...templates];
    if (searchTerm) {
      list = list.filter(t =>
        t.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        t.description.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }
    if (statusFilter !== 'all') {
      list = list.filter(t => {
        const total = t.activities.length;
        const done = t.activities.filter(a => a.completed).length;
        const state = done === total ? 'completed' : done === 0 ? 'pending' : 'in-progress';
        return state === statusFilter;
      });
    }
    setFiltered(list);
  }, [searchTerm, statusFilter, templates]);

  const toggleExpand = (id) => {
    setExpanded(prev => ({ ...prev, [id]: !prev[id] }));
  };

  const handleComplete = async (tId, aId) => {
    try {
      await routineTemplateService.completeActivity(tId, aId);
      setTemplates(prev => prev.map(t => {
        if (t.id !== tId) return t;
        return {
          ...t,
          activities: t.activities.map(a => a.id === aId ? { ...a, completed: true } : a)
        };
      }));
    } catch (e) {
      console.error(e);
      alert('No se pudo marcar como completada.');
    }
  };

  if (loading) return <div className="loading">Cargando plantillas...</div>;
  if (error)   return <div className="error">{error}</div>;

  return (
    <div className="routine-templates-container">
      <header className="templates-header">
        <h1>Mis Plantillas</h1>
        <div className="filters">
          <div className="search-box">
            <FaSearch />
            <input
              type="text"
              placeholder="Buscar plantillas..."
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
            />
          </div>
          <div className="status-filter">
            <FaFilter />
            <select value={statusFilter} onChange={e => setStatusFilter(e.target.value)}>
              <option value="all">Todas</option>
              <option value="pending">Pendientes</option>
              <option value="in-progress">En Progreso</option>
              <option value="completed">Completadas</option>
            </select>
          </div>
        </div>
      </header>

      <main className="templates-grid">
        {filtered.length === 0 ? (
          <div className="no-results">No se encontraron plantillas.</div>
        ) : filtered.map(template => {
          const doneCount = template.activities.filter(a => a.completed).length;
          const totalCount = template.activities.length;
          const progress = Math.round((doneCount / totalCount) * 100);

          return (
            <article key={template.id} className="template-card">
              <div className="template-card-header">
                <h2>{template.name}</h2>
                <button onClick={() => toggleExpand(template.id)} className="toggle-details">
                  <FaInfoCircle /> {expanded[template.id] ? 'Ocultar' : 'Ver Detalles'}
                </button>
              </div>
              <div className="template-description">
                {template.description}
              </div>
              <div className="template-progress">
                <div className="progress-bar">
                  <div
                    className="progress-filled"
                    style={{ width: `${progress}%` }}
                  />
                </div>
                <span>{progress}% completado</span>
              </div>

              {expanded[template.id] && (
                <ul className="activity-list">
                  {template.activities.map(activity => (
                    <li key={activity.id} className="activity-item">
                      <div className="activity-main">
                        {activity.completed ? <FaCheckCircle /> : <FaRegCheckCircle />}
                        <div className="activity-info">
                          <h3>{activity.title}</h3>
                          <p>{activity.description}</p>
                          <div className="activity-meta">
                            <span className="badge type">{activity.type}</span>
                            <span className="badge schedule">
                              {activity.scheduledDays.join(', ')}
                            </span>
                            {activity.dueDate && <span className="badge due">Para: {new Date(activity.dueDate).toLocaleDateString()}</span>}
                          </div>
                        </div>
                      </div>
                      {!activity.completed && (
                        <button
                          className="btn-complete"
                          onClick={() => handleComplete(template.id, activity.id)}
                        >
                          Marcar como completada
                        </button>
                      )}
                    </li>
                  ))}
                </ul>
              )}
            </article>
          );
        })}
      </main>
    </div>
  );
};

export default PatientTemplates;
