// src/pages/PatientTemplatesPage.js
import React, { useState, useEffect, useMemo } from 'react';
import {
  FaChevronDown,
  FaChevronUp,
  FaCheckCircle,
  FaRegCircle
} from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';
import '../styles/routineTemplates.css';

export default function PatientTemplatesPage() {
  const [instances, setInstances] = useState([]);
  const [expanded, setExpanded]   = useState({});
  const [loading, setLoading]     = useState(true);
  const [error, setError]         = useState(null);
  const [filter, setFilter]       = useState('all');
  const [search, setSearch]       = useState('');
  const [sortOrder, setSortOrder] = useState('recent');
  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  // Extraer patientId del payload del JWT
  let patientId = null;
  if (token) {
    try {
      const payload = JSON.parse(atob(token.split('.')[1]));
      patientId = payload.user?.id || payload.id || payload.sub;
    } catch (e) {
      console.error('Error decodificando token:', e);
    }
  }

  // Fetch de instancias asignadas
  useEffect(() => {
    if (!token || !patientId) {
      alert('Debes iniciar sesi√≥n como paciente.');
      navigate('/login');
      return;
    }
    const headers = {
      'Content-Type': 'application/json',
      'x-auth-token': token
    };
    fetch(`/api/routines/instances/${patientId}`, { headers })                      /* :contentReference[oaicite:0]{index=0}:contentReference[oaicite:1]{index=1} */
      .then(res => {
        if (res.status === 401) {
          alert('Sesi√≥n expirada. Vuelve a iniciar sesi√≥n.');
          navigate('/login');
          return null;
        }
        if (!res.ok) throw new Error(`HTTP ${res.status}`);
        return res.json();
      })
      .then(data => {
        if (Array.isArray(data)) setInstances(data);
        else throw new Error('Datos inv√°lidos del servidor');
      })
      .catch(err => setError(err.message))
      .finally(() => setLoading(false));
  }, [token, patientId, navigate]);

  // Handler de marcado de actividad
  const markActivity = (instId, idx, completed) => {
    const headers = {
      'Content-Type': 'application/json',
      'x-auth-token': token
    };
    fetch(`/api/routines/instances/${instId}/activities/${idx}`, {
      method: 'POST',
      headers,
      body: JSON.stringify({ completed })
    })                                                                              
      .then(res => {
        if (!res.ok) throw new Error('No se pudo actualizar');
        // Actualizaci√≥n local optimista
        setInstances(list =>
          list.map(i =>
            i._id !== instId
              ? i
              : {
                  ...i,
                  completedActivities: completed
                    ? [...i.completedActivities, idx]
                    : i.completedActivities.filter(j => j !== idx)
                }
          )
        );
      })
      .catch(console.error);
  };

  // Completar todas las actividades
  const completeAll = inst => {
    const toComplete = inst.template.activities
      .map((_, i) => i)
      .filter(i => !inst.completedActivities.includes(i));
    toComplete.forEach(idx => markActivity(inst._id, idx, true));
  };

  // Posponer (cambia solo UI)
  const postpone = instId => {
    setInstances(list =>
      list.map(i =>
        i._id === instId ? { ...i, status: 'postponed' } : i
      )
    );
  };

  // Expandir / colapsar detalles
  const toggleExpand = id =>
    setExpanded(e => ({ ...e, [id]: !e[id] }));

  // Filtrado, b√∫squeda y ordenaci√≥n memoizados
  const displayed = useMemo(() => {
    let arr = instances.filter(i =>
      (filter === 'all' || i.status === filter) &&
      i.template.name.toLowerCase().includes(search.toLowerCase())
    );
    if (sortOrder === 'name') {
      arr.sort((a, b) =>
        a.template.name.localeCompare(b.template.name)
      );
    } else {
      arr.sort((a, b) =>
        new Date(b.createdAt) - new Date(a.createdAt)
      );
    }
    return arr;
  }, [instances, filter, search, sortOrder]);

  if (loading) {
    return (
      <div className="tpl-container">
        <h1>Mis Plantillas</h1>
        <p>Cargando plantillas‚Ä¶</p>
      </div>
    );
  }
  if (error) {
    return (
      <div className="tpl-container">
        <h1>Mis Plantillas</h1>
        <p style={{ color: 'red' }}>Error: {error}</p>
      </div>
    );
  }

  return (
    <div className="tpl-container patient-assigns">                           {/* :contentReference[oaicite:2]{index=2}:contentReference[oaicite:3]{index=3} */}
      <div className="tpl-header">
        <h1>Mis Plantillas</h1>
        <div style={{ display: 'flex', gap: '0.5rem' }}>
          <select value={filter} onChange={e => setFilter(e.target.value)}>
            <option value="all">Todas</option>
            <option value="pending">Pendientes</option>
            <option value="completed">Completadas</option>
            <option value="postponed">Pospuestas</option>
          </select>
          <input
            type="text"
            placeholder="Buscar..."
            value={search}
            onChange={e => setSearch(e.target.value)}
          />
          <select value={sortOrder} onChange={e => setSortOrder(e.target.value)}>
            <option value="recent">M√°s recientes</option>
            <option value="name">Nombre A‚ÜíZ</option>
          </select>
        </div>
      </div>

      {displayed.length === 0 ? (
        <div className="empty-state" style={{ marginTop: '2rem' }}>
          ü§î No hay plantillas que coincidan
        </div>
      ) : (
        <div className="tpl-grid">                                             {/* :contentReference[oaicite:4]{index=4}:contentReference[oaicite:5]{index=5} */}
          {displayed.map(inst => {
            const tmpl = inst.template;
            const total = tmpl.activities.length;
            const done  = inst.completedActivities.length;
            const pct   = total ? (done / total) * 100 : 0;
            return (
              <div
                key={inst._id}
                className={`tpl-card ${
                  inst.status === 'completed'
                    ? 'status-completed'
                    : inst.status === 'postponed'
                    ? 'status-postponed'
                    : ''
                }`}
              >
                <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                  <div>
                    <h2>{tmpl.name}</h2>
                    <small>Asignada el {new Date(inst.createdAt).toLocaleDateString()}</small>
                  </div>
                  <button
                    className="btn-icon"
                    onClick={() => toggleExpand(inst._id)}
                  >
                    {expanded[inst._id] ? <FaChevronUp /> : <FaChevronDown />}
                  </button>
                </div>

                <p className="tpl-desc">{tmpl.description}</p>

                <div style={{ margin: '0.5rem 0' }}>
                  <label>{done}/{total} completadas</label>
                  <progress value={pct} max="100" style={{ width: '100%' }} />
                </div>

                <div className="assign-actions">
                  {inst.status === 'pending' && (
                    <>
                      <button
                        className="btn-complete"
                        onClick={() => completeAll(inst)}
                      >
                        Completar todo
                      </button>
                      <button
                        className="btn-postpone"
                        onClick={() => postpone(inst._id)}
                      >
                        Posponer
                      </button>
                    </>
                  )}
                  {inst.status === 'completed' && <span>‚úîÔ∏è Completada</span>}
                  {inst.status === 'postponed' && <span>‚è≥ Pospuesta</span>}
                </div>

                {expanded[inst._id] && (
                  <div className="activities-list" style={{ marginTop: '1rem' }}>
                    {tmpl.activities.map((act, idx) => {
                      const completed = inst.completedActivities.includes(idx);
                      return (
                        <div
                          key={idx}
                          className={`activity-line ${completed ? 'completed' : ''}`}
                          style={{ display: 'flex', justifyContent: 'space-between' }}
                        >
                          <div>
                            {completed ? <FaCheckCircle /> : <FaRegCircle />}
                            <span style={{ marginLeft: '0.5rem' }}>
                              {act.name} ({act.minutes}min, {act.challenge})
                            </span>
                          </div>
                          <button
                            className="btn-complete"
                            onClick={() => markActivity(inst._id, idx, !completed)}
                          >
                            {completed ? 'Desmarcar' : 'Marcar'}
                          </button>
                        </div>
                      );
                    })}
                  </div>
                )}
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
}
