// src/pages/NewRoutineTemplatePage.js
import React, { useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/routineTemplates.css';
import { FaGripVertical, FaPlus, FaTrash } from 'react-icons/fa';

export default function NewRoutineTemplatePage() {
  const navigate = useNavigate();
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [category, setCategory] = useState('');
  const [tags, setTags] = useState([]);
  const [tagInput, setTagInput] = useState('');
  const [duration, setDuration] = useState(30);
  const [reminder, setReminder] = useState('none');
  const [activities, setActivities] = useState([]);

  const addActivity = () => {
    setActivities(a => [
      ...a,
      { id: Date.now(), name: '', desc: '', challenge: 'Bajo', minutes: 10 }
    ]);
  };

  const updateActivity = (id, field, val) => {
    setActivities(a =>
      a.map(x => x.id === id ? { ...x, [field]: val } : x)
    );
  };

  const removeActivity = id => {
    setActivities(a => a.filter(x => x.id !== id));
  };

  // Reorder drag & drop
  const onDragStart = (e, idx) => {
    e.dataTransfer.setData('text/plain', idx);
  };
  const onDrop = (e, idx) => {
    const draggedIdx = +e.dataTransfer.getData('text/plain');
    if (draggedIdx === idx) return;
    setActivities(a => {
      const copy = [...a];
      const [moved] = copy.splice(draggedIdx, 1);
      copy.splice(idx, 0, moved);
      return copy;
    });
  };
  const allowDrop = e => e.preventDefault();

  const addTag = () => {
    const t = tagInput.trim();
    if (t && !tags.includes(t)) setTags(ts => [...ts, t]);
    setTagInput('');
  };
  const removeTag = t => setTags(ts => ts.filter(x => x !== t));

  const handleSubmit = async () => {
    const payload = {
      name, description, category, tags, duration,
      reminder, activities
    };
    const res = await fetch('/api/routinetemplates', {
      method:'POST',
      headers:{ 'Content-Type':'application/json' },
      body: JSON.stringify(payload)
    });
    if (res.ok) navigate('/therapist/templates');
    else alert('Error al guardar. Revisa los datos.');
  };

  return (
    <div className="routine-templates-page">
      <div className="tpl-container tpl-form">
        <h2>Crear Nueva Plantilla</h2>

        <label>Nombre de la plantilla</label>
        <input value={name} onChange={e=>setName(e.target.value)} placeholder="Ej. Rutina matutina" />

        <label>DescripciÃ³n general</label>
        <textarea value={description}
                  onChange={e=>setDescription(e.target.value)}
                  rows={3}
                  placeholder="Describe el objetivo de esta plantilla" />

        <label>CategorÃ­a</label>
        <select value={category} onChange={e=>setCategory(e.target.value)}>
          <option value="">â€” Selecciona â€”</option>
          <option>RelajaciÃ³n</option>
          <option>Ejercicio FÃ­sico</option>
          <option>Mindfulness</option>
          <option>HÃ¡bito Saludable</option>
        </select>

        <label>Tags</label>
        <div className="tag-input-group">
          <input value={tagInput}
                 onChange={e=>setTagInput(e.target.value)}
                 placeholder="AÃ±adir tag y pulsar Enter"
                 onKeyDown={e=> e.key==='Enter' && (e.preventDefault(),addTag())} />
          <button className="btn-new" style={{padding:'0.5rem'}} onClick={addTag}><FaPlus/></button>
        </div>
        <div className="tags-list">
          {tags.map(t=>(
            <span key={t} className="tag-pill">
              {t} <FaTrash onClick={()=>removeTag(t)} style={{cursor:'pointer'}}/>
            </span>
          ))}
        </div>

        <label>DuraciÃ³n total estimada (min)</label>
        <input type="number" value={duration}
               onChange={e=>setDuration(+e.target.value)} min={5} />

        <label>Recordatorio</label>
        <select value={reminder} onChange={e=>setReminder(e.target.value)}>
          <option value="none">Sin recordatorio</option>
          <option value="daily">Diario</option>
          <option value="weekly">Semanal</option>
        </select>

        <hr style={{margin:'2rem 0'}}/>

        <h3>Actividades ({activities.length})</h3>
        <button className="btn-new" onClick={addActivity}>
          <FaPlus style={{marginRight:'0.5rem'}}/> AÃ±adir Actividad
        </button>

        <div className="activities-list">
          {activities.map((act, idx) => (
            <div key={act.id}
                 className="activity-item"
                 draggable
                 onDragStart={e=>onDragStart(e, idx)}
                 onDragOver={allowDrop}
                 onDrop={e=>onDrop(e, idx)}>
              <FaGripVertical className="drag-handle"/>
              <input className="small-input"
                     placeholder="Nombre actividad"
                     value={act.name}
                     onChange={e=>updateActivity(act.id,'name',e.target.value)}/>
              <input className="small-input"
                     placeholder="DescripciÃ³n breve"
                     value={act.desc}
                     onChange={e=>updateActivity(act.id,'desc',e.target.value)}/>
              <select className="small-select"
                      value={act.challenge}
                      onChange={e=>updateActivity(act.id,'challenge',e.target.value)}>
                <option>Bajo</option>
                <option>Medio</option>
                <option>Alto</option>
              </select>
              <input type="number"
                     className="small-input"
                     style={{width:'4rem'}}
                     value={act.minutes}
                     onChange={e=>updateActivity(act.id,'minutes',+e.target.value)}/>
              <FaTrash className="remove-ico" onClick={()=>removeActivity(act.id)}/>
            </div>
          ))}
        </div>

        <button className="btn-save" onClick={handleSubmit}>
          ðŸ’¾ Guardar Plantilla
        </button>
      </div>
    </div>
  );
}
