import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import axios from 'axios';
import { v4 as uuid } from 'uuid';        // Genera IDs √∫nicos
import '../styles/timeMachine.css';

// Lista de actividades "plantilla" (pool)
const initialActivities = [
  { id: 'futbol', title: 'Jugar al f√∫tbol', icon: '‚öΩ' },
  { id: 'dibujos', title: 'Ver dibujos animados', icon: 'üì∫' },
  { id: 'comics', title: 'Leer c√≥mics', icon: 'üìö' },
  { id: 'tarea', title: 'Hacer tarea', icon: 'üìù' },
  { id: 'videojuegos', title: 'Jugar videojuegos', icon: 'üéÆ' },
  { id: 'helado', title: 'Comer helado', icon: 'üç¶' },
  { id: 'parque', title: 'Ir al parque', icon: 'üèûÔ∏è' },
  { id: 'banio', title: 'Ba√±arse', icon: 'üõÅ' },
  { id: 'dormir', title: 'Dormir', icon: 'üò¥' },
  { id: 'musica', title: 'Escuchar m√∫sica', icon: 'üéµ' },
  { id: 'bailar', title: 'Bailar', icon: 'üíÉ' },
  { id: 'amigos', title: 'Jugar con amigos', icon: 'üë´' },
  { id: 'bici', title: 'Montar en bicicleta', icon: 'üö≤' },
  { id: 'dibujar', title: 'Dibujar', icon: 'üé®' },
  { id: 'mascotas', title: 'Jugar con mascotas', icon: 'üê∂' },
  { id: 'experimentos', title: 'Hacer experimentos', icon: 'üî¨' },
  { id: 'cantar', title: 'Cantar', icon: 'üé§' },
  { id: 'lego', title: 'Construir con LEGO', icon: 'üß±' },
  { id: 'nadar', title: 'Nadar', icon: 'üèä' },
  { id: 'pc', title: 'Jugar en la computadora', icon: 'üíª' }
];

// Genera 24 slots horarios (00:00 a 23:00)
const generateTimeSlots = () => {
  const slots = [];
  for (let i = 0; i < 24; i++) {
    const label = `${String(i).padStart(2, '0')}:00`;
    slots.push({ id: label, activities: [] });
  }
  return slots;
};

function TimeMachine() {
  const [dayType, setDayType] = useState('Mi√©rcoles');
  const [pool, setPool] = useState(initialActivities);    // Actividades disponibles (plantilla)
  const [timeSlots, setTimeSlots] = useState(generateTimeSlots());
  const [instructionsVisible, setInstructionsVisible] = useState(true);
  const [saving, setSaving] = useState(false);

  // Manejador de arrastre
  const onDragEnd = (result) => {
    const { source, destination } = result;
    if (!destination) return;   // Solt√≥ fuera de un droppable
    if (
      source.droppableId === destination.droppableId &&
      source.index === destination.index
    ) {
      return;                   // No hay cambios
    }

    // CASO 1: Arrastrar desde el pool a un slot
    if (source.droppableId === 'pool' && destination.droppableId !== 'pool') {
      // Tomamos la actividad "plantilla" (pero NO la quitamos del pool si queremos usarla varias veces)
      const activity = pool[source.index];

      // Creamos una "instancia" nueva con un ID √∫nico
      const instance = {
        instanceId: uuid(),           // Nuevo ID √∫nico
        templateId: activity.id,      // Referencia a la plantilla
        title: activity.title,
        icon: activity.icon,
        duration: 60                  // Duraci√≥n por defecto
      };

      // Insertar la instancia en el slot destino
      const newSlots = Array.from(timeSlots);
      const destSlotIndex = newSlots.findIndex(s => s.id === destination.droppableId);
      const destActivities = Array.from(newSlots[destSlotIndex].activities);

      destActivities.splice(destination.index, 0, instance);
      newSlots[destSlotIndex].activities = destActivities;
      setTimeSlots(newSlots);

      // Si quieres que desaparezca del pool (solo se use una vez), descomenta:
      // const newPool = Array.from(pool);
      // newPool.splice(source.index, 1);
      // setPool(newPool);

      return;
    }

    // CASO 2: Mover dentro de un mismo slot o entre slots
    if (source.droppableId !== 'pool' && destination.droppableId !== 'pool') {
      const newSlots = Array.from(timeSlots);

      // El slot origen
      const sourceSlotIndex = newSlots.findIndex(s => s.id === source.droppableId);
      const sourceActivities = Array.from(newSlots[sourceSlotIndex].activities);
      const [movedItem] = sourceActivities.splice(source.index, 1);
      newSlots[sourceSlotIndex].activities = sourceActivities;

      // El slot destino
      const destSlotIndex = newSlots.findIndex(s => s.id === destination.droppableId);
      const destActivities = Array.from(newSlots[destSlotIndex].activities);
      destActivities.splice(destination.index, 0, movedItem);
      newSlots[destSlotIndex].activities = destActivities;

      setTimeSlots(newSlots);
    }

    // CASO 3: Arrastrar de un slot de vuelta al pool (si quisieras permitirlo):
    // if (source.droppableId !== 'pool' && destination.droppableId === 'pool') { ... }
  };

  // Ajustar la duraci√≥n de una actividad en un slot
  const handleDurationChange = (slotId, index, newDuration) => {
    const newSlots = Array.from(timeSlots);
    const slotIndex = newSlots.findIndex(s => s.id === slotId);
    newSlots[slotIndex].activities[index].duration = newDuration;
    setTimeSlots(newSlots);
  };

  // Guardar en backend
  const handleSave = async () => {
    setSaving(true);
    // Preparar datos para el POST
    const resultData = {
      day: dayType,
      timeSlots: timeSlots
        .filter(slot => slot.activities.length > 0)
        .map(slot => ({
          slot: slot.id,
          activities: slot.activities
        }))
    };

    try {
      await axios.post('/api/time-machine', resultData, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      alert('Resultado guardado exitosamente');
    } catch (err) {
      console.error('Error guardando resultado:', err);
      alert('Error al guardar. Int√©ntalo de nuevo.');
    }
    setSaving(false);
  };

  return (
    <div className="time-machine-container">
      <header className="tm-header">
        <h1>La M√°quina del Tiempo</h1>
        <select value={dayType} onChange={(e) => setDayType(e.target.value)}>
          <option value="Mi√©rcoles">Mi√©rcoles</option>
          <option value="S√°bado">S√°bado</option>
        </select>
      </header>

      {instructionsVisible && (
        <div className="tm-instructions">
          <p>
            ¬°Esta es tu m√°quina del tiempo! Arrastra las actividades que normalmente haces 
            un {dayType}. Ajusta el tiempo deslizando la barra para cada actividad.
          </p>
          <button onClick={() => setInstructionsVisible(false)}>Cerrar instrucciones</button>
        </div>
      )}

      <DragDropContext onDragEnd={onDragEnd}>
        <div className="tm-content">
          {/* Pool de actividades */}
          <div className="tm-pool">
            <h2>Actividades disponibles</h2>
            <Droppable droppableId="pool">
              {(provided) => (
                <div ref={provided.innerRef} {...provided.droppableProps}>
                  {pool.map((activity, index) => (
                    <Draggable key={activity.id} draggableId={activity.id} index={index}>
                      {(providedDraggable) => (
                        <div
                          className="tm-activity-card"
                          ref={providedDraggable.innerRef}
                          {...providedDraggable.draggableProps}
                          {...providedDraggable.dragHandleProps}
                        >
                          <span className="tm-activity-icon">{activity.icon}</span>
                          <span className="tm-activity-title">{activity.title}</span>
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          </div>

          {/* Tablero con slots */}
          <div className="tm-board">
            <h2>Organiza tu d√≠a</h2>
            <div className="tm-timeline">
              {timeSlots.map((slot) => (
                <Droppable droppableId={slot.id} key={slot.id}>
                  {(providedSlot) => (
                    <div
                      className="tm-slot"
                      ref={providedSlot.innerRef}
                      {...providedSlot.droppableProps}
                    >
                      <div className="tm-slot-label">{slot.id}</div>
                      <div className="tm-slot-activities">
                        {slot.activities.map((act, index) => (
                          <Draggable
                            key={act.instanceId}               // ID √∫nico por instancia
                            draggableId={act.instanceId}        // Igual
                            index={index}
                          >
                            {(providedActivity) => (
                              <div
                                className="tm-slot-activity"
                                ref={providedActivity.innerRef}
                                {...providedActivity.draggableProps}
                                {...providedActivity.dragHandleProps}
                              >
                                <div className="tm-slot-activity-header">
                                  <span className="tm-activity-icon">{act.icon}</span>
                                  <span className="tm-activity-title">{act.title}</span>
                                </div>
                                <div className="tm-duration-control">
                                  <input
                                    type="range"
                                    min="30"
                                    max="120"
                                    step="15"
                                    value={act.duration}
                                    onChange={(e) =>
                                      handleDurationChange(slot.id, index, parseInt(e.target.value, 10))
                                    }
                                  />
                                  <span>{act.duration} min</span>
                                </div>
                              </div>
                            )}
                          </Draggable>
                        ))}
                        {providedSlot.placeholder}
                      </div>
                    </div>
                  )}
                </Droppable>
              ))}
            </div>
          </div>
        </div>
      </DragDropContext>

      {/* Bot√≥n de guardado */}
      <div className="tm-save-container">
        <button onClick={handleSave} disabled={saving}>
          {saving ? 'Guardando...' : 'Guardar mi d√≠a'}
        </button>
      </div>
    </div>
  );
}

export default TimeMachine;
