// src/pages/PatientTemplatesPage.js
import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/patientTemplates.css';
import { FaCheckCircle, FaRegCircle, FaFileDownload } from 'react-icons/fa';

export default function PatientTemplatesPage() {
  const [templates, setTemplates] = useState([]);
  const [filter, setFilter]       = useState('All');
  const [search, setSearch]       = useState('');
  const [loading, setLoading]     = useState(true);
  const [error, setError]         = useState(null);
  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  const headers = {
    'Content-Type': 'application/json',
    'x-auth-token': token
  };

  // Carga inicial de plantillas asignadas
  useEffect(() => {
    if (!token) return navigate('/login');
    fetch('/api/patient/templates', { headers })
      .then(res => {
        if (res.status === 401) throw new Error('Sesión expirada');
        if (!res.ok) throw new Error(`Error ${res.status}`);
        return res.json();
      })
      .then(data => {
        // Ordenar por fecha de asignación descendente
        const sorted = data.sort((a, b) =>
          new Date(b.assignedAt) - new Date(a.assignedAt)
        );
        setTemplates(sorted);
      })
      .catch(err => setError(err.message))
      .finally(() => setLoading(false));
  }, [navigate, token]);

  // Toggle de completado de actividad
  const toggleActivity = useCallback(async (tplId, idx) => {
    try {
      const res = await fetch(
        `/api/patient/templates/${tplId}/activities/${idx}/toggle`,
        { method: 'PATCH', headers }
      );
      if (!res.ok) throw new Error(`Error ${res.status}`);
      const updated = await res.json();
      setTemplates(ts =>
        ts.map(t => (t._id === tplId ? updated : t))
      );
    } catch (err) {
      console.error(err);
      alert('No se pudo actualizar el estado.');
    }
  }, [headers]);

  // Marcar todas como completadas
  const completeAll = useCallback(async tplId => {
    try {
      const res = await fetch(
        `/api/patient/templates/${tplId}/completeAll`,
        { method: 'PATCH', headers }
      );
      if (!res.ok) throw new Error(`Error ${res.status}`);
      const updated = await res.json();
      setTemplates(ts =>
        ts.map(t => (t._id === tplId ? updated : t))
      );
    } catch (err) {
      console.error(err);
      alert('No se pudo completar todas las actividades.');
    }
  }, [headers]);

  // Filtros y búsqueda
  const visible = templates.filter(t => {
    // Búsqueda por nombre
    if (search && !t.name.toLowerCase().includes(search.toLowerCase())) {
      return false;
    }
    // Filtrado por estado
    const doneCount = t.activities.filter(a => a.completed).length;
    if (filter === 'Completed' && doneCount !== t.activities.length) {
      return false;
    }
    if (filter === 'InProgress' && doneCount === 0) {
      return false;
    }
    return true;
  });

  if (loading) return <div className="ptpl-container">Cargando…</div>;
  if (error)   return <div className="ptpl-container error">Error: {error}</div>;

  return (
    <div className="patient-templates-page">
      <div className="ptpl-controls">
        <input
          type="text"
          placeholder="Buscar plantilla..."
          value={search}
          onChange={e => setSearch(e.target.value)}
        />
        <select
          value={filter}
          onChange={e => setFilter(e.target.value)}
        >
          <option value="All">Todas</option>
          <option value="InProgress">En progreso</option>
          <option value="Completed">Completadas</option>
        </select>
      </div>

      {visible.length === 0 ? (
        <div className="ptpl-empty">No hay plantillas que mostrar</div>
      ) : (
        <div className="ptpl-grid">
          {visible.map(tpl => {
            const done = tpl.activities.filter(a => a.completed).length;
            const total = tpl.activities.length;
            const pct = Math.round((done/total)*100);
            return (
              <div key={tpl._id} className="ptpl-card">
                <h2>{tpl.name}</h2>
                <p className="ptpl-desc">{tpl.description}</p>

                <div className="ptpl-meta">
                  <div className="ptpl-progress-bar">
                    <div
                      className="ptpl-progress"
                      style={{ width: `${pct}%` }}
                    />
                  </div>
                  <span>{pct}% completado</span>
                </div>

                <ul className="ptpl-activities">
                  {tpl.activities.map((act, i) => (
                    <li key={i}>
                      <button
                        className="ptpl-toggle"
                        onClick={() => toggleActivity(tpl._id, i)}
                      >
                        {act.completed
                          ? <FaCheckCircle/>
                          : <FaRegCircle/>
                        }
                      </button>
                      <div className="ptpl-act-info">
                        <strong>{act.name}</strong>
                        <span>{act.desc}</span>
                      </div>
                      <span className="ptpl-time">{act.minutes}′</span>
                    </li>
                  ))}
                </ul>

                <div className="ptpl-footer">
                  <button
                    className="btn-complete-all"
                    onClick={() => completeAll(tpl._id)}
                  >
                    Marcar todo completado
                  </button>
                  <button
                    className="btn-download"
                    onClick={() => alert('Descargando informe PDF…')}
                  >
                    <FaFileDownload/> Informe PDF
                  </button>
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
}
