// src/pages/PatientTemplates.js
import React, { useState, useEffect } from 'react';
import assignedRoutineService from '../services/assignedRoutineService';
import '../styles/routineTemplates.css';
import './patientTemplates.css';

export default function PatientTemplates() {
  const [templates, setTemplates] = useState([]);
  const [loading, setLoading] = useState(true);
  const [expandedIds, setExpandedIds] = useState([]);
  const [search, setSearch] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');
  const [sortKey, setSortKey] = useState('dueDate');

  useEffect(() => {
    assignedRoutineService.getAssignedTemplates()
      .then(res => {
        setTemplates(res.data);
        setLoading(false);
      })
      .catch(err => console.error(err));
  }, []);

  const handleSearch = e => setSearch(e.target.value.toLowerCase());
  const handleFilter = e => setFilterStatus(e.target.value);
  const handleSort   = e => setSortKey(e.target.value);

  const toggleExpand = id => {
    setExpandedIds(prev =>
      prev.includes(id) ? prev.filter(x => x !== id) : [...prev, id]
    );
  };

  // Filtrar, buscar y ordenar
  const visible = templates
    .filter(t => filterStatus === 'all' || t.status === filterStatus)
    .filter(t => t.title.toLowerCase().includes(search))
    .sort((a, b) => {
      if (sortKey === 'dueDate') return new Date(a.dueDate) - new Date(b.dueDate);
      if (sortKey === 'progress') return a.progress - b.progress;
      return 0;
    });

  const handleActivityToggle = (templateId, activityId) => {
    assignedRoutineService
      .toggleActivityCompletion(templateId, activityId)
      .then(({ data }) => {
        setTemplates(ts =>
          ts.map(t =>
            t.id === templateId
              ? {
                  ...t,
                  activities: t.activities.map(act =>
                    act.id === activityId ? { ...act, completed: data.completed } : act
                  )
                }
              : t
          )
        );
      });
  };

  const handleNoteChange = (templateId, activityId, note) => {
    setTemplates(ts =>
      ts.map(t =>
        t.id === templateId
          ? {
              ...t,
              activities: t.activities.map(act =>
                act.id === activityId ? { ...act, note } : act
              )
            }
          : t
      )
    );
  };

  const submitNote = (templateId, activityId, note) => {
    assignedRoutineService
      .saveActivityNote(templateId, activityId, note)
      .catch(err => console.error(err));
  };

  if (loading) return <div className="pt-loading">Cargando plantillas...</div>;

  return (
    <div className="pt-container">
      <header className="pt-header">
        <h1>Mis Plantillas Asignadas</h1>
        <div className="pt-controls">
          <input
            type="text"
            placeholder="Buscar plantilla..."
            value={search}
            onChange={handleSearch}
          />
          <select value={filterStatus} onChange={handleFilter}>
            <option value="all">Todas</option>
            <option value="pending">Pendientes</option>
            <option value="in-progress">En progreso</option>
            <option value="completed">Completadas</option>
          </select>
          <select value={sortKey} onChange={handleSort}>
            <option value="dueDate">Por fecha vencimiento</option>
            <option value="progress">Por progreso</option>
          </select>
        </div>
      </header>

      <div className="pt-grid">
        {visible.map(t => (
          <div key={t.id} className="pt-card">
            <div className="pt-card-header" onClick={() => toggleExpand(t.id)}>
              <div>
                <h2>{t.title}</h2>
                <small>Vence: {new Date(t.dueDate).toLocaleDateString()}</small>
              </div>
              <div className="pt-progress-wrapper">
                <div className="pt-progress-bar">
                  <div
                    className="pt-progress-filled"
                    style={{ width: `${t.progress}%` }}
                  />
                </div>
                <span className="pt-progress-label">{t.progress}%</span>
              </div>
            </div>

            {expandedIds.includes(t.id) && (
              <div className="pt-card-body">
                <p className="pt-description">{t.description}</p>
                <div className="pt-activities">
                  {t.activities.map(act => (
                    <div
                      key={act.id}
                      className={`pt-activity-item ${act.completed ? 'done' : ''}`}
                    >
                      <div className="pt-act-main">
                        <input
                          type="checkbox"
                          checked={act.completed}
                          onChange={() => handleActivityToggle(t.id, act.id)}
                        />
                        <div>
                          <h3>{act.name}</h3>
                          <p>{act.details}</p>
                          <small>Tiempo estimado: {act.estimatedTime} min</small>
                        </div>
                      </div>
                      <div className="pt-act-extra">
                        <textarea
                          placeholder="Añadir nota..."
                          value={act.note || ''}
                          onChange={e =>
                            handleNoteChange(t.id, act.id, e.target.value)
                          }
                          onBlur={() =>
                            submitNote(t.id, act.id, act.note || '')
                          }
                        />
                        <button
                          className="pt-btn-submit"
                          onClick={() =>
                            submitNote(t.id, act.id, act.note || '')
                          }
                        >
                          Guardar nota
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
