import React, { useState } from 'react';
import routineService from '../services/routineTemplateService';
import './styles/routineTemplates.css';
import { useNavigate } from 'react-router-dom';

export default function TemplateForm() {
  const [form, setForm] = useState({ title:'', description:'', type:'Ejercicio', duration:30 });
  const navigate = useNavigate();

  const handleSubmit = async e => {
    e.preventDefault();
    await routineService.createTemplate(form);
    navigate('/therapist/templates');
  };

  return (
    <form className="tpl-form" onSubmit={handleSubmit}>
      <h2>Crear Plantilla de Rutina</h2>
      <label>Título
        <input value={form.title} onChange={e=>setForm({...form, title:e.target.value})} required/>
      </label>
      <label>Descripción
        <textarea value={form.description} onChange={e=>setForm({...form, description:e.target.value})} required/>
      </label>
      <label>Tipo
        <select value={form.type} onChange={e=>setForm({...form, type:e.target.value})}>
          <option>Ejercicio</option>
          <option>Mindfulness</option>
          <option>Tarea</option>
        </select>
      </label>
      <label>Duración (min)
        <input type="number" value={form.duration} onChange={e=>setForm({...form, duration:+e.target.value})} min="5" required/>
      </label>
      <button type="submit" className="btn-save">Guardar Plantilla</button>
    </form>
  );
}
