import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/diarioEmociones.css';

function DiarioEmociones() {
  const navigate = useNavigate();
  const [timeMachineResults, setTimeMachineResults] = useState([]);
  const [ratings, setRatings] = useState({}); // resultId -> rating (1-5)
  const [loading, setLoading] = useState(true);

  // Verifica que el usuario esté autenticado
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/');
      return;
    }
    // Obtener resultados de la Máquina del Tiempo (rutinas guardadas)
    axios.get('http://localhost:5000/api/game', {
      headers: { 'x-auth-token': token }
    })
      .then(res => {
        setTimeMachineResults(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error al obtener resultados:', err);
        setLoading(false);
      });
  }, [navigate]);

  // Niveles de emoción
  const emotionLevels = [
    { label: 'Muy feliz', icon: '😄', value: 5 },
    { label: 'Feliz', icon: '🙂', value: 4 },
    { label: 'Neutral', icon: '😐', value: 3 },
    { label: 'Poco feliz', icon: '🙁', value: 2 },
    { label: 'Nada feliz', icon: '😞', value: 1 }
  ];

  // Seleccionar un nivel para un resultado específico
  const handleRatingSelect = (resultId, rating) => {
    setRatings(prev => ({ ...prev, [resultId]: rating }));
  };

  // Enviar la valoración (para cada resultado, se guarda de forma individual)
  const handleSubmitDiaryEntry = async (resultId) => {
    const token = localStorage.getItem('token');
    const rating = ratings[resultId];
    if (!rating) {
      alert('Por favor, selecciona una emoción.');
      return;
    }
    const result = timeMachineResults.find(r => r._id === resultId);
    if (!result) return;
    // Construir payload para guardar la entrada
    const payload = {
      timeMachineResultId: resultId,
      day: result.day,
      rating
    };
    try {
      await axios.post('http://localhost:5000/api/diario', payload, {
        headers: { 'x-auth-token': token }
      });
      alert('Entrada guardada.');
      // Opcional: remover el resultado ya calificado
      setTimeMachineResults(prev => prev.filter(r => r._id !== resultId));
    } catch (err) {
      console.error('Error al guardar la entrada:', err);
      alert('Error al guardar el diario.');
    }
  };

  if (loading) return <p>Cargando resultados...</p>;

  return (
    <div className="diario-emociones-container">
      <header className="top-bar">
        <div className="left-section">
          <button className="back-dashboard-btn" onClick={() => navigate('/dashboard')}>←</button>
          <i className="fa fa-smile-o user-icon" aria-hidden="true"></i>
        </div>
        <div className="center-section">
          <h1 className="title">Diario de Emociones</h1>
        </div>
      </header>

      <main className="main-content">
        <section className="instructions">
          <p>¿Cómo te sientes con lo bien que haces estas actividades?</p>
        </section>

        <section className="results-list">
          {timeMachineResults.length === 0 ? (
            <p>No hay resultados de Máquina del Tiempo para calificar.</p>
          ) : (
            timeMachineResults.map(result => (
              <div key={result._id} className="result-card">
                <h2>{result.day}</h2>
                <div className="activities-list">
                  {result.timeSlots.map((slot, idx) => (
                    <div key={idx} className="slot">
                      <span className="slot-label">{slot.slot}</span>
                      <div className="slot-activities">
                        {slot.activities.map((act, j) => (
                          <span key={j} className="activity-icon">{act.icon}</span>
                        ))}
                      </div>
                    </div>
                  ))}
                </div>
                <div className="emotion-selector">
                  {emotionLevels.map(level => (
                    <button
                      key={level.value}
                      className={`emotion-btn ${ratings[result._id] === level.value ? 'selected' : ''}`}
                      onClick={() => handleRatingSelect(result._id, level.value)}
                      title={level.label}
                    >
                      {level.icon}
                    </button>
                  ))}
                </div>
                <button className="submit-diary-btn" onClick={() => handleSubmitDiaryEntry(result._id)}>
                  Guardar entrada
                </button>
              </div>
            ))
          )}
        </section>
      </main>

      <footer className="footer-bar">
        <p>2025 © Iván Vela Campos</p>
      </footer>
    </div>
  );
}

export default DiarioEmociones;
