// src/components/PatientTemplates.js

import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import authService from '../services/authService';
import '../styles/routineTemplates.css';
import './patientTemplates.css';

export default function PatientTemplates() {
  const navigate = useNavigate();
  const [instances, setInstances] = useState([]);
  const [filterTag, setFilterTag] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // 1) Si no hay token, redirigimos al login inmediatamente
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/');
    }
  }, [navigate]);

  // 2) Cargamos las instancias del paciente
  useEffect(() => {
    const fetchInstances = async () => {
      const token = localStorage.getItem('token');
      const user = authService.getCurrentUser();
      if (!token || !user) {
        setError('No estás autenticado. Por favor, inicia sesión.');
        setLoading(false);
        return;
      }

      try {
        const res = await axios.get(
          `/api/routines/instances/${user.id}`,
          { headers: { 'x-auth-token': token } }
        );

        const data = Array.isArray(res.data)
          ? res.data
          : Array.isArray(res.data.instances)
            ? res.data.instances
            : [];

        setInstances(data);
      } catch (err) {
        console.error('Error al cargar plantillas:', err);
        setError(err.response?.data?.msg || 'Error al cargar plantillas');
      } finally {
        setLoading(false);
      }
    };

    fetchInstances();
  }, [navigate]);

  const toggleActivity = async (instanceId, activityIdx) => {
    const token = localStorage.getItem('token');
    const inst   = instances.find(i => i._id === instanceId);
    if (!inst) return;

    const completed = inst.completedActivities.includes(activityIdx);

    try {
      await axios.post(
        `/api/routines/instances/${instanceId}/activities/${activityIdx}`,
        { completed: !completed },
        { headers: { 'x-auth-token': token } }
      );

      setInstances(prev =>
        prev.map(i =>
          i._id !== instanceId
            ? i
            : {
                ...i,
                completedActivities: completed
                  ? i.completedActivities.filter(idx => idx !== activityIdx)
                  : [...i.completedActivities, activityIdx]
              }
        )
      );
    } catch (err) {
      console.error('Error marcando actividad:', err);
      setError('No se pudo actualizar el estado de la actividad');
    }
  };

  if (loading) return <p>Cargando tus plantillas…</p>;
  if (error)   return <p className="error-msg">{error}</p>;

  const filtered = instances.filter(inst =>
    filterTag
      ? inst.template.tags?.includes(filterTag)
      : true
  );

  return (
    <div className="routine-templates-page patient-templates-page">
      <div className="tpl-container">
        <div className="tpl-header">
          <h1>Mis Plantillas Asignadas</h1>
          <input
            type="text"
            placeholder="Filtrar por etiqueta"
            value={filterTag}
            onChange={e => setFilterTag(e.target.value)}
            className="small-input tag-filter"
          />
        </div>

        {filtered.length === 0 ? (
          <p>No se encontraron plantillas con esa etiqueta.</p>
        ) : (
          <div className="tpl-grid">
            {filtered.map(inst => (
              <div key={inst._id} className="tpl-card">
                <h2 className="tpl-title">{inst.template.name}</h2>
                <p className="tpl-desc">{inst.template.description}</p>
                <div className="tpl-meta">
                  Asignado el: {new Date(inst.createdAt).toLocaleDateString()}<br/>
                  Duración: {inst.template.duration} min | Tags:{' '}
                  {inst.template.tags?.join(', ')}
                </div>

                <ul className="tpl-activities-list">
                  {inst.template.activities.map((act, idx) => {
                    const isDone = inst.completedActivities.includes(idx);
                    return (
                      <li key={idx} className="activity-item">
                        <label>
                          <input
                            type="checkbox"
                            checked={isDone}
                            onChange={() => toggleActivity(inst._id, idx)}
                          />
                          <span className={isDone ? 'completed' : ''}>
                            {act.name} – Nivel {act.challenge} – {act.minutes} min
                          </span>
                        </label>
                        {act.desc && (
                          <p className="activity-desc">{act.desc}</p>
                        )}
                      </li>
                    );
                  })}
                </ul>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
