// src/pages/PatientTemplatesPage.js
import React, { useState, useEffect } from 'react';
import '../styles/routineTemplates.css';
import { FaCheck, FaChevronDown, FaChevronUp } from 'react-icons/fa';

export default function PatientTemplatesPage() {
  const [templates, setTemplates] = useState([]);
  const [loading, setLoading]     = useState(true);
  const [error, setError]         = useState(null);
  const [expanded, setExpanded]   = useState({});
  const [completed, setCompleted] = useState({});
  const [filter, setFilter]       = useState('');

  const token = localStorage.getItem('token');
  const headers = {
    'Content-Type': 'application/json',
    'x-auth-token': token
  };

  useEffect(() => {
    if (!token) {
      setError('Debes iniciar sesión');
      setLoading(false);
      return;
    }
    fetch('/api/patient/templates', { headers })
      .then(res => {
        if (!res.ok) throw new Error(`Error ${res.status}`);
        return res.json();
      })
      .then(data => {
        setTemplates(data);
        // init completed flags
        const comp = {};
        data.forEach(t => {
          comp[t._id] = Array(t.activities.length).fill(false);
        });
        setCompleted(comp);
      })
      .catch(err => setError(err.message))
      .finally(() => setLoading(false));
  }, [token]);

  const toggleExpand = id => {
    setExpanded(e => ({ ...e, [id]: !e[id] }));
  };

  const toggleActivity = (tplId, idx) => {
    setCompleted(c => {
      const arr = [...c[tplId]];
      arr[idx] = !arr[idx];
      return { ...c, [tplId]: arr };
    });
  };

  const handleSaveProgress = tplId => {
    const done = completed[tplId].map((v,i) => v ? i : null).filter(v=>v!==null);
    fetch(`/api/patient/templates/${tplId}/progress`, {
      method: 'POST',
      headers,
      body: JSON.stringify({ completedIndices: done })
    }).then(res => {
      if (!res.ok) throw new Error(`Error ${res.status}`);
      alert('Progreso guardado');
    }).catch(err => {
      console.error(err);
      alert('No se pudo guardar progreso');
    });
  };

  const handleCompleteAll = tplId => {
    fetch(`/api/patient/templates/${tplId}/complete`, {
      method: 'POST',
      headers
    }).then(res => {
      if (!res.ok) throw new Error(`Error ${res.status}`);
      alert('Plantilla completada');
      // Optionally remove from list:
      setTemplates(ts => ts.filter(t=>t._id!==tplId));
    }).catch(err => {
      console.error(err);
      alert('No se pudo marcar completada');
    });
  };

  if (loading) return <div className="tpl-container">Cargando plantillas…</div>;
  if (error)   return <div className="tpl-container">Error: {error}</div>;

  const filtered = templates.filter(t =>
    t.name.toLowerCase().includes(filter.toLowerCase())
  );

  return (
    <div className="routine-templates-page">
      <div className="tpl-container">
        <div className="tpl-header">
          <h1>Mis Plantillas Asignadas</h1>
          <input
            className="search-input"
            type="text"
            placeholder="🔍 Buscar plantilla..."
            value={filter}
            onChange={e => setFilter(e.target.value)}
            style={{
              padding: '.6rem',
              borderRadius: '4px',
              border: '1px solid #ccc',
              width: '60%',
              maxWidth: '300px'
            }}
          />
        </div>

        {filtered.length === 0 ? (
          <div
            className="empty-state"
            style={{
              border: '2px dashed var(--primary-color)',
              padding: '2rem',
              textAlign: 'center',
              borderRadius: '8px',
              color: 'var(--primary-color)',
              fontSize: '1.2rem',
              marginTop: '2rem'
            }}
          >
            🤔 No hay plantillas que coincidan
          </div>
        ) : (
          <div className="tpl-grid">
            {filtered.map(t => {
              const doneCount = completed[t._id].filter(v=>v).length;
              const total = t.activities.length;
              const pct = total ? Math.round((doneCount/total)*100) : 0;
              return (
                <div key={t._id} className="tpl-card">
                  <div
                    style={{
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'space-between',
                      cursor: 'pointer'
                    }}
                    onClick={()=>toggleExpand(t._id)}
                  >
                    <h2 style={{ margin: 0 }}>{t.name}</h2>
                    {expanded[t._id] ? <FaChevronUp/> : <FaChevronDown/>}
                  </div>

                  <div className="tpl-meta" style={{ margin: '0.5rem 0' }}>
                    {pct}% completado ({doneCount}/{total})
                    <div 
                      style={{
                        height: '6px',
                        background: '#eee',
                        borderRadius: '3px',
                        overflow: 'hidden',
                        marginTop: '4px'
                      }}
                    >
                      <div
                        style={{
                          width: `${pct}%`,
                          height: '100%',
                          background: 'var(--primary-color)'
                        }}
                      />
                    </div>
                  </div>

                  {expanded[t._id] && (
                    <div>
                      <p className="tpl-desc">{t.description}</p>
                      {t.activities.map((act, i) => (
                        <div
                          key={i}
                          className="activity-item"
                          style={{
                            display: 'flex',
                            alignItems: 'center',
                            gap: '0.5rem',
                            marginBottom: '0.5rem'
                          }}
                        >
                          <input
                            type="checkbox"
                            checked={completed[t._id][i]}
                            onChange={()=>toggleActivity(t._id,i)}
                          />
                          <span style={{ flex: 1 }}>{act.name}</span>
                          <span
                            className="challenge-indicator"
                            data-level={act.challenge}
                            style={{ fontSize: '0.8rem' }}
                          >
                            {act.challenge}
                          </span>
                          <small style={{ color: '#666' }}>
                            {act.minutes}min
                          </small>
                        </div>
                      ))}
                      <div style={{ display: 'flex', gap: '1rem', marginTop: '1rem' }}>
                        <button
                          className="btn-save"
                          onClick={()=>handleSaveProgress(t._id)}
                        >
                          💾 Guardar progreso
                        </button>
                        <button
                          className="btn-new"
                          onClick={()=>handleCompleteAll(t._id)}
                        >
                          ✓ Completar plantilla
                        </button>
                      </div>
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
}
