import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/routineTemplates.css';   // reuse existing styles
import { FaCheckCircle, FaRegCircle } from 'react-icons/fa';

export default function PatientTemplatesPage() {
  const navigate = useNavigate();
  const [templates, setTemplates] = useState([]);
  const [loading, setLoading]     = useState(true);
  const [error, setError]         = useState(null);
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!token) return navigate('/');
    fetch('/api/patients/me/templates', {               // Assumes this endpoint returns assigned templates
      headers: { 'Content-Type': 'application/json', 'x-auth-token': token }
    })
      .then(async res => {
        if (res.status === 401) throw new Error('SesiÃ³n expirada');
        if (!res.ok)     throw new Error(`Error ${res.status}`);
        return res.json();
      })
      .then(data => {
        setTemplates(data);
      })
      .catch(err => {
        console.error(err);
        setError(err.message);
      })
      .finally(() => setLoading(false));
  }, [navigate, token]);

  const toggleDone = async (tplId, idx, done) => {
    try {
      // Call your API to record completion. Adjust URL/body as your backend expects.
      await fetch(`/api/patients/me/templates/${tplId}/activities/${idx}`, {
        method: 'POST',
        headers: { 'Content-Type':'application/json', 'x-auth-token': token },
        body: JSON.stringify({ completed: done })
      });
      // Optimistically update UI
      setTemplates(ts =>
        ts.map(t =>
          t._id === tplId
            ? {
                ...t,
                activities: t.activities.map((a,i) =>
                  i===idx ? { ...a, completed: done } : a
                )
              }
            : t
        )
      );
    } catch (err) {
      console.error('Error guardando estado:', err);
      alert('No se pudo actualizar el estado de la actividad');
    }
  };

  if (loading) return <div className="tpl-container">Cargandoâ€¦</div>;
  if (error)   return <div className="tpl-container">Error: {error}</div>;

  return (
    <div className="routine-templates-page">
      <div className="tpl-container">
        <div className="tpl-header">
          <h1>Mis Plantillas Asignadas</h1>
        </div>

        {templates.length === 0 ? (
          <div className="empty-state">
            ðŸ“‚ No tienes plantillas asignadas.
          </div>
        ) : (
          templates.map(t => (
            <div key={t._id} className="tpl-card">
              <h2>{t.name}</h2>
              <p className="tpl-desc">{t.description}</p>
              <ul className="tpl-activities-list">
                {t.activities.map((act, i) => (
                  <li key={i}>
                    <button
                      className="activity-toggle"
                      onClick={() => toggleDone(t._id, i, !act.completed)}
                    >
                      {act.completed
                        ? <FaCheckCircle />
                        : <FaRegCircle />}
                    </button>
                    <span className={act.completed ? 'done' : ''}>
                      {act.name} ({act.minutes} min, nivel {act.challenge})
                    </span>
                  </li>
                ))}
              </ul>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
