// src/pages/RoutineTemplatesPage.js

import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaTrash, FaEdit } from 'react-icons/fa';
import axios from 'axios';
import '../styles/routineTemplates.css';

export default function RoutineTemplatesPage() {
  const [templates, setTemplates] = useState([]);
  const [patients, setPatients]   = useState([]);
  const [loading, setLoading]     = useState(true);
  const [error, setError]         = useState(null);
  const [expandedPatient, setExpandedPatient] = useState(null);
  const [instancesByPatient, setInstancesByPatient] = useState({});
  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  // Fetch inicial de plantillas y pacientes
  useEffect(() => {
    if (!token) {
      navigate('/login');
      setLoading(false);
      return;
    }
    const headers = { 'Content-Type': 'application/json', 'x-auth-token': token };
    Promise.all([
      axios.get('/api/routines/templates', { headers }),
      axios.get('/api/therapist/patients',  { headers })
    ])
      .then(([tplRes, patRes]) => {
        setTemplates(Array.isArray(tplRes.data) ? tplRes.data : []);
        setPatients(Array.isArray(patRes.data) ? patRes.data : []);
      })
      .catch(err => {
        console.error(err);
        if (err.response?.status === 401) {
          alert('Sesión expirada. Vuelve a iniciar sesión.');
          navigate('/login');
        } else {
          setError(err.message);
        }
      })
      .finally(() => setLoading(false));
  }, [navigate, token]);

  // Drag & Drop handlers (igual que antes)
  const onDragStart = (e, item, type) => {
    e.dataTransfer.setData('application/json', JSON.stringify({ type, item }));
    e.dataTransfer.effectAllowed = 'copyMove';
  };
  const allowDrop = e => e.preventDefault();
  const handleDrop = useCallback((e, zone) => {
    e.preventDefault();
    const { type, item } = JSON.parse(e.dataTransfer.getData('application/json'));
    const headers = { 'Content-Type': 'application/json', 'x-auth-token': token };

    if (zone === 'trash' && type === 'template') {
      setTemplates(ts => ts.filter(t => t._id !== item._id));
      axios.delete(`/api/routines/templates/${item._id}`, { headers }).catch(console.error);
    }
    else if (zone === 'duplicate' && type === 'template') {
      const payload = {
        ...item,
        name: item.name + ' (copia)',
        activities: item.activities.map(a => ({
          name: a.name,
          desc: a.desc,
          challenge: a.challenge,
          minutes: a.minutes
        }))
      };
      axios.post('/api/routines/templates', payload, { headers })
        .then(res => setTemplates(ts => [res.data, ...ts]))
        .catch(err => alert('No se pudo duplicar: ' + err.message));
    }
    else if (zone.startsWith('assign-') && type === 'template') {
      const patientId = zone.split('-')[1];
      axios.post(
        '/api/routines/instances',
        { templateId: item._id, patientId },
        { headers }
      )
      .then(() => alert(`Plantilla “${item.name}” asignada a paciente ${patientId}`))
      .catch(err => alert('No se pudo asignar: ' + err.message));
    }
  }, [token]);

  // Toggle expand para ver detalle de un paciente
  const toggleExpand = useCallback(async patientId => {
    setExpandedPatient(prev => prev === patientId ? null : patientId);
    if (!instancesByPatient[patientId]) {
      try {
        const { data } = await axios.get(
          `/api/routines/instances/${patientId}`,
          { headers: { 'x-auth-token': token } }
        );
        setInstancesByPatient(prev => ({
          ...prev,
          [patientId]: Array.isArray(data) ? data : data.instances || []
        }));
      } catch (err) {
        console.error('Error cargando detalle de paciente:', err);
      }
    }
  }, [instancesByPatient, token]);

  if (loading) return <div className="tpl-container">Cargando datos…</div>;
  if (error)   return <div className="tpl-container">Error: {error}</div>;

  return (
    <div className="routine-templates-page">
      <div className="tpl-container">
        {/* Header y grid de plantillas */}
        <div className="tpl-header">
          <h1>Mis Plantillas</h1>
          <button className="btn-new" onClick={() => navigate('/therapist/templates/new')}>
            📄 Nueva plantilla
          </button>
        </div>

        {templates.length === 0 ? (
          <div className="empty-state">
            🤔 Aún no se han creado plantillas
          </div>
        ) : (
          <div className="tpl-grid">
            {templates.map(t => (
              <div
                key={t._id}
                className="tpl-card"
                draggable
                onDragStart={e => onDragStart(e, t, 'template')}
              >
                <FaEdit
                  className="icon-edit"
                  onClick={() => navigate(`/therapist/templates/${t._id}/edit`)}
                />
                <h2>{t.name}</h2>
                <p className="tpl-desc">{t.description}</p>
                <div className="tpl-meta">{t.activities.length} actividades</div>
              </div>
            ))}
          </div>
        )}

        {/* Papelera y duplicado */}
        <div className="dropzones">
          <div className="dropzone trash" onDragOver={allowDrop} onDrop={e => handleDrop(e, 'trash')}>
            🗑️ Arrastra aquí para eliminar
          </div>
          <div className="dropzone duplicate" onDragOver={allowDrop} onDrop={e => handleDrop(e, 'duplicate')}>
            📋 Arrastra aquí Ctrl+drag para duplicar
          </div>
        </div>

        {/* Lista de pacientes */}
        <h3 style={{ marginTop: '2rem' }}>Pacientes</h3>
        <div className="tpl-grid">
          {patients.map(p => (
            <div
              key={p._id}
              className="patient-card"
              draggable
              onDragOver={allowDrop}
              onDrop={e => handleDrop(e, `assign-${p._id}`)}
              onClick={() => toggleExpand(p._id)}
            >
              <img src={p.avatar} alt={p.nombre} className="avatar-small" />
              <div className="patient-info">
                <div className="patient-name">{p.nombre} {p.apellidos}</div>
                <div className="patient-email">{p.email}</div>
              </div>

              {/* Detalle expandible */}
              {expandedPatient === p._id && (
                <PatientDetail instances={instancesByPatient[p._id] || []} />
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

// Componente internal para mostrar el detalle de un paciente
function PatientDetail({ instances }) {
  if (!instances.length) {
    return <p className="tpl-desc">Este paciente aún no tiene plantillas asignadas.</p>;
  }
  return (
    <div className="patient-detail">
      {instances.map(inst => {
        const tpl   = inst.template;
        const total = tpl.activities.length;
        const done  = inst.completedActivities.length;
        const pct   = Math.round((done / total) * 100);

        return (
          <div key={inst._id} className="detail-card">
            <div className="detail-header">
              <h4>{tpl.name}</h4>
              <span className={`challenge-indicator level-${tpl.challengeLevel}`}>
                Reto {tpl.challengeLevel}
              </span>
            </div>

            <div className="tpl-meta">
              Asignado: {new Date(inst.createdAt).toLocaleDateString()} | Última: {new Date(inst.updatedAt||inst.createdAt).toLocaleString()}
            </div>

            <div className="progress-bar small">
              <div className="progress-filled" style={{ width: `${pct}%` }} />
            </div>
            <p className="progress-text small">{done}/{total} actividades ({pct}%)</p>

            <div className="tags-container">
              {tpl.tags.map(tag => (
                <span key={tag} className="tag-badge">{tag}</span>
              ))}
            </div>

            <ul className="tpl-activities-list">
              {tpl.activities.map((act, i) => {
                const completed = inst.completedActivities.includes(i);
                const note      = inst.notes?.[i];
                return (
                  <li key={i} className={`activity-item ${completed ? 'status-completed' : ''}`}>
                    <div className="activity-main">
                      <span className="activity-name">{act.name}</span>
                      <span className="activity-info">{act.minutes} min · Reto {act.challenge}</span>
                      <span className="activity-status">{completed ? '✔️' : '◻️'}</span>
                    </div>
                    {act.desc && <p className="activity-desc">{act.desc}</p>}
                    {note && (
                      <div className="activity-note">
                        <strong>Nota:</strong> {note}
                      </div>
                    )}
                  </li>
                );
              })}
            </ul>
          </div>
        );
      })}
    </div>
  );
}
