import React, { useState, useEffect } from 'react';
import { useNavigate }          from 'react-router-dom';
import Calendar                 from 'react-calendar';
import axios                    from 'axios';
import '../styles/therapistDashboard.css';

function TherapistDashboardHome() {
  const navigate       = useNavigate();
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [showInviteModal, setShowInviteModal] = useState(false);
  const [inviteEmail, setInviteEmail]   = useState('');
  const [inviteMsg, setInviteMsg]       = useState('');

  // Estados para modal de perfil
  const [showProfileModal, setShowProfileModal] = useState(false);
  const [currentAvatar, setCurrentAvatar]       = useState(null);
  const [avatarFile, setAvatarFile]             = useState(null);
  const [avatarPreview, setAvatarPreview]       = useState(null);

  useEffect(() => {
    // Traer avatar actual al montar
    const token = localStorage.getItem('token');
    if (token) {
      axios
        .get('/api/users/me', { headers: { 'x-auth-token': token } })
        .then(res => setCurrentAvatar(res.data.avatarUrl))
        .catch(() => {});
    }
  }, []);

  const handleAvatarChange = e => {
    const file = e.target.files[0];
    if (file) {
      setAvatarFile(file);
      setAvatarPreview(URL.createObjectURL(file));
    }
  };

  const handleSaveAvatar = async () => {
    if (!avatarFile) return;
    const token = localStorage.getItem('token');
    const form  = new FormData();
    form.append('avatar', avatarFile);
    try {
      const { data } = await axios.post(
        '/api/users/avatar',
        form,
        {
          headers: {
            'x-auth-token': token,
            'Content-Type': 'multipart/form-data'
          }
        }
      );
      setCurrentAvatar(data.avatarUrl);
      setShowProfileModal(false);
      setAvatarFile(null);
      setAvatarPreview(null);
    } catch {
      alert('Error subiendo la nueva foto.');
    }
  };

  const handleInvitationSubmit = async () => {
    const emailTrimmed = inviteEmail.trim();
    if (!emailTrimmed) {
      setInviteMsg('Por favor, ingresa un correo electrónico válido.');
      return;
    }
    try {
      const token = localStorage.getItem('token');
      await axios.post(
        '/api/invitations',
        { invitedEmail: emailTrimmed },
        {
          headers: {
            'Content-Type': 'application/json',
            'x-auth-token': token
          }
        }
      );
      setInviteMsg('Invitación enviada con éxito.');
      setShowInviteModal(false);
      setInviteEmail('');
    } catch (err) {
      console.error(err);
      setInviteMsg(err.response?.data?.msg || 'Error al enviar la invitación.');
    }
  };

  const handleLogout = () => navigate('/');

  return (
    <div className="home-container">
      {/* Cabecera */}
      <header className="top-bar">
        <div className="left-section">
          <i
            className="fa fa-user-circle user-icon"
            aria-hidden="true"
            onClick={() => setShowProfileModal(true)}
          />
        </div>
        <div className="center-section">
          <h1 className="title">Dashboard Terapeuta</h1>
        </div>
        <div className="right-section">
          <i className="fa fa-bell bell-icon" aria-hidden="true" />
          <button className="logout-btn" onClick={handleLogout}>
            <i className="fa fa-sign-out" aria-hidden="true" />
            <span>Salir</span>
          </button>
        </div>
      </header>

      {/* Modal de cambiar avatar */}
      {showProfileModal && (
        <div className="profile-modal-overlay">
          <div className="profile-modal-content">
            <h2>Actualizar Foto de Perfil</h2>
            <img
              src={
                avatarPreview ||
                currentAvatar ||
                '/default-profile.png'
              }
              alt="Avatar"
              className="avatar-preview"
            />
            <input
              type="file"
              accept="image/*"
              className="file-input"
              onChange={handleAvatarChange}
            />
            <div className="modal-buttons">
              <button
                className="profile-btn"
                onClick={handleSaveAvatar}
                disabled={!avatarFile}
              >
                Guardar
              </button>
              <button
                className="cancel-btn"
                onClick={() => setShowProfileModal(false)}
              >
                Cancelar
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Modal de invitación */}
      {showInviteModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Generar Invitación</h2>
            <p>Introduce el correo electrónico del paciente:</p>
            <input
              type="email"
              value={inviteEmail}
              onChange={e => setInviteEmail(e.target.value)}
              placeholder="paciente@ejemplo.com"
            />
            <div className="modal-buttons">
              <button onClick={handleInvitationSubmit}>
                Enviar Invitación
              </button>
              <button onClick={() => setShowInviteModal(false)}>
                Cancelar
              </button>
            </div>
            {inviteMsg && <p className="invite-msg">{inviteMsg}</p>}
          </div>
        </div>
      )}


      {/* Contenido principal */}
      <main className="main-content">
        <section className="intro-section">
          <h2>¡Bienvenido, Terapeuta!</h2>
          <p>Accede a tus herramientas y consulta la información.</p>
        </section>
        <div className="buttons-and-calendar">
          <div className="buttons-block">
            <div className="button-group">
              <h3>GESTIÓN</h3>
              <button className="option-btn" onClick={() => navigate('/therapist/patients')}>
                <i className="fa fa-users icon" /> Lista de Pacientes
              </button>
              <button className="option-btn" onClick={() => navigate('/messaging')}>
                <i className="fa fa-comments icon" /> Mensajería
              </button>
              <button className="option-btn" onClick={() => setShowInviteModal(true)}>
                <i className="fa fa-envelope icon" /> Invitación
              </button>
              <button className="option-btn" onClick={() => navigate('/therapist/templates')}>
                <i className="fa fa-file-text icon" /> Plantillas
              </button>
              <button className="option-btn" onClick={() => navigate('/therapist/session-notes')}>
                <i className="fa fa-sticky-note-o icon" /> Notas de Sesión
              </button>
            </div>
            <div className="button-group">
              <h3>ANÁLISIS</h3>
              <button className="option-btn" onClick={() => navigate('/therapist/routines')}>
                <i className="fa fa-clock-o icon" /> Rutinas
              </button>
              <button className="option-btn" onClick={() => navigate('/therapist/interests')}>
                <i className="fa fa-line-chart icon" /> Intereses
              </button>
              <button className="option-btn" onClick={() => navigate('/therapist/satisfaction')}>
                <i className="fa fa-smile-o icon" /> Satisfacción
              </button>
            </div>
          </div>
          <div className="calendar-block">
            <Calendar onChange={setSelectedDate} value={selectedDate} />
          </div>
        </div>
      </main>

      {/* Footer */}
      <footer className="footer-bar">
        <p>2025 © Iván Vela Campos</p>
      </footer>

      {/* Modal: generar invitación */}
      {showInviteModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Generar Invitación</h2>
            <input
              type="email"
              value={inviteEmail}
              onChange={e => setInviteEmail(e.target.value)}
              placeholder="correo@ejemplo.com"
            />
            <div className="modal-buttons">
              <button onClick={handleInvitationSubmit}>Enviar</button>
              <button onClick={() => setShowInviteModal(false)}>Cancelar</button>
            </div>
            {inviteMsg && <p className="invite-msg">{inviteMsg}</p>}
          </div>
        </div>
      )}
    </div>
  );
}

export default TherapistDashboardHome;
