import React, { useEffect, useState } from 'react';
import authService from '../services/authService';
import './patientTemplates.css';
import '../styles/routineTemplates.css';

function PatientTemplates() {
  const [templates, setTemplates] = useState([]);
  const [filterTag, setFilterTag] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAssigned = async () => {
      // 1) Recuperamos el usuario de forma segura
      const user = authService.getCurrentUser();
      if (!user) {
        setError('No est√°s autenticado. Por favor, inicia sesi√≥n.');
        setLoading(false);
        return;
      }

      // 2) Hacemos fetch de las plantillas asignadas
      try {
        const res = await fetch(`/api/routineInstances?patientId=${user.id}`);
        if (!res.ok) throw new Error(`Error ${res.status}: ${res.statusText}`);
        const data = await res.json();
        setTemplates(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchAssigned();
  }, []);

  const toggleCompleted = async (instId, actId) => {
    try {
      await fetch(`/api/routineInstances/${instId}/activities/${actId}/toggle`, {
        method: 'PATCH'
      });
      setTemplates(ts =>
        ts.map(t =>
          t.id !== instId
            ? t
            : {
                ...t,
                activities: t.activities.map(a =>
                  a.id !== actId ? a : { ...a, completed: !a.completed }
                )
              }
        )
      );
    } catch (e) {
      console.error(e);
      alert('No se pudo cambiar el estado.');
    }
  };

  const handleDurationChange = (instId, actId, val) => {
    setTemplates(ts =>
      ts.map(t =>
        t.id !== instId
          ? t
          : {
              ...t,
              activities: t.activities.map(a =>
                a.id !== actId ? a : { ...a, duration: val }
              )
            }
      )
    );
  };

  const saveChanges = async instId => {
    const tpl = templates.find(t => t.id === instId);
    try {
      const res = await fetch(`/api/routineInstances/${instId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ activities: tpl.activities })
      });
      if (!res.ok) throw new Error('Error guardando');
      alert('Cambios guardados üëç');
    } catch (err) {
      console.error(err);
      alert('Error guardando cambios');
    }
  };

  const filtered = templates.filter(t =>
    filterTag ? t.tags?.includes(filterTag) : true
  );

  return (
    <div className="routine-templates-page patient-templates-page">
      <div className="tpl-container">
        <div className="tpl-header">
          <h1>Mis Plantillas Asignadas</h1>
          <input
            type="text"
            placeholder="Filtrar por etiqueta"
            value={filterTag}
            onChange={e => setFilterTag(e.target.value)}
            className="small-input tag-filter"
          />
        </div>

        {loading && <p>Cargando plantillas‚Ä¶</p>}
        {error && <p className="error-msg">{error}</p>}

        {!loading && !error && (
          <div className="tpl-grid">
            {filtered.length === 0 && (
              <p>No se encontraron plantillas con esa etiqueta.</p>
            )}
            {filtered.map(tpl => (
              <div key={tpl.id} className="tpl-card">
                <h2>{tpl.title}</h2>
                <p className="tpl-desc">{tpl.description}</p>
                <p className="tpl-meta">
                  Asignado el: {new Date(tpl.assignedAt).toLocaleDateString()}
                </p>
                <div className="challenge-indicator" data-level={tpl.challengeLevel}>
                  Nivel {tpl.challengeLevel}
                </div>

                <ul className="tpl-activities-list">
                  {tpl.activities.map(act => (
                    <li key={act.id}>
                      <div
                        className={`activity-item ${
                          act.completed ? 'status-completed' : ''
                        }`}
                      >
                        <span className="drag-handle">‚â°</span>
                        <div className="activity-content">
                          <h3>{act.title}</h3>
                          <p>{act.description}</p>
                          <div className="form-grid">
                            <input
                              type="text"
                              value={act.duration}
                              onChange={e =>
                                handleDurationChange(tpl.id, act.id, e.target.value)
                              }
                              className="small-input"
                            />
                            <select
                              value={act.category}
                              onChange={e => {
                                /* opcional: editar categor√≠a */
                              }}
                              className="small-select"
                            >
                              <option>Ejercicio</option>
                              <option>Meditaci√≥n</option>
                              <option>Lectura</option>
                              <option>Otro</option>
                            </select>
                          </div>
                        </div>
                        <button
                          className="activity-toggle"
                          onClick={() => toggleCompleted(tpl.id, act.id)}
                        >
                          {act.completed ? '‚úîÔ∏è' : '‚óªÔ∏è'}
                        </button>
                      </div>
                    </li>
                  ))}
                </ul>

                <button className="btn-save" onClick={() => saveChanges(tpl.id)}>
                  Guardar Cambios
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default PatientTemplates;
