import axios from 'axios';
const API_URL = '/api/routine-templates';

const routineTemplateService = {
  createTemplate: async data => {
    const token = localStorage.getItem('token');
    const res = await axios.post(API_URL, data, { headers: { 'x-auth-token': token } });
    return res.data;
  },
  getTemplates: async () => {
    const token = localStorage.getItem('token');
    const res = await axios.get(API_URL, { headers: { 'x-auth-token': token } });
    return res.data;
  },
  assignTemplate: async (templateId, patientId) => {
    const token = localStorage.getItem('token');
    const res = await axios.post(
      `${API_URL}/${templateId}/assign`,
      { patientId },
      { headers: { 'x-auth-token': token } }
    );
    return res.data;
  },
  getAssignments: async patientId => {
    const token = localStorage.getItem('token');
    const params = patientId ? { patientId } : {};
    const res = await axios.get(`${API_URL}/assignments`, {
      params,
      headers: { 'x-auth-token': token }
    });
    return res.data;
  },
  updateAssignment: async (id, data) => {
    const token = localStorage.getItem('token');
    const res = await axios.put(`${API_URL}/assignments/${id}`, data, {
      headers: { 'x-auth-token': token }
    });
    return res.data;
  }
};

export default routineTemplateService;
