import React, { useState, useEffect } from 'react';
import {
  FaChevronDown,
  FaChevronUp,
  FaCheckCircle,
  FaRegCircle
} from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';
import '../styles/routineTemplates.css';

export default function PatientTemplatesPage() {
  const [instances, setInstances] = useState([]);
  const [expanded, setExpanded]   = useState({});
  const [loading, setLoading]     = useState(true);
  const [error, setError]         = useState(null);
  const navigate = useNavigate();

  // === Cambiado ===
  // Recupera el objeto completo "user" y extrae token e id:
  const userStr = localStorage.getItem('user');
  let token, patientId;
  try {
    const user = userStr ? JSON.parse(userStr) : null;
    token     = user?.token;
    patientId = user?.id || user?._id;
  } catch {
    token = null;
    patientId = null;
  }

  useEffect(() => {
    if (!token || !patientId) {
      console.warn('No hay token o patientId');  // ahora verás esto solo si realmente falta
      navigate('/login');
      return;
    }

    const headers = {
      'Content-Type': 'application/json',
      'x-auth-token': token
    };

    fetch(`/api/routines/instances/${patientId}`, { headers })
      .then(res => {
        if (res.status === 401) {
          alert('Sesión expirada. Vuelve a iniciar sesión.');
          navigate('/login');
          return null;
        }
        if (!res.ok) {
          throw new Error(`Error al cargar: ${res.status}`);
        }
        return res.json();
      })
      .then(data => {
        if (Array.isArray(data)) {
          setInstances(data);
        } else {
          throw new Error('Respuesta inesperada del servidor');
        }
      })
      .catch(err => {
        console.error(err);
        setError(err.message);
      })
      .finally(() => setLoading(false));
  }, [navigate, token, patientId]);

  const toggleExpand = id => {
    setExpanded(e => ({ ...e, [id]: !e[id] }));
  };

  const markActivity = (instId, idx) => {
    const headers = {
      'Content-Type': 'application/json',
      'x-auth-token': token
    };
    fetch(`/api/routines/instances/${instId}/activities/${idx}`, {
      method: 'POST',
      headers
    })
      .then(res => {
        if (!res.ok) throw new Error('No se pudo marcar actividad');
        setInstances(list =>
          list.map(i =>
            i._id === instId
              ? {
                  ...i,
                  activities: i.activities.map((a, j) =>
                    j === idx ? { ...a, completed: !a.completed } : a
                  )
                }
              : i
          )
        );
      })
      .catch(console.error);
  };

  const completeAll = inst => {
    inst.activities.forEach((_, idx) => {
      if (!inst.activities[idx].completed) {
        markActivity(inst._id, idx);
      }
    });
  };

  const postpone = instId => {
    setInstances(list =>
      list.map(i =>
        i._id === instId ? { ...i, status: 'postponed' } : i
      )
    );
  };

  if (loading) {
    return (
      <div className="routine-templates-page tpl-container">
        <h1>Mis Plantillas Asignadas</h1>
        <p>Cargando plantillas…</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="routine-templates-page tpl-container">
        <h1>Mis Plantillas Asignadas</h1>
        <p style={{ color: 'red' }}>Error: {error}</p>
      </div>
    );
  }

  return (
    <div className="routine-templates-page">
      <div className="tpl-container">
        <h1>Mis Plantillas Asignadas</h1>

        {instances.length === 0 ? (
          <div
            className="empty-state"
            style={{
              border: '2px dashed var(--primary-color)',
              padding: '2rem',
              textAlign: 'center',
              borderRadius: '8px',
              color: 'var(--primary-color)',
              fontSize: '1.2rem',
              marginTop: '2rem'
            }}
          >
            🤔 Aún no tienes plantillas asignadas
          </div>
        ) : (
          <div className="tpl-grid">
            {instances.map(inst => {
              const total = inst.activities.length;
              const done = inst.activities.filter(a => a.completed).length;
              const pct = total ? (done / total) * 100 : 0;

              return (
                <div
                  key={inst._id}
                  className={`tpl-card ${
                    inst.status === 'completed'
                      ? 'status-completed'
                      : inst.status === 'postponed'
                      ? 'status-postponed'
                      : ''
                  }`}
                >
                  {/* Header: título y toggle */}
                  <div className="assign-header">
                    <div>
                      <h2>{inst.template.name}</h2>
                      <p className="tpl-desc">
                        {inst.template.description}
                      </p>
                    </div>
                    <button
                      className="btn-icon"
                      onClick={() => toggleExpand(inst._id)}
                    >
                      {expanded[inst._id] ? (
                        <FaChevronUp />
                      ) : (
                        <FaChevronDown />
                      )}
                    </button>
                  </div>

                  {/* Progreso */}
                  <div className="progress-container">
                    <label>
                      {done}/{total} completadas
                    </label>
                    <progress value={pct} max="100" />
                  </div>

                  {/* Acciones globales */}
                  <div className="assign-actions">
                    {inst.status === 'pending' && (
                      <>
                        <button
                          className="btn-complete"
                          onClick={() => completeAll(inst)}
                        >
                          Completar todo
                        </button>
                        <button
                          className="btn-postpone"
                          onClick={() => postpone(inst._id)}
                        >
                          Posponer
                        </button>
                      </>
                    )}
                    {inst.status === 'completed' && (
                      <span>✔️ Completado</span>
                    )}
                    {inst.status === 'postponed' && (
                      <span>⏳ Pospuesto</span>
                    )}
                  </div>

                  {/* Detalle de actividades */}
                  {expanded[inst._id] && (
                    <div className="activities-list">
                      {inst.activities.map((act, idx) => (
                        <div
                          key={idx}
                          className={`activity-line ${
                            act.completed ? 'completed' : ''
                          }`}
                        >
                          <div>
                            {act.completed ? (
                              <FaCheckCircle />
                            ) : (
                              <FaRegCircle />
                            )}
                            <span style={{ marginLeft: '0.5rem' }}>
                              {act.name}
                            </span>
                          </div>
                          {!act.completed && (
                            <button
                              className="btn-complete"
                              onClick={() =>
                                markActivity(inst._id, idx)
                              }
                            >
                              Marcar
                            </button>
                          )}
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
}
