// src/pages/PatientTemplatesPage.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/routineTemplates.css';
import { FaCheckCircle, FaRegCircle, FaSyncAlt } from 'react-icons/fa';

export default function PatientTemplatesPage() {
  const navigate = useNavigate();
  const [instances, setInstances] = useState([]);
  const [loading, setLoading]     = useState(true);
  const [error, setError]         = useState(null);

  const token     = localStorage.getItem('token');
  const patientId = localStorage.getItem('userId'); // guardado al loguear
  const headers   = { 'Content-Type':'application/json', 'x-auth-token':token };

  const fetchInstances = async () => {
    setLoading(true);
    try {
      const res = await fetch(`/api/routines/instances/${patientId}`, { headers });
      if (res.status === 401) {
        alert('SesiÃ³n expirada');
        navigate('/login');
        return;
      }
      if (!res.ok) throw new Error(`Error ${res.status}`);
      const data = await res.json();
      if (!Array.isArray(data)) throw new Error('Respuesta invÃ¡lida');
      setInstances(data);
    } catch (err) {
      console.error(err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { fetchInstances() }, [patientId]);

  const markActivity = async (instanceId, idx) => {
    try {
      const res = await fetch(
        `/api/routines/instances/${instanceId}/activities/${idx}`,
        { method:'POST', headers }
      );
      if (res.status===401) { alert('SesiÃ³n expirada'); navigate('/login'); return; }
      if (!res.ok) throw new Error(`Error ${res.status}`);
      // actualizar local
      setInstances(insts => insts.map(inst => {
        if (inst._id !== instanceId) return inst;
        const updated = { ...inst };
        updated.activities[idx].completed = true;
        return updated;
      }));
    } catch (err) {
      console.error(err);
      alert('No se pudo marcar actividad');
    }
  };

  if (loading) return <div className="tpl-container">Cargando tus plantillasâ€¦</div>;
  if (error)   return <div className="tpl-container">Error: {error}</div>;

  return (
    <div className="routine-templates-page">
      <div className="tpl-container">
        <div className="tpl-header">
          <h1>Mis Plantillas</h1>
          <button className="btn-new" onClick={fetchInstances}>
            <FaSyncAlt style={{marginRight:'0.5rem'}}/> Refrescar
          </button>
        </div>

        {instances.length === 0 ? (
          <div className="empty-state">
            ðŸ˜” No tienes plantillas asignadas
          </div>
        ) : (
          <div className="tpl-grid">
            {instances.map(inst => {
              const total = inst.activities.length;
              const done  = inst.activities.filter(a => a.completed).length;
              return (
                <div key={inst._id} className="tpl-card">
                  <div style={{display:'flex', justifyContent:'space-between', alignItems:'center'}}>
                    <h2 style={{margin:0}}>{inst.template.name}</h2>
                    <span className="tpl-meta">
                      {done}/{total} completas
                    </span>
                  </div>
                  <p className="tpl-desc">{inst.template.description}</p>

                  <ul style={{listStyle:'none', padding:0, margin:'1rem 0'}}>
                    {inst.activities.map((act, idx) => (
                      <li key={idx} style={{
                        display:'flex', alignItems:'center', marginBottom:'0.75rem'
                      }}>
                        <span
                          style={{cursor: act.completed ? 'default' : 'pointer'}}
                          onClick={() => !act.completed && markActivity(inst._id, idx)}
                        >
                          {act.completed
                            ? <FaCheckCircle color="var(--secondary-color)" />
                            : <FaRegCircle color="#ccc" />}
                        </span>
                        <div style={{marginLeft:'0.5rem'}}>
                          <div style={{fontWeight:600}}>{act.name}</div>
                          <div style={{fontSize:'0.85rem', color:'#666'}}>
                            {act.desc} â€” {act.minutes} min â€” Reto: {act.challenge}
                          </div>
                        </div>
                      </li>
                    ))}
                  </ul>

                  <button
                    className="btn-new"
                    style={{width:'100%', marginTop:'auto'}}
                    onClick={() => alert('Â¡Felicidades! Has completado tu plantilla.')}
                    disabled={done < total}
                  >
                    {done === total ? 'Marcar como completada ðŸŽ‰' : 'Completa todas las actividades'}
                  </button>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
}
