import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import '../styles/timeMachine.css';

const initialActivities = [
  { id: 'activity-1', name: 'Jugar con LEGO', icon: 'fa fa-cubes', defaultDuration: 60 },
  { id: 'activity-2', name: 'Ver dibujos animados', icon: 'fa fa-tv', defaultDuration: 60 },
  { id: 'activity-3', name: 'Comer helado 🍦', icon: 'fa fa-ice-cream', defaultDuration: 30 },
  { id: 'activity-4', name: 'Bailar', icon: 'fa fa-music', defaultDuration: 60 },
  { id: 'activity-5', name: 'Jugar videojuegos', icon: 'fa fa-gamepad', defaultDuration: 60 },
  { id: 'activity-6', name: 'Leer cómics', icon: 'fa fa-book', defaultDuration: 45 },
  { id: 'activity-7', name: 'Ir al parque', icon: 'fa fa-tree', defaultDuration: 90 },
  { id: 'activity-8', name: 'Hacer manualidades', icon: 'fa fa-paint-brush', defaultDuration: 60 },
  { id: 'activity-9', name: 'Montar bicicleta', icon: 'fa fa-bicycle', defaultDuration: 60 },
  { id: 'activity-10', name: 'Jugar al escondite', icon: 'fa fa-child', defaultDuration: 30 },
  { id: 'activity-11', name: 'Dormir una siesta', icon: 'fa fa-bed', defaultDuration: 90 },
  { id: 'activity-12', name: 'Comer merienda', icon: 'fa fa-cutlery', defaultDuration: 30 },
  { id: 'activity-13', name: 'Hacer tareas', icon: 'fa fa-pencil', defaultDuration: 60 },
];

const hours = Array.from({ length: 24 }, (_, i) => {
  const hourStr = String(i).padStart(2, '0') + ':00';
  return { id: hourStr, time: hourStr, activities: [] };
});

function TimeMachine() {
  const [selectedDay, setSelectedDay] = useState('Miércoles');
  const [palette, setPalette] = useState(initialActivities);
  const [schedule, setSchedule] = useState({
    'Miércoles': JSON.parse(JSON.stringify(hours)),
    'Sábado': JSON.parse(JSON.stringify(hours)),
  });

  // Alterna entre Miércoles y Sábado
  const handleDayToggle = (day) => {
    setSelectedDay(day);
  };

  // Maneja el evento onDragEnd para arrastrar desde la paleta al timeline
  const onDragEnd = (result) => {
    const { source, destination, draggableId } = result;
    if (!destination) return;

    // Arrastre desde la paleta a un slot del timeline
    if (source.droppableId === 'palette' && destination.droppableId.startsWith('time-')) {
      const hourId = destination.droppableId.replace('time-', '');
      const activity = palette.find(act => act.id === draggableId);
      if (!activity) return;

      const scheduledActivity = {
        ...activity,
        scheduledId: draggableId + '-' + Date.now(),
        startTime: hourId,
        duration: activity.defaultDuration, // duración en minutos
      };

      setSchedule(prevSchedule => {
        const daySchedule = prevSchedule[selectedDay].map(slot => {
          if (slot.id === hourId) {
            return { ...slot, activities: [...slot.activities, scheduledActivity] };
          }
          return slot;
        });
        return { ...prevSchedule, [selectedDay]: daySchedule };
      });
    }

    // Reordenar actividades dentro del mismo slot
    if (source.droppableId === destination.droppableId && destination.droppableId.startsWith('time-')) {
      const hourId = destination.droppableId.replace('time-', '');
      setSchedule(prevSchedule => {
        const daySchedule = prevSchedule[selectedDay].map(slot => {
          if (slot.id === hourId) {
            const newActivities = Array.from(slot.activities);
            const [removed] = newActivities.splice(source.index, 1);
            newActivities.splice(destination.index, 0, removed);
            return { ...slot, activities: newActivities };
          }
          return slot;
        });
        return { ...prevSchedule, [selectedDay]: daySchedule };
      });
    }
  };

  // Ajusta la duración de una actividad mediante el slider
  const handleDurationChange = (hourId, scheduledId, newDuration) => {
    setSchedule(prevSchedule => {
      const daySchedule = prevSchedule[selectedDay].map(slot => {
        if (slot.id === hourId) {
          const newActivities = slot.activities.map(act => {
            if (act.scheduledId === scheduledId) {
              return { ...act, duration: newDuration };
            }
            return act;
          });
          return { ...slot, activities: newActivities };
        }
        return slot;
      });
      return { ...prevSchedule, [selectedDay]: daySchedule };
    });
  };

  return (
    <div className="time-machine-container">
      <header className="time-machine-header">
        <h1>La máquina del tiempo</h1>
        <div className="day-toggle">
          <button 
            className={selectedDay === 'Miércoles' ? 'active' : ''}
            onClick={() => handleDayToggle('Miércoles')}
          >
            Miércoles
          </button>
          <button 
            className={selectedDay === 'Sábado' ? 'active' : ''}
            onClick={() => handleDayToggle('Sábado')}
          >
            Sábado
          </button>
        </div>
      </header>

      <div className="instructions-box">
        <p>
          ¡Esta es tu máquina del tiempo! Arrastra las actividades que normalmente haces un {selectedDay}. 
          Ajusta el tiempo deslizando la barra para cada actividad.
        </p>
      </div>

      <div className="time-machine-content">
        <DragDropContext onDragEnd={onDragEnd}>
          <div className="palette">
            <h2>Actividades</h2>
            <Droppable droppableId="palette" isDropDisabled={true}>
              {(provided) => (
                <div 
                  className="palette-list" 
                  ref={provided.innerRef} 
                  {...provided.droppableProps}
                >
                  {palette.map((activity, index) => (
                    <Draggable key={activity.id} draggableId={activity.id} index={index}>
                      {(providedDrag) => (
                        <div 
                          className="activity-card"
                          ref={providedDrag.innerRef}
                          {...providedDrag.draggableProps}
                          {...providedDrag.dragHandleProps}
                        >
                          <i className={activity.icon}></i>
                          <span>{activity.name}</span>
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          </div>

          <div className="timeline-board">
            <h2>Tu Día: {selectedDay}</h2>
            <div className="timeline-columns">
              {schedule[selectedDay].map((slot) => (
                <Droppable droppableId={`time-${slot.id}`} key={slot.id}>
                  {(provided) => (
                    <div 
                      className="time-slot"
                      ref={provided.innerRef}
                      {...provided.droppableProps}
                    >
                      <div className="slot-header">
                        <span>{slot.time}</span>
                      </div>
                      <div className="slot-activities">
                        {slot.activities.map((act, index) => (
                          <Draggable key={act.scheduledId} draggableId={act.scheduledId} index={index}>
                            {(providedDrag) => (
                              <div 
                                className="scheduled-activity"
                                ref={providedDrag.innerRef}
                                {...providedDrag.draggableProps}
                                {...providedDrag.dragHandleProps}
                              >
                                <div className="activity-info">
                                  <i className={act.icon}></i>
                                  <span>{act.name}</span>
                                </div>
                                <div className="duration-adjust">
                                  <input 
                                    type="range" 
                                    min="30" 
                                    max="180" 
                                    step="15" 
                                    value={act.duration} 
                                    onChange={(e) => handleDurationChange(slot.id, act.scheduledId, e.target.value)}
                                  />
                                  <span>{act.duration} min</span>
                                </div>
                              </div>
                            )}
                          </Draggable>
                        ))}
                        {provided.placeholder}
                      </div>
                    </div>
                  )}
                </Droppable>
              ))}
            </div>
          </div>
        </DragDropContext>
      </div>
    </div>
  );
}

export default TimeMachine;
