import axios from 'axios';
const API = '/api/routines';

const routineTemplateService = {
  getTemplates:   ( ) => axios.get(`${API}/templates`, { headers: { 'x-auth-token': localStorage.getItem('token') } }),
  createTemplate:(data) => axios.post(`${API}/templates`,  data, { headers: { 'x-auth-token': localStorage.getItem('token') } }),
  assignTemplate:(data) => axios.post(`${API}/assign`,     data, { headers: { 'x-auth-token': localStorage.getItem('token') } }),
  getAssignments:( ) => axios.get(`${API}/assignments`,   { headers: { 'x-auth-token': localStorage.getItem('token') } }),
  updateAssign: (id, data) => axios.put(`${API}/assign/${id}`, data, { headers: { 'x-auth-token': localStorage.getItem('token') } }),
};

export default routineTemplateService;
