// src/components/PatientInterests.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  ResponsiveContainer,
  PieChart, Pie, Cell, Tooltip, Legend, Label,
  BarChart, Bar, XAxis, YAxis, CartesianGrid,
  LineChart, Line
} from 'recharts';
import '../styles/patientInterests.css';

const CATEGORY_COLORS = {
  ocio: '#8884d8',
  'cuidado personal': '#82ca9d',
  'obligaciones': '#ffc658',
  escuela: '#ff8042',
  'participación comunitaria': '#0088FE',
  'descanso y sueño': '#a4de6c'
};

export default function PatientInterests() {
  const navigate = useNavigate();
  const [data, setData] = useState([]);
  const [stats, setStats] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    axios
      .get('/api/interests?patientId=' + /* extra query if needed */ {
        headers: { 'x-auth-token': token },
      })
      .then(res => {
        setData(res.data); // Assume res.data is array of { category, score, timestamp }
        computeStats(res.data);
      })
      .catch(console.error);
  }, []);

  function computeStats(entries) {
    if (!entries.length) return;
    // Example stats:
    const avg = +(entries.reduce((sum,e) => sum + e.score, 0) / entries.length).toFixed(2);
    const latest = entries[entries.length - 1];
    // Count per category:
    const byCat = {};
    entries.forEach(e => {
      byCat[e.category] = (byCat[e.category] || 0) + 1;
    });
    setStats({ average: avg, lastCategory: latest.category, counts: byCat });
  }

  if (!stats) return <p>Cargando intereses...</p>;

  // Prepare chart data:
  const pieData = Object.entries(stats.counts).map(([cat, cnt]) => ({
    name: cat,
    value: cnt
  }));

  const timeSeries = data.map((e, i) => ({
    index: i + 1,
    score: e.score
  }));

  return (
    <div className="interests-container">
      <header className="interests-header">
        <h2>Tus Intereses</h2>
        <button onClick={() => navigate('/dashboard')}>← Volver</button>
      </header>

      {/* Quick stats with info tooltips */}
      <div className="interests-stats">
        <div className="stat-card">
          <span>Puntuación Media: <strong>{stats.average}</strong></span>
          <i
            className="fa fa-info-circle info-icon"
            title="Promedio de todas las puntuaciones de interés registradas (suma puntuaciones / nº de registros)."
          ></i>
        </div>
        <div className="stat-card">
          <span>Última Categoría: <strong>{stats.lastCategory}</strong></span>
          <i
            className="fa fa-info-circle info-icon"
            title="Categoría del interés más recientemente marcado."
          ></i>
        </div>
      </div>

      {/* Charts */}
      <div className="interests-charts">
        {/* Pie: Distribución de elecciones */}
        <div className="chart-block">
          <h3>Distribución de Categorías</h3>
          <p className="chart-label">Número de veces que seleccionaste cada categoría.</p>
          <ResponsiveContainer width="100%" height={250}>
            <PieChart>
              <Pie
                data={pieData}
                dataKey="value"
                nameKey="name"
                cx="50%"
                cy="50%"
                outerRadius={80}
                label
              >
                {pieData.map((entry, idx) => (
                  <Cell
                    key={entry.name}
                    fill={CATEGORY_COLORS[entry.name] || '#ccc'}
                  />
                ))}
              </Pie>
              <Tooltip />
              <Legend verticalAlign="bottom" height={36}/>
            </PieChart>
          </ResponsiveContainer>
        </div>

        {/* Bar: Conteo por categoría */}
        <div className="chart-block">
          <h3>Conteo por Categoría</h3>
          <p className="chart-label">Barra que indica cuántas veces elegiste cada categoría.</p>
          <ResponsiveContainer width="100%" height={250}>
            <BarChart
              data={pieData}
              margin={{ top: 20, right: 30, left: 20, bottom: 20 }}
            >
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name">
                <Label value="Categorías" offset={-10} position="insideBottom" />
              </XAxis>
              <YAxis>
                <Label
                  value="Veces seleccionada"
                  angle={-90}
                  position="insideLeft"
                  offset={-5}
                />
              </YAxis>
              <Tooltip />
              <Legend />
              <Bar dataKey="value">
                {pieData.map((entry) => (
                  <Cell
                    key={entry.name}
                    fill={CATEGORY_COLORS[entry.name] || '#8884d8'}
                  />
                ))}
              </Bar>
            </BarChart>
          </ResponsiveContainer>
        </div>

        {/* Line: Evolución de la puntuación */}
        <div className="chart-block">
          <h3>Evolución de Puntuaciones</h3>
          <p className="chart-label">Línea que muestra cómo han cambiado tus puntuaciones con el tiempo.</p>
          <ResponsiveContainer width="100%" height={250}>
            <LineChart
              data={timeSeries}
              margin={{ top: 20, right: 30, left: 20, bottom: 20 }}
            >
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="index">
                <Label value="Registro" offset={-10} position="insideBottom" />
              </XAxis>
              <YAxis domain={[0, 10]}>
                <Label
                  value="Puntuación"
                  angle={-90}
                  position="insideLeft"
                  offset={-5}
                />
              </YAxis>
              <Tooltip />
              <Legend />
              <Line
                type="monotone"
                dataKey="score"
                stroke="#8884d8"
                dot={{ r: 4 }}
              />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>
    </div>
  );
}
