import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import {
  PieChart, Pie, Cell, Tooltip, Legend,
  BarChart, Bar, XAxis, YAxis, CartesianGrid,
  RadialBarChart, RadialBar,
  LineChart, Line,
  ResponsiveContainer
} from 'recharts';
import '../styles/therapistRoutines.css';

const categoryMap = {
  futbol: 'Ocio', dibujos: 'Ocio', comics: 'Ocio',
  tarea: 'Obligaciones', videojuegos: 'Ocio', helado: 'Ocio',
  parque: 'Ocio', banio: 'Autocuidado', dormir: 'Autocuidado',
  musica: 'Ocio', bailar: 'Ocio', amigos: 'Ocio',
  bicicleta: 'Ocio', dibujar: 'Ocio', mascotas: 'Ocio',
  experimentos: 'Obligaciones', cantar: 'Ocio', lego: 'Ocio',
  nadar: 'Autocuidado', computadora: 'Ocio'
};

const COLORS = ['#7c63f6', '#82ca9d', '#ffc658', '#ff8042', '#0088FE'];

function PatientRoutines() {
  const { patientId } = useParams();
  const [results, setResults]     = useState([]);
  const [filtered, setFiltered]   = useState([]);
  const [from, setFrom]           = useState('');
  const [to, setTo]               = useState('');
  const [selectedDate, setSelectedDate] = useState('');
  const [metrics, setMetrics]     = useState(null);

  // 1) Fetch de resultados
  useEffect(() => {
    const token = localStorage.getItem('token');
    axios.get(`http://localhost:8080/api/game?patientId=${patientId}`, {
      headers: { 'x-auth-token': token }
    })
    .then(resp => {
      setResults(resp.data);
      // filtro por defecto √∫ltimos 7 d√≠as
      const now = new Date(), weekAgo = new Date(now);
      weekAgo.setDate(now.getDate() - 6);
      setFrom(weekAgo.toISOString().slice(0,10));
      setTo(now.toISOString().slice(0,10));
    })
    .catch(console.error);
  }, [patientId]);

  // 2) Filtrar resultados por fecha
  useEffect(() => {
    if (!from || !to) return;
    const f = new Date(from), t = new Date(to);
    t.setHours(23,59,59,999);
    setFiltered(results.filter(r => {
      const d = new Date(r.createdAt);
      return d >= f && d <= t;
    }));
  }, [from, to, results]);

  // 3) Seleccionar primer d√≠a tras filtrar
  useEffect(() => {
    if (filtered.length) setSelectedDate(filtered[0].createdAt);
    else setSelectedDate('');
  }, [filtered]);

  // 4) Recalcular m√©tricas cuando cambie la selecci√≥n
  useEffect(() => {
    if (!selectedDate) { setMetrics(null); return; }
    const res = filtered.find(r => r.createdAt === selectedDate);
    setMetrics(res ? computeMetrics(res) : null);
  }, [selectedDate, filtered]);

  // 5) C√°lculo de m√©tricas
  function computeMetrics(result) {
    const slots = result.timeSlots || [];
    const activities = slots.flatMap(slot =>
      (slot.activities || []).map(act => ({
        ...act,
        slot: slot.slot
      }))
    );
    const totalMin = activities.reduce((sum,a) => sum + a.duration, 0) || 1;

    // a) Distribuci√≥n %
    const catSum = {};
    activities.forEach(a => {
      const c = categoryMap[a.activityId] || 'Otros';
      catSum[c] = (catSum[c] || 0) + a.duration;
    });
    const distribution = Object.entries(catSum).map(([name,val],i) => ({
      name, value: +(val/totalMin*100).toFixed(1), fill: COLORS[i%COLORS.length]
    }));

    // b) Diversidad (entrop√≠a de Shannon)
    const entropy = distribution.reduce((H,cat) => {
      const p = cat.value/100;
      return p>0 ? H - p*Math.log2(p) : H;
    }, 0);
    const diversity = +entropy.toFixed(2);

    // c) Balance activo/reposo
    // aqu√≠: activo = Obligaciones+Ocio, reposo = Autocuidado
    const sumActive = (catSum['Obligaciones']||0) + (catSum['Ocio']||0);
    const sumRest   = (catSum['Autocuidado']||0);
    const ratioActiveRest = +((sumActive/sumRest)||0).toFixed(2);

    // d) Ocupaci√≥n horaria
    const hourly = Array.from({length:24},(_,h)=>({ hour:`${h.toString().padStart(2,'0')}:00`, minutes:0 }));
    activities.forEach(a => {
      const h = parseInt(a.slot.split(':')[0],10);
      if (hourly[h]) hourly[h].minutes += a.duration;
    });
    const maxMin = Math.max(...hourly.map(h=>h.minutes)) || 1;

    // e) Cumplimiento de objetivos (thresholds por categor√≠a)
    const objectivesDef = [
      { category:'Autocuidado', target:120 },
      { category:'Obligaciones', target:180 },
      { category:'Ocio',       target:180 }
    ];
    const objectives = objectivesDef.map(obj => {
      const actual = Math.round((catSum[obj.category]||0));
      const percent = Math.min(100, +((actual/obj.target)*100).toFixed(1));
      return { ...obj, actual, percent };
    });

    return { distribution, diversity, ratioActiveRest, hourly, maxMin, objectives };
  }

  return (
    <div className="routines-container">
      <header className="routines-header">
        <Link to="/therapist/routines" className="back-link">‚Üê Volver</Link>
        <h2>Rutinas del Paciente</h2>
      </header>

      <section className="filter-section">
        <label>
          Desde
          <input type="date" value={from} onChange={e=>setFrom(e.target.value)} />
        </label>
        <label>
          Hasta
          <input type="date" value={to} onChange={e=>setTo(e.target.value)} />
        </label>
        <label>
          D√≠a
          <select value={selectedDate} onChange={e=>setSelectedDate(e.target.value)}>
            {filtered.map(r => {
              const d = new Date(r.createdAt).toLocaleDateString();
              return <option key={r.createdAt} value={r.createdAt}>{d}</option>;
            })}
          </select>
        </label>
      </section>

      {metrics && (
        <>
          <div className="metrics-grid">
            <div className="metric-card">üìä Diversidad: <strong>{metrics.diversity} bits</strong></div>
            <div className="metric-card">‚öñÔ∏è Activo/Reposo: <strong>{metrics.ratioActiveRest}</strong></div>
          </div>

          <div className="charts-grid">
            <div className="chart-block">
              <h3>Distribuci√≥n % por Categor√≠a</h3>
              <ResponsiveContainer width="100%" height={240}>
                <PieChart>
                  <Pie data={metrics.distribution} dataKey="value" nameKey="name" cx="50%" cy="50%" outerRadius={80} label>
                    {metrics.distribution.map((entry,i) =>
                      <Cell key={i} fill={entry.fill} />
                    )}
                  </Pie>
                  <Tooltip/>
                  <Legend verticalAlign="bottom" height={36}/>
                </PieChart>
              </ResponsiveContainer>
            </div>

            <div className="chart-block">
              <h3>Gauge Activo/Reposo</h3>
              <ResponsiveContainer width="100%" height={240}>
                <RadialBarChart
                  cx="50%" cy="50%" innerRadius="80%" outerRadius="100%"
                  data={[{ name:'Activo/Reposo', value: metrics.ratioActiveRest * 50 }]}
                  startAngle={180} endAngle={0}
                >
                  <RadialBar minAngle={15} dataKey="value" cornerRadius={10} />
                  <Tooltip/>
                </RadialBarChart>
              </ResponsiveContainer>
            </div>

            <div className="chart-block">
              <h3>Ocupaci√≥n Horaria</h3>
              <div className="heatmap-grid">
                {metrics.hourly.map(h =>
                  <div
                    key={h.hour}
                    className="heat-cell"
                    style={{ backgroundColor: `rgba(124,99,246,${h.minutes/metrics.maxMin})` }}
                  >
                    <span className="heat-label">{h.hour}</span>
                  </div>
                )}
              </div>
            </div>

            <div className="chart-block full-width">
              <h3>Cumplimiento de Objetivos</h3>
              <div className="objective-list">
                {metrics.objectives.map(obj => (
                  <div key={obj.category} className="objective-item">
                    <span className="obj-name">{obj.category}</span>
                    <div className="progress-bar-bg">
                      <div
                        className="progress-bar"
                        style={{ width: `${obj.percent}%` }}
                      />
                    </div>
                    <span className="obj-value">{obj.actual}/{obj.target} min</span>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );
}

export default PatientRoutines;
