// src/pages/PatientTemplatesPage.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/routineTemplates.css';
import { FaCheck, FaClock } from 'react-icons/fa';

export default function PatientTemplatesPage() {
  const navigate = useNavigate();
  const [instances, setInstances] = useState([]);
  const [loading, setLoading]     = useState(true);
  const [error, setError]         = useState(null);

  // Recuperar token y patientId desde el payload JWT
  const token = localStorage.getItem('token');
  let patientId = null;
  if (token) {
    try {
      const payload = JSON.parse(atob(token.split('.')[1]));
      patientId = payload.user.id;
    } catch {
      console.warn('No se pudo decodificar el token');
    }
  }

  useEffect(() => {
    if (!token || !patientId) {
      navigate('/login');
      return;
    }
    const headers = { 'Content-Type':'application/json', 'x-auth-token': token };
    fetch(`/api/routines/instances/${patientId}`, { headers })
      .then(async res => {
        if (res.status === 401) {
          alert('SesiÃ³n expirada.');
          return navigate('/login');
        }
        if (!res.ok) throw new Error(`Error ${res.status}: ${await res.text()}`);
        return res.json();
      })
      .then(data => {
        setInstances(data);
      })
      .catch(err => {
        console.error(err);
        setError(err.message);
      })
      .finally(() => setLoading(false));
  }, [navigate, patientId, token]);

  // Marcar una actividad como completada
  const toggleActivity = async (instanceId, idx) => {
    const headers = { 'Content-Type':'application/json', 'x-auth-token': token };
    try {
      const res = await fetch(
        `/api/routines/instances/${instanceId}/activities/${idx}`,
        { method:'POST', headers }
      );
      if (!res.ok) throw new Error(`Error ${res.status}`);
      const updated = await res.json(); // retornamos la instancia actualizada
      setInstances(insts =>
        insts.map(inst => inst._id === instanceId ? updated : inst)
      );
    } catch (err) {
      console.error('No se pudo marcar actividad:', err);
      alert('Error al marcar actividad.');
    }
  };

  // Marcar todas completadas
  const completeAll = (instance) => {
    instance.activities.forEach((_, idx) =>
      !instance.activities[idx].completed && toggleActivity(instance._id, idx)
    );
  };

  if (loading) return <div className="patient-assigns">Cargando tus plantillasâ€¦</div>;
  if (error)   return <div className="patient-assigns">Error: {error}</div>;

  return (
    <div className="patient-assigns">
      <h1>Mis Plantillas Asignadas</h1>

      {instances.length === 0 ? (
        <div className="empty-state" style={{
          border:'2px dashed var(--primary-color)',
          padding:'2rem',
          textAlign:'center',
          borderRadius:'8px',
          color:'var(--primary-color)',
          marginTop:'2rem'
        }}>
          ðŸ˜• No tienes plantillas asignadas
        </div>
      ) : instances.map(inst => {
        const total = inst.activities.length;
        const done  = inst.activities.filter(a => a.completed).length;
        const pct   = Math.round(done / total * 100);
        const statusClass = done === total
          ? 'status-completed'
          : 'status-postponed';

        return (
          <div key={inst._id} className={`assign-card ${statusClass}`}>
            <h3>{inst.template.name}</h3>
            <small>{inst.template.description}</small>

            {/* Barra de progreso */}
            <div style={{
              background:'#eee',
              borderRadius:'4px',
              overflow:'hidden',
              margin:'1rem 0'
            }}>
              <div style={{
                width:`${pct}%`,
                background:'#388e3c',
                color:'#fff',
                textAlign:'center',
                padding:'4px 0'
              }}>
                {pct}% completado
              </div>
            </div>

            {/* Lista de actividades */}
            <ul style={{ listStyle:'none', padding:0 }}>
              {inst.activities.map((act, idx) => (
                <li key={idx} style={{
                  display:'flex',
                  justifyContent:'space-between',
                  alignItems:'center',
                  marginBottom:'0.5rem'
                }}>
                  <span style={{
                    textDecoration: act.completed ? 'line-through' : 'none',
                    color: act.completed ? '#888' : '#333'
                  }}>
                    {act.name} ({act.minutes} min)
                  </span>
                  <button
                    className="btn-complete"
                    onClick={() => toggleActivity(inst._id, idx)}
                    disabled={act.completed}
                    title={act.completed ? 'Ya completada' : 'Marcar completada'}
                  >
                    {act.completed ? <FaCheck/> : <FaCheck/>}
                  </button>
                </li>
              ))}
            </ul>

            {/* Acciones globales */}
            <div className="assign-actions">
              <button
                className="btn-complete"
                onClick={() => completeAll(inst)}
                disabled={done === total}
              >
                <FaCheck style={{ marginRight:'0.5rem' }}/>
                Completar todo
              </button>
              <button
                className="btn-postpone"
                onClick={() => alert('FunciÃ³n de posponer prÃ³ximamente')}
              >
                <FaClock style={{ marginRight:'0.5rem' }}/>
                Posponer
              </button>
            </div>
          </div>
        );
      })}
    </div>
  );
}
