import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaBell, FaPlus, FaTrash, FaClock } from 'react-icons/fa';
import '../styles/taskPlanner.css';
import axios from 'axios';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

function TaskPlanner() {
  const navigate = useNavigate();
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({ title: '', scope: '', hour: '' });
  const [reminders, setReminders] = useState([]);

  const handleChange = (e) => {
    setNewTask({ ...newTask, [e.target.name]: e.target.value });
  };

  const handleAddTask = async () => {
    if (!newTask.title || !newTask.scope || !newTask.hour) return;

    const token = localStorage.getItem('token');
    const createdTask = {
      ...newTask,
      id: Date.now().toString(),
    };

    try {
      await axios.post('http://localhost:5000/api/tasks', createdTask, {
        headers: { 'x-auth-token': token },
      });
      setTasks((prev) => [...prev, createdTask]);
      setReminders((prev) => [...prev, createdTask]);
      setNewTask({ title: '', scope: '', hour: '' });
    } catch (err) {
      console.error('Error creando tarea:', err);
    }
  };

  const handleDeleteTask = (id) => {
    setTasks((prev) => prev.filter((task) => task.id !== id));
  };

  const onDragEnd = (result) => {
    if (!result.destination) return;
    const items = Array.from(tasks);
    const [reordered] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reordered);
    setTasks(items);
  };

  return (
    <div className="planner-container">
      <header className="top-bar">
        <div className="left-section">
          <i className="fa fa-user-circle user-icon" aria-hidden="true"></i>
        </div>
        <div className="center-section">
          <h1 className="title">PLANIFICADOR DE TAREAS</h1>
        </div>
        <div className="right-section">
          <button className="bell-icon" onClick={() => navigate('/dashboard')}>
            <FaBell />
            {reminders.length > 0 && <span className="notification-dot"></span>}
          </button>
        </div>
      </header>

      <main className="main-content">
        <div className="task-creator">
          <input
            type="text"
            name="title"
            placeholder="¿Qué vas a hacer?"
            value={newTask.title}
            onChange={handleChange}
          />
          <select name="scope" value={newTask.scope} onChange={handleChange}>
            <option value="">Ámbito</option>
            <option value="Personal">Personal</option>
            <option value="Familiar">Familiar</option>
            <option value="Educativo">Educativo</option>
            <option value="Social">Social</option>
          </select>
          <input
            type="time"
            name="hour"
            value={newTask.hour}
            onChange={handleChange}
          />
          <button className="add-task-btn" onClick={handleAddTask}>
            <FaPlus />
          </button>
        </div>

        <DragDropContext onDragEnd={onDragEnd}>
          <Droppable droppableId="taskList">
            {(provided) => (
              <ul
                className="task-list"
                {...provided.droppableProps}
                ref={provided.innerRef}
              >
                {tasks.map((task, index) => (
                  <Draggable key={task.id} draggableId={task.id} index={index}>
                    {(provided) => (
                      <li
                        className={`task-item ${task.scope.toLowerCase()}`}
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                      >
                        <div className="task-info">
                          <span className="task-title">{task.title}</span>
                          <span className="task-scope">{task.scope}</span>
                          <span className="task-hour">
                            <FaClock /> {task.hour}
                          </span>
                        </div>
                        <button
                          className="delete-btn"
                          onClick={() => handleDeleteTask(task.id)}
                        >
                          <FaTrash />
                        </button>
                      </li>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </ul>
            )}
          </Droppable>
        </DragDropContext>
      </main>

      <footer className="footer-bar">
        <p>2025 © Iván Vela Campos</p>
      </footer>
    </div>
  );
}

export default TaskPlanner;
