// src/components/PatientTemplateDetail.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, Link } from 'react-router-dom';
import '../styles/routineTemplates.css';

/** Decodifica un JWT y devuelve su payload como objeto, o null si falla */
function decodeJwt(token) {
  try {
    const b64Url = token.split('.')[1];
    const b64    = b64Url.replace(/-/g, '+').replace(/_/g, '/');
    return JSON.parse(atob(b64));
  } catch {
    return null;
  }
}

function getPatientId() {
  const raw = localStorage.getItem('token');
  if (!raw) return null;
  const token = raw.startsWith('Bearer ') ? raw.slice(7) : raw;
  const payload = decodeJwt(token);
  return payload?.id || payload?.sub || payload?.user?.id || localStorage.getItem('userId');
}

export default function PatientTemplateDetail() {
  const { instanceId } = useParams();
  const [instance, setInstance] = useState(null);
  const [acts, setActs]         = useState([]);
  const [loading, setLoading]   = useState(true);
  const [error, setError]       = useState(null);

  useEffect(() => {
    const fetchDetail = async () => {
      const token     = localStorage.getItem('token');
      const patientId = getPatientId();
      if (!token || !patientId) {
        setError('No estás autenticado. Por favor, inicia sesión.');
        setLoading(false);
        return;
      }

      try {
        const res = await axios.get(
          `/api/routines/instances/${patientId}`,
          { headers: { 'x-auth-token': token } }
        );
        const list = Array.isArray(res.data)
          ? res.data
          : Array.isArray(res.data.instances)
            ? res.data.instances
            : [];
        const found = list.find(i => i._id === instanceId);
        if (!found) {
          setError('No se encontró la plantilla solicitada.');
        } else {
          setInstance(found);
          // Prepara un estado editable de actividades
          setActs(
            found.template.activities.map((act, idx) => ({
              ...act,
              idx,
              completed: found.completedActivities.includes(idx),
              duration: act.minutes,
              category: act.category ?? 'Otro'
            }))
          );
        }
      } catch (err) {
        console.error(err);
        setError(err.response?.data?.msg || 'Error al cargar detalles');
      } finally {
        setLoading(false);
      }
    };

    fetchDetail();
  }, [instanceId]);

  const toggleComplete = idx => {
    setActs(prev =>
      prev.map(a =>
        a.idx !== idx ? a : { ...a, completed: !a.completed }
      )
    );
  };

  const onFieldChange = (idx, field, value) => {
    setActs(prev =>
      prev.map(a =>
        a.idx !== idx ? a : { ...a, [field]: value }
      )
    );
  };

  const saveProgress = async () => {
    const token = localStorage.getItem('token');
    try {
      await Promise.all(
        acts.map(a =>
          axios.post(
            `/api/routines/instances/${instanceId}/activities/${a.idx}`,
            { completed: a.completed },
            { headers: { 'x-auth-token': token } }
          )
        )
      );
      alert('Progreso guardado 👍');
    } catch {
      alert('Error guardando progreso');
    }
  };

  if (loading) return <p>Cargando detalles…</p>;
  if (error)   return <p className="error-msg">{error}</p>;

  const tpl = instance.template;
  const completedCount = acts.filter(a => a.completed).length;
  const totalCount     = acts.length;
  const progressPct    = Math.round((completedCount / totalCount) * 100);

  return (
    <div className="routine-templates-page patient-templates-page tpl-detail-page">
      <div className="tpl-detail-card">
        <div className="detail-header">
          <Link to="/patient/templates" className="back-link">← Volver</Link>
          <h1 className="tpl-title">{tpl.name}</h1>
        </div>

        <div className="tags">
          {tpl.tags?.map(tag => (
            <span key={tag} className="tag-badge">{tag}</span>
          ))}
        </div>

        <p className="tpl-desc">{tpl.description}</p>

        <div className="tpl-detail-meta">
          <div><strong>Asignado:</strong>{' '}
            {new Date(instance.createdAt).toLocaleDateString()}</div>
          <div><strong>Duración total:</strong> {tpl.duration} min</div>
          <div>
            <strong>Nivel reto:</strong>{' '}
            <span className={`challenge-level lvl-${tpl.challengeLevel}`}>
              {tpl.challengeLevel}
            </span>
          </div>
          <div className="progress-bar">
            <label>Progreso: {completedCount}/{totalCount}</label>
            <progress value={completedCount} max={totalCount} />
            <span className="pct-label">{progressPct}%</span>
          </div>
        </div>

        <h2 className="activities-title">Actividades</h2>
        <div className="activities-container">
          {acts.map(a => (
            <div key={a.idx} className="activity-item detail">
              <div className="activity-header">
                <span className="activity-number">#{a.idx + 1}</span>
                <h3>{a.name}</h3>
                <span className={`badge challenge-badge lvl-${a.challenge}`}>
                  Reto {a.challenge}
                </span>
              </div>
              {a.desc && <p className="activity-desc">{a.desc}</p>}
              <div className="activity-fields">
                <div className="field">
                  <label>Duración (min):</label>
                  <input
                    type="number"
                    value={a.duration}
                    onChange={e => onFieldChange(a.idx, 'duration', e.target.value)}
                  />
                </div>
                <div className="field">
                  <label>Categoría:</label>
                  <select
                    value={a.category}
                    onChange={e => onFieldChange(a.idx, 'category', e.target.value)}
                  >
                    <option>Ejercicio</option>
                    <option>Meditación</option>
                    <option>Lectura</option>
                    <option>Otro</option>
                  </select>
                </div>
                <div className="field complete-toggle">
                  <label>
                    <input
                      type="checkbox"
                      checked={a.completed}
                      onChange={() => toggleComplete(a.idx)}
                    />
                    Completada
                  </label>
                </div>
              </div>
            </div>
          ))}
        </div>

        <button className="btn-save" onClick={saveProgress}>
          Guardar progreso
        </button>
      </div>
    </div>
  );
}
