// src/components/TherapistTemplates.js
import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/routineTemplates.css';

export default function TherapistTemplates() {
  const [templates, setTemplates] = useState([]);
  const [patients, setPatients]   = useState([]);
  const [loading, setLoading]     = useState(true);
  const [error, setError]         = useState(null);
  const navigate = useNavigate();

  // Leer token desde localStorage
  const token = localStorage.getItem('token');
  const headers = {
    'Content-Type': 'application/json',
    'x-auth-token': token
  };

  useEffect(() => {
    if (!token) {
      navigate('/login');
      return;
    }

    (async () => {
      try {
        // GET plantillas
        const resTpl = await fetch('/api/routines/templates', { headers });
        if (resTpl.status === 401) {
          alert('Sesi√≥n expirada. Vuelve a iniciar sesi√≥n.');
          return navigate('/login');
        }
        if (!resTpl.ok) {
          throw new Error(`Error ${resTpl.status}: ${await resTpl.text()}`);
        }
        const dataTpl = await resTpl.json();
        if (!Array.isArray(dataTpl)) {
          throw new Error('Formato de datos de plantillas inv√°lido');
        }

        // GET pacientes
        const resPat = await fetch('/api/therapist/patients', { headers });
        if (resPat.status === 401) {
          alert('Sesi√≥n expirada. Vuelve a iniciar sesi√≥n.');
          return navigate('/login');
        }
        if (!resPat.ok) {
          throw new Error(`Error ${resPat.status}: ${await resPat.text()}`);
        }
        const dataPat = await resPat.json();
        if (!Array.isArray(dataPat)) {
          throw new Error('Formato de datos de pacientes inv√°lido');
        }

        setTemplates(dataTpl);
        setPatients(dataPat);
      } catch (err) {
        console.error(err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    })();
  }, [navigate, token]);

  const allowDrop = e => e.preventDefault();

  const onDragStart = (e, item, type) => {
    e.dataTransfer.setData('application/json', JSON.stringify({ type, item }));
    e.dataTransfer.effectAllowed = 'copyMove';
  };

  const handleDrop = useCallback((e, zone) => {
    e.preventDefault();
    const { type, item } = JSON.parse(e.dataTransfer.getData('application/json'));

    // ELIMINAR
    if (zone === 'trash' && type === 'template') {
      setTemplates(ts => ts.filter(t => t._id !== item._id));
      fetch(`/api/routines/templates/${item._id}`, {
        method: 'DELETE', headers
      }).catch(console.error);
      return;
    }

    // DUPLICAR
    if (zone === 'duplicate' && type === 'template') {
      fetch('/api/routines/templates', {
        method: 'POST',
        headers,
        body: JSON.stringify({ ...item, name: item.name + ' (copia)' })
      })
        .then(res => {
          if (!res.ok) throw new Error(`Error al duplicar: ${res.status}`);
          return res.json();
        })
        .then(newTpl => setTemplates(ts => [newTpl, ...ts]))
        .catch(console.error);
      return;
    }

    // ASIGNAR A PACIENTE
    if (zone.startsWith('assign-') && type === 'template') {
      const patientId = zone.split('-')[1];
      fetch(`/api/patients/${patientId}/assignTemplate`, {
        method: 'POST',
        headers,
        body: JSON.stringify({ templateId: item._id })
      })
        .then(res => {
          if (!res.ok) throw new Error(`Error ${res.status}`);
          alert(`Plantilla ‚Äú${item.name}‚Äù asignada a paciente.`);
        })
        .catch(console.error);
    }
  }, [patients, token]);

  if (loading) return <div className="tpl-container">Cargando plantillas‚Ä¶</div>;
  if (error)   return <div className="tpl-container">Error: {error}</div>;

  return (
    <div className="routine-templates-page">
      <div className="tpl-container">
        <div className="tpl-header">
          <h1>Mis Plantillas</h1>
          <button className="btn-new" onClick={() => navigate('/therapist/templates/new')}>
            üìÑ Nueva plantilla
          </button>
        </div>

        {templates.length === 0 ? (
          <div
            className="empty-state"
            style={{
              border: '2px dashed var(--primary-color)',
              padding: '2rem',
              textAlign: 'center',
              borderRadius: '8px',
              color: 'var(--primary-color)',
              fontSize: '1.2rem',
              marginTop: '2rem'
            }}
          >
            ü§î A√∫n no se han creado plantillas
          </div>
        ) : (
          <div className="tpl-grid">
            {templates.map(t => (
              <div
                key={t._id}
                className="tpl-card"
                draggable
                onDragStart={e => onDragStart(e, t, 'template')}
              >
                <h2>{t.name}</h2>
                <p className="tpl-desc">{t.description}</p>
                <div className="tpl-meta">{t.activities.length} actividades</div>
              </div>
            ))}
          </div>
        )}

        <div className="dropzones" style={{ display:'flex', gap:'1rem', marginTop:'2rem' }}>
          <div className="dropzone trash" onDragOver={allowDrop} onDrop={e => handleDrop(e,'trash')}>
            üóëÔ∏è Arrastra aqu√≠ para eliminar
          </div>
          <div className="dropzone duplicate" onDragOver={allowDrop} onDrop={e => handleDrop(e,'duplicate')}>
            üìã Arrastra aqu√≠ Ctrl+drag para duplicar
          </div>
        </div>

        <h3 style={{ marginTop:'2rem' }}>Pacientes</h3>
        <div className="tpl-grid">
          {patients.map(p => (
            <div
              key={p._id}
              className="patient-card"
              onDragOver={allowDrop}
              onDrop={e => handleDrop(e, `assign-${p._id}`)}
            >
              <img src={p.avatar} alt={p.nombre} style={{ width:40, borderRadius:'50%' }}/>
              <div style={{ marginLeft:'0.5rem' }}>
                <strong>{p.nombre} {p.apellidos}</strong>
                <div style={{ fontSize:'0.8rem', color:'#666' }}>{p.email}</div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
