import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, Link } from 'react-router-dom';
import '../styles/routineTemplates.css';

export default function TherapistTemplateDetail() {
  const { templateId } = useParams();
  const [instances, setInstances] = useState([]);
  const [loading, setLoading]     = useState(true);
  const [error, setError]         = useState(null);

  useEffect(() => {
    const fetchInstances = async () => {
      try {
        const token = localStorage.getItem('token');
        // Asumimos que existe este endpoint en backend
        const { data } = await axios.get(
          `/api/routineInstances/template/${templateId}`,
          { headers: { 'x-auth-token': token } }
        );
        setInstances(Array.isArray(data) ? data : data.instances);
      } catch (err) {
        setError(err.response?.data?.msg || 'Error cargando pacientes');
      } finally {
        setLoading(false);
      }
    };
    fetchInstances();
  }, [templateId]);

  if (loading) return <p>Cargando pacientes…</p>;
  if (error)   return <p className="error-msg">{error}</p>;

  return (
    <div className="routine-templates-page therapist-template-detail">
      <div className="tpl-container">
        <div className="detail-header">
          <Link to="/therapist/templates" className="back-link">
            ← Volver a Plantillas
          </Link>
          <h1>Progreso de Pacientes</h1>
        </div>

        <div className="patients-grid">
          {instances.map(inst => {
            const p = inst.patient;
            const total = inst.template.activities.length;
            const done  = inst.completedActivities.length;
            const pct   = Math.round((done / total) * 100);

            return (
              <Link
                key={inst._id}
                to={`/therapist/templates/${templateId}/patient/${inst._id}`}
                className="patient-card"
                title={`Ver detalle de ${p.nombre} ${p.apellidos}`}
              >
                <h2>{p.nombre} {p.apellidos}</h2>
                <small>{new Date(p.fechaNacimiento).toLocaleDateString()} │ {p.email}</small>
                <div className="progress-bar">
                  <div
                    className="progress-filled"
                    style={{ width: `${pct}%` }}
                    aria-label={`${pct}% completado`}
                  />
                </div>
                <p className="progress-text">
                  {done} de {total} actividades ({pct}%)
                </p>
                <small className="last-update">
                  Última actualización: {new Date(inst.updatedAt || inst.createdAt).toLocaleString()}
                </small>
              </Link>
            );
          })}
        </div>
      </div>
    </div>
  );
}
