import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/patientTemplates.css';
import { FaCheckCircle, FaCircle } from 'react-icons/fa';

export default function PatientTemplatesPage() {
  const navigate = useNavigate();
  const [templates, setTemplates] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!token) {
      navigate('/login');
      return;
    }
    fetch('/api/patient/templates', {
      headers: { 'Content-Type': 'application/json', 'x-auth-token': token }
    })
      .then(res => {
        if (res.status === 401) throw new Error('SesiÃ³n expirada');
        if (!res.ok) throw new Error(`Error ${res.status}`);
        return res.json();
      })
      .then(data => {
        if (!Array.isArray(data)) throw new Error('Datos invÃ¡lidos');
        setTemplates(data.map(t => ({ ...t, completed: Array(t.activities.length).fill(false) })));
      })
      .catch(err => setError(err.message))
      .finally(() => setLoading(false));
  }, [navigate, token]);

  const toggleActivity = (tplId, idx) => {
    setTemplates(ts => ts.map(t => {
      if (t._id !== tplId) return t;
      const comp = [...t.completed]; comp[idx] = !comp[idx];
      return { ...t, completed: comp };
    }));
    // TODO: PATCH /api/patient/templates/:tplId/activities/:idx
  };

  if (loading) return <div className="pt-container">Cargando plantillasâ€¦</div>;
  if (error)   return <div className="pt-container">Error: {error}</div>;

  return (
    <div className="patient-templates-page">
      <div className="pt-container">
        <h1>Plantillas Asignadas</h1>
        {templates.length === 0 ? (
          <div className="empty-state">
            ðŸ“‚ No tienes plantillas asignadas
          </div>
        ) : (
          <div className="pt-grid">
            {templates.map(template => {
              const doneCount = template.completed.filter(c => c).length;
              const total = template.activities.length;
              return (
                <div key={template._id} className="pt-card">
                  <div className="pt-header">
                    <h2>{template.name}</h2>
                    <div className="pt-progress">
                      {doneCount}/{total} completadas
                    </div>
                  </div>
                  <p className="pt-desc">{template.description}</p>
                  <ul className="pt-activities">
                    {template.activities.map((act, i) => (
                      <li key={i} className="pt-activity">
                        <button
                          className="pt-checkbox"
                          onClick={() => toggleActivity(template._id, i)}
                        >
                          {template.completed[i]
                            ? <FaCheckCircle />
                            : <FaCircle />}
                        </button>
                        <span className={template.completed[i] ? 'done' : ''}>
                          {act.name}
                        </span>
                      </li>
                    ))}
                  </ul>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
}
