// src/pages/PatientTemplatesPage.js
import React, { useState, useEffect } from 'react';
import { useNavigate }                from 'react-router-dom';
import '../styles/routineTemplates.css';
import { FaCheckCircle, FaRegCircle } from 'react-icons/fa';

export default function PatientTemplatesPage() {
  const navigate = useNavigate();
  const [instances, setInstances] = useState([]);
  const [loading, setLoading]     = useState(true);
  const [error, setError]         = useState(null);

  // Leer token y user id del JWT
  const token = localStorage.getItem('token');
  let payload = null;
  try {
    payload = JSON.parse(atob(token.split('.')[1])); // decodifica JWT
  } catch {}
  const patientId = payload?.user?.id;
  const headers = {
    'Content-Type': 'application/json',
    'x-auth-token': token
  };

  useEffect(() => {
    if (!token || !patientId) {
      navigate('/login');
      return;
    }
    (async () => {
      try {
        const res = await fetch(
          `/api/routines/instances/${patientId}`,
          { headers }
        );
        if (res.status === 401) {
          navigate('/login');
          return;
        }
        if (!res.ok) {
          throw new Error(`Error ${res.status}`);
        }
        const data = await res.json();
        // data: [{ _id, template: { name, description, activities }, completed: [bool...] }, â€¦]
        setInstances(data);
      } catch (err) {
        console.error(err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    })();
  }, [navigate, patientId, token]);

  // Marca una actividad como completada
  const toggleActivity = async (instanceId, idx) => {
    try {
      const res = await fetch(
        `/api/routines/instances/${instanceId}/activities/${idx}`,
        {
          method: 'POST',
          headers
        }
      );
      if (!res.ok) throw new Error(`HTTP ${res.status}`);
      // Actualizar estado localmente
      setInstances(insts =>
        insts.map(inst => {
          if (inst._id !== instanceId) return inst;
          const comp = [...inst.completed];
          comp[idx] = !comp[idx];
          return { ...inst, completed: comp };
        })
      );
    } catch (err) {
      console.error('Error marcando actividad:', err);
      alert('No se pudo actualizar el estado de la actividad');
    }
  };

  if (loading) return <div className="tpl-container">Cargando rutinasâ€¦</div>;
  if (error)   return <div className="tpl-container">Error: {error}</div>;

  return (
    <div className="patient-assigns">
      <h1>Mis Rutinas Asignadas</h1>

      {instances.length === 0 ? (
        <p>ðŸ¤” AÃºn no tienes rutinas asignadas.</p>
      ) : (
        instances.map(inst => {
          const { _id, template, completed = [] } = inst;
          // Si todas las actividades completadas, estilo â€œcompletadoâ€
          const allDone = template.activities.every((_, i) => completed[i]);
          const cardClass = allDone
            ? 'assign-card status-completed'
            : 'assign-card';

          return (
            <div key={_id} className={cardClass}>
              <h3>{template.name}</h3>
              <small>{template.description}</small>

              <ul className="assign-list">
                {template.activities.map((act, idx) => (
                  <li key={idx} className="assign-item">
                    <button
                      className="activity-toggle"
                      onClick={() => toggleActivity(_id, idx)}
                      title={
                        completed[idx]
                          ? 'Marcar como pendiente'
                          : 'Marcar como completada'
                      }
                    >
                      {completed[idx]
                        ? <FaCheckCircle />
                        : <FaRegCircle />}
                    </button>
                    <span
                      className={
                        completed[idx]
                          ? 'activity-completed'
                          : ''
                      }
                    >
                      {act.name} ({act.minutes} min)
                    </span>
                  </li>
                ))}
              </ul>

              <div className="assign-actions">
                {allDone
                  ? <span>âœ… Rutina completada</span>
                  : <span>ðŸ•’ Pendiente</span>}
              </div>
            </div>
          );
        })
      )}
    </div>
  );
}
