import React, { useState, useEffect, useRef } from 'react';
import { useNavigate }          from 'react-router-dom';
import Calendar                 from 'react-calendar';
import axios                    from 'axios';
import 'react-calendar/dist/Calendar.css';
import '../styles/home.css';
import '../styles/taskPlanner.css';

function decodeJwt(token) {
  try {
    const b64 = token
      .split('.')[1]
      .replace(/-/g, '+')
      .replace(/_/g, '/');
    return JSON.parse(atob(b64));
  } catch {
    return null;
  }
}
function getUserId() {
  const raw = localStorage.getItem('token');
  if (!raw) return null;
  const token  = raw.startsWith('Bearer ') ? raw.slice(7) : raw;
  const payload= decodeJwt(token);
  return payload?.id || payload?.sub || payload?.user?.id || null;
}

export default function Home() {
  const navigate   = useNavigate();
  const [selectedDate, setSelectedDate] = useState(new Date());
  // … restantes estados para recordatorios …

  // Modal de perfil
  const [showProfileModal, setShowProfileModal] = useState(false);
  const [currentAvatar, setCurrentAvatar]       = useState(null);
  const [avatarFile, setAvatarFile]             = useState(null);
  const [avatarPreview, setAvatarPreview]       = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios
        .get('/api/users/me', { headers: { 'x-auth-token': token } })
        .then(res => setCurrentAvatar(res.data.avatarUrl))
        .catch(() => {});
    }
  }, []);

  const handleAvatarChange = e => {
    const file = e.target.files[0];
    if (file) {
      setAvatarFile(file);
      setAvatarPreview(URL.createObjectURL(file));
    }
  };

  const handleSaveAvatar = async () => {
    if (!avatarFile) return;
    const token = localStorage.getItem('token');
    const form  = new FormData();
    form.append('avatar', avatarFile);
    try {
      const { data } = await axios.post(
        '/api/users/avatar',
        form,
        {
          headers: {
            'x-auth-token': token,
            'Content-Type': 'multipart/form-data'
          }
        }
      );
      setCurrentAvatar(data.avatarUrl);
      setShowProfileModal(false);
      setAvatarFile(null);
      setAvatarPreview(null);
    } catch {
      alert('Error al actualizar la foto.');
    }
  };

  const handleLogout = () => navigate('/');

  return (
    <div className="home-container">
      <header className="top-bar">
        <div className="left-section">
          <i
            className="fa fa-user-circle user-icon"
            onClick={() => setShowProfileModal(true)}
          />
        </div>
        <div className="center-section">
          <h1 className="title">JANUS</h1>
        </div>
        <div className="right-section">
          <div className="notification-wrapper">
            <i className="fa fa-bell bell-icon" />
            {/* … punto de notificación … */}
          </div>
          <button className="logout-btn" onClick={handleLogout}>
            <i className="fa fa-sign-out" />
            <span>Salir</span>
          </button>
        </div>
      </header>

      {/* Modal de cambiar avatar */}
      {showProfileModal && (
        <div className="profile-modal-overlay">
          <div className="profile-modal-content">
            <h2>Actualizar Foto de Perfil</h2>
            <img
              src={
                avatarPreview ||
                currentAvatar ||
                '/default-profile.png'
              }
              alt="Avatar"
              className="avatar-preview"
            />
            <input
              type="file"
              accept="image/*"
              className="file-input"
              onChange={handleAvatarChange}
            />
            <div className="modal-buttons">
              <button
                className="profile-btn"
                onClick={handleSaveAvatar}
                disabled={!avatarFile}
              >
                Guardar
              </button>
              <button
                className="cancel-btn"
                onClick={() => setShowProfileModal(false)}
              >
                Cancelar
              </button>
            </div>
          </div>
        </div>
      )}

      {/** notification panel omitted for brevity **/}

      <main className="main-content">
        <section className="intro-section">
          <h2>¡Bienvenido/a a JANUS!</h2>
          <p>Explora o gestiona tus recordatorios:</p>
          <button className="create-task-btn" onClick={()=>setShowCreateModal(true)}>
            <i className="fa fa-plus" /> Añadir recordatorio
          </button>
          <button className="manage-btn" onClick={openManage}>
            <i className="fa fa-cog" /> Gestionar Recordatorios
          </button>
        </section>

        <div className="buttons-and-calendar">
          <div className="buttons-block">
            <button className="option-btn" onClick={()=>navigate('/messaging')}>
              <i className="fa fa-comments icon" /> Mensajería
            </button>
            <button className="option-btn" onClick={()=>navigate('/time-machine-game')}>
              <i className="fa fa-clock-o icon" /> Máquina del Tiempo
            </button>
            <button className="option-btn" onClick={()=>navigate('/mi-planeta')}>
              <i className="fa fa-globe icon" /> Mi Planeta
            </button>
            <button className="option-btn" onClick={()=>navigate('/patient/templates')}>
              <i className="fa fa-folder-open icon" /> Mis Plantillas
            </button>
            <button className="option-btn" onClick={()=>navigate('/diario-de-emociones')}>
              <i className="fa fa-book icon" /> Diario de Emociones
            </button>
          </div>
          <div className="calendar-block">
            <Calendar
              onChange={setSelectedDate}
              value={selectedDate}
              tileContent={tileContent}
            />
          </div>
        </div>
      </main>

      {/* Crear recordatorio modal */}
      {showCreateModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Nuevo Recordatorio</h2>
            <label>
              Título
              <input
                type="text"
                value={newTitle}
                onChange={e=>setNewTitle(e.target.value)}
              />
            </label>
            <label>
              Fecha y hora
              <input
                type="datetime-local"
                value={newDateTime}
                onChange={e=>setNewDateTime(e.target.value)}
              />
            </label>
            <div className="modal-buttons">
              <button onClick={handleCreate}>Guardar</button>
              <button onClick={()=>setShowCreateModal(false)}>Cancelar</button>
            </div>
          </div>
        </div>
      )}

      {/* Gestionar recordatorios modal */}
      {showManage && (
        <div className="modal-overlay">
          <div className="modal-content manage-content">
            <h2>Gestionar Recordatorios</h2>
            <ul className="manage-list">
              {tasks.map(t => {
                const { title, datetime } = editStates[t._id]||{};
                return (
                  <li key={t._id} className="manage-item">
                    <input
                      value={title||''}
                      onChange={e=>{
                        setEditStates(es=>({
                          ...es,
                          [t._id]: { ...es[t._id], title: e.target.value }
                        }));
                      }}
                    />
                    <input
                      type="datetime-local"
                      value={datetime||''}
                      onChange={e=>{
                        setEditStates(es=>({
                          ...es,
                          [t._id]: { ...es[t._id], datetime: e.target.value }
                        }));
                      }}
                    />
                    <div className="manage-actions">
                      <button onClick={()=>handleEdit(t._id)}>Guardar</button>
                      <button onClick={()=>handleDelete(t._id)}>Eliminar</button>
                    </div>
                  </li>
                );
              })}
            </ul>
            <button onClick={()=>setShowManage(false)}>Cerrar</button>
          </div>
        </div>
      )}

      <footer className="footer-bar">
        <p>2025 © Iván Vela Campos</p>
      </footer>
    </div>
  );
}
