// src/pages/PatientTemplatesPage.js
import React, { useState, useEffect } from 'react';
import {
  FaChevronDown,
  FaChevronUp,
  FaCheckCircle,
  FaRegCircle
} from 'react-icons/fa';
import '../styles/routineTemplates.css'; // Estilos de tarjetas y grid :contentReference[oaicite:0]{index=0}:contentReference[oaicite:1]{index=1}

export default function PatientTemplatesPage() {
  const [instances, setInstances]       = useState([]);
  const [filtered, setFiltered]         = useState([]);
  const [expanded, setExpanded]         = useState({});
  const [loading, setLoading]           = useState(true);
  const [error, setError]               = useState(null);
  const [search, setSearch]             = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [sortOption, setSortOption]     = useState('name');
  const [currentPage, setCurrentPage]   = useState(1);
  const itemsPerPage = 6;

  const navigate = useNavigate();
  const token    = localStorage.getItem('token');

  // Decodificar patientId desde el payload del JWT :contentReference[oaicite:2]{index=2}:contentReference[oaicite:3]{index=3}
  let patientId = null;
  if (token) {
    try {
      const payload = JSON.parse(atob(token.split('.')[1]));
      patientId = payload.user?.id || payload.id || payload.sub;
    } catch (e) {
      console.error('Token inv√°lido:', e);
    }
  }

  // Fetch de instancias asignadas :contentReference[oaicite:4]{index=4}:contentReference[oaicite:5]{index=5}
  useEffect(() => {
    if (!token || !patientId) {
      navigate('/login');
      return;
    }
    const headers = {
      'Content-Type': 'application/json',
      'x-auth-token': token
    };
    fetch(`/api/routines/instances/${patientId}`, { headers })
      .then(res => {
        if (res.status === 401) {
          alert('Sesi√≥n expirada. Inicia sesi√≥n de nuevo.');
          navigate('/login');
          return null;
        }
        if (!res.ok) throw new Error(`Error ${res.status}`);
        return res.json();
      })
      .then(data => Array.isArray(data) ? setInstances(data) : setError('Respuesta inesperada'))
      .catch(err => setError(err.message))
      .finally(() => setLoading(false));
  }, [navigate, token, patientId]);

  // Aplicar b√∫squeda, filtro y orden sobre instances
  useEffect(() => {
    let list = [...instances];
    // b√∫squeda por nombre
    if (search) {
      list = list.filter(i =>
        i.template.name.toLowerCase().includes(search.toLowerCase())
      );
    }
    // filtro por estado
    if (statusFilter !== 'all') {
      list = list.filter(i => i.status === statusFilter);
    }
    // orden
    list.sort((a, b) => {
      if (sortOption === 'name') {
        return a.template.name.localeCompare(b.template.name);
      } else if (sortOption === 'progress') {
        const pa = a.completedActivities.length / a.template.activities.length;
        const pb = b.completedActivities.length / b.template.activities.length;
        return pb - pa;
      }
      return 0;
    });
    setFiltered(list);
    setCurrentPage(1);
  }, [instances, search, statusFilter, sortOption]);

  // Helpers de interacci√≥n
  const toggleExpand = id => {
    setExpanded(e => ({ ...e, [id]: !e[id] }));
  };
  const markActivity = (instId, idx) => {
    const headers = { 'Content-Type':'application/json','x-auth-token':token };
    fetch(`/api/routines/instances/${instId}/activities/${idx}`, {
      method: 'POST', headers, body: JSON.stringify({ completed: true })
    })
      .then(res => {
        if (!res.ok) throw new Error('No se pudo actualizar');
        // actualizar local
        setInstances(list =>
          list.map(i => {
            if (i._id !== instId) return i;
            const set = new Set(i.completedActivities);
            set.add(idx);
            return { ...i, completedActivities: Array.from(set) };
          })
        );
      })
      .catch(console.error);
  };
  const completeAll = inst => {
    inst.template.activities.forEach((_, idx) => {
      if (!inst.completedActivities.includes(idx)) {
        markActivity(inst._id, idx);
      }
    });
  };
  const postpone = instId => {
    setInstances(list =>
      list.map(i => i._id === instId ? { ...i, status: 'postponed' } : i)
    );
  };

  // Paginaci√≥n
  const totalPages = Math.ceil(filtered.length / itemsPerPage);
  const paginated = filtered.slice(
    (currentPage-1)*itemsPerPage,
    currentPage*itemsPerPage
  );

  if (loading) return <div className="tpl-container"><p>Cargando‚Ä¶</p></div>;
  if (error)   return <div className="tpl-container"><p style={{color:'red'}}>{error}</p></div>;

  return (
    <div className="routine-templates-page">
      <div className="tpl-container">
        <h1>Mis Plantillas Asignadas</h1>

        {/* B√∫squeda y filtros */}
        <div className="filter-section">
          <input
            type="text"
            placeholder="Buscar por nombre‚Ä¶"
            value={search}
            onChange={e=>setSearch(e.target.value)}
          />
          <select value={statusFilter} onChange={e=>setStatusFilter(e.target.value)}>
            <option value="all">Todas</option>
            <option value="pending">Pendientes</option>
            <option value="completed">Completadas</option>
            <option value="postponed">Pospuestas</option>
          </select>
          <select value={sortOption} onChange={e=>setSortOption(e.target.value)}>
            <option value="name">Ordenar por nombre</option>
            <option value="progress">Ordenar por progreso</option>
          </select>
        </div>

        {paginated.length === 0 ? (
          <div className="empty-state">
            ü§î No hay plantillas que mostrar
          </div>
        ) : (
          <div className="tpl-grid">
            {paginated.map(inst => {
              const total = inst.template.activities.length;
              const done  = inst.completedActivities.length;
              const pct   = total ? (done/total)*100 : 0;
              const statusClass =
                inst.status==='completed' ? 'status-completed' :
                inst.status==='postponed'  ? 'status-postponed' : '';

              return (
                <div key={inst._id} className={`tpl-card ${statusClass}`}>
                  <div className="assign-header">
                    <div>
                      <h2>{inst.template.name}</h2>
                      <p className="tpl-desc">{inst.template.description}</p>
                    </div>
                    <button className="btn-icon" onClick={()=>toggleExpand(inst._id)}>
                      {expanded[inst._id] ? <FaChevronUp/> : <FaChevronDown/>}
                    </button>
                  </div>

                  <div className="progress-container">
                    <label>{done}/{total} completadas</label>
                    <progress value={pct} max="100"/>
                  </div>

                  <div className="assign-actions">
                    {inst.status==='pending' && (
                      <>
                        <button className="btn-complete" onClick={()=>completeAll(inst)}>
                          Completar todo
                        </button>
                        <button className="btn-postpone" onClick={()=>postpone(inst._id)}>
                          Posponer
                        </button>
                      </>
                    )}
                    {inst.status==='completed' && <span>‚úîÔ∏è Completado</span>}
                    {inst.status==='postponed'  && <span>‚è≥ Pospuesto</span>}
                  </div>

                  {expanded[inst._id] && (
                    <div className="activities-list">
                      {inst.template.activities.map((act, idx) => (
                        <div key={idx}
                          className={`activity-line ${inst.completedActivities.includes(idx)?'completed':''}`}>
                          <div>
                            {inst.completedActivities.includes(idx)
                              ? <FaCheckCircle/>
                              : <FaRegCircle/>}
                            <span style={{marginLeft:'0.5rem'}}>{act.name}</span>
                          </div>
                          {!inst.completedActivities.includes(idx) && (
                            <button
                              className="btn-complete"
                              onClick={()=>markActivity(inst._id, idx)}>
                              Marcar
                            </button>
                          )}
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        )}

        {/* Paginaci√≥n */}
        {totalPages > 1 && (
          <div style={{textAlign:'center', margin:'2rem 0'}}>
            <button
              onClick={()=>setCurrentPage(p=>Math.max(p-1,1))}
              disabled={currentPage===1}
            >¬´ Anterior</button>
            <span style={{margin:'0 1rem'}}>{currentPage} / {totalPages}</span>
            <button
              onClick={()=>setCurrentPage(p=>Math.min(p+1,totalPages))}
              disabled={currentPage===totalPages}
            >Siguiente ¬ª</button>
          </div>
        )}
      </div>
    </div>
  );
}
