import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import authService from '../services/authService';
import './login.css';

// IMPORTA AQUÍ TU LOGO (imagen real o placeholder)
import janusLogo from '../assets/janus-logo.png';

function Login() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await authService.login(formData);
      localStorage.setItem('token', response.data.token);
      navigate('/dashboard');
    } catch (err) {
      setError('Credenciales inválidas');
    }
  };

  return (
    <div className="login-container">
      <div className="logo-section">
        <img src={janusLogo} alt="Janus Logo" className="janus-logo" />
      </div>

      <form onSubmit={handleSubmit} className="login-form">
        {error && <p className="error">{error}</p>}

        <div className="input-wrapper">
          <label htmlFor="email" className="sr-only">Email</label>
          <div className="input-icon-container">
            <i className="fa fa-envelope icon" aria-hidden="true"></i>
            <input
              type="email"
              name="email"
              id="email"
              placeholder="example@gmail.com"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
        </div>

        <div className="input-wrapper">
          <label htmlFor="password" className="sr-only">Password</label>
          <div className="input-icon-container">
            <i className="fa fa-key icon" aria-hidden="true"></i>
            <input
              type="password"
              name="password"
              id="password"
              placeholder="********"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
        </div>

        <div className="options-container">
          <label className="remember-me">
            <input type="checkbox" name="remember" />
            <span>Remember me</span>
          </label>
          <a href="#!" className="forgot-password">Forgot Password?</a>
        </div>

        <button type="submit" className="login-btn">Login</button>
      </form>

      <p className="register-link">
        Don’t have an account? <a href="/register">Register</a>
      </p>
    </div>
  );
}

export default Login;
